contract AllocationMarketplace is ERC1155, Ownable {
    using SafeERC20 for IERC20;

    /// @notice MMPRO token address
    address public immutable mmpro;

    /// @notice BUSD token address
    address public immutable busd;

    /// @notice swapV2 router address
    address public immutable swapRouter;

    /// @notice price of token id (which represents allocation type) in busd
    mapping(uint256 => uint256) public allocationPrice;

    /// @notice limit of allocation for each tier
    mapping(uint256 => uint128) public allocationLimit;

    /// @notice current allocated amount for each tier
    mapping(uint256 => uint128) public allocatedAmount;


    uint256 public constant TIER_1_ALLOCATION_TOKEN_ID = 0;
    uint256 public constant TIER_2_ALLOCATION_TOKEN_ID = 1;
    uint256 public constant TIER_3_ALLOCATION_TOKEN_ID = 2;
    uint256 public constant TIER_4_ALLOCATION_TOKEN_ID = 3;
    uint256 public constant TIER_5_ALLOCATION_TOKEN_ID = 4;

    /// @notice emits when token is minted(bought)
    event Mint(
        uint256 indexed tokenId,
        address indexed minter,
        uint256 mmproReceived
    );

    constructor(
        address _mmpro,
        address _busd,
        address _swapRouter
    ) ERC1155("") {
        mmpro = _mmpro;
        busd = _busd;
        swapRouter = _swapRouter;

        // approve busd just once to save gas later
        IERC20(_busd).approve(_swapRouter, type(uint256).max);

        // set allocation prices
        allocationPrice[TIER_1_ALLOCATION_TOKEN_ID] = 50 * 10**15;
        allocationPrice[TIER_2_ALLOCATION_TOKEN_ID] = 180 * 10**15;
        allocationPrice[TIER_3_ALLOCATION_TOKEN_ID] = 300 * 10**15;
        allocationPrice[TIER_4_ALLOCATION_TOKEN_ID] = 700 * 10**15;
        allocationPrice[TIER_5_ALLOCATION_TOKEN_ID] = 3000 * 10**15;

        allocationLimit[TIER_1_ALLOCATION_TOKEN_ID] = 300;
        allocationLimit[TIER_2_ALLOCATION_TOKEN_ID] = 250;
        allocationLimit[TIER_3_ALLOCATION_TOKEN_ID] = 100;
        allocationLimit[TIER_4_ALLOCATION_TOKEN_ID] = 50;
        allocationLimit[TIER_5_ALLOCATION_TOKEN_ID] = 20;

    }

    /// @notice mints tokenId to msg.sender by taking busd as a payment
    /// @param tokenId - token id to mint. Should not be minted before
    /// @param amountOutMin - minimum mmpro amount to be locked after mint
    /// @param deadline - busd-mmpro swap deadline
    function mint(
        uint256 tokenId,
        uint256 amountOutMin,
        uint256 deadline
    ) external {
        require(allocationPrice[tokenId] != 0, "Invalid allocation id");
        require(balanceOf(_msgSender(), tokenId) == 0, "Already own");
        require(allocatedAmount[tokenId] < allocationLimit[tokenId], "Allocation limit reached");

        IERC20(busd).safeTransferFrom(
            _msgSender(),
            address(this),
            allocationPrice[tokenId]
        );

        uint256 mmproReceived = _swapExactBusdToMmpro(
            allocationPrice[tokenId],
            amountOutMin,
            deadline
        );

        _mint(_msgSender(), tokenId, 1, "");
        allocatedAmount[tokenId] = allocatedAmount[tokenId] + 1;

        emit Mint(tokenId, _msgSender(), mmproReceived);
    }



    /// @notice sets new mint price
    function setAllocationPrice(uint256 allocationId, uint256 newPrice) external onlyOwner {
        allocationPrice[allocationId] = newPrice;
    }

    /// @notice sets new allocation limit
    function setAllocationLimit(uint256 allocationId, uint128 limit) external onlyOwner {
        allocationLimit[allocationId] = limit;
    }

    /// @notice withdraws tokens from marketplace
    function withdrawExtraTokens(
        address token,
        uint256 amount,
        address withdrawTo
    ) external onlyOwner {
        IERC20(token).safeTransfer(withdrawTo, amount);
    }

    /// @dev swaps exact amount of busd to mmpro
    /// @return amount of mmrpo returned from swap
    function _swapExactBusdToMmpro(
        uint256 amountIn,
        uint256 amountOutMin,
        uint256 deadline
    ) private returns (uint256) {
        address[] memory path = new address[](2);

        path[0] = busd;
        path[1] = mmpro;

        return
        IUniswapV2Router01(swapRouter).swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            path,
            address(this),
            deadline
        )[1];
    }
}
