    // The precision factor
    uint256 public PRECISION_FACTOR;

    // The payment token
    IERC20Metadata public paymentToken;

    //ownership
    address public adminAddr;

    //fees collector
    address public feesCollector;

    // Info of collector
    Collector[] public collectors; 
    uint256 public transactionFeesPercent;

    struct Collector {
        address receiverAddress;
        uint256 allocatePercent;
    }

    event Payment(address indexed user, address indexed receiver, uint256 amount, uint256 transactionFeesPercent, uint256 timestamp);
    event Collect(address indexed user, uint256 amount, uint256 allocatePercent, uint256 timestamp);
    event TransactionFee(address indexed user, uint256 amount, uint256 transactionFeesPercent, uint256 timestamp);
    event Refund(address indexed user, uint256 amount, uint256 timestamp);
    event TokenRecovery(address indexed token, uint256 amount);

    /*
     * @notice Initialize the contract
     * @param _paymentToken: payment token address
     * @param _transactionFeesPercent: transaction fees charge
     * @param _admin: admin address with ownership
     * @param _feesCollector: fees address
     */
    function initialize(
        IERC20Metadata _paymentToken,
        uint256 _transactionFeesPercent,
        address _admin,
        address _feesCollector
    ) external {
        require(!isInitialized, "Already initialized");

        // Make this contract initialized
        isInitialized = true;

        paymentToken = _paymentToken;
        transactionFeesPercent = _transactionFeesPercent;
        adminAddr = _admin;
        feesCollector = _feesCollector;

        uint256 decimalsRewardToken = uint256(paymentToken.decimals());
        require(decimalsRewardToken < 30, "Must be less than 30");

        PRECISION_FACTOR = uint256(10**(uint256(30) - decimalsRewardToken));

        // Transfer ownership to the admin address who becomes owner of the contract
        transferOwnership(_admin);
    }

    function payment(address _receiverAddress, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Payment: Insufficient fund");
        require(isInitialized, "Not initialized");

        uint256 collectorAllocatePercent = 0;
        uint256 totalAllocatePercent = 10000;
        uint256 paymentTimestamp = block.timestamp;
        for(uint256 i =0; i< collectors.length; i++){
            collectorAllocatePercent = collectorAllocatePercent.add(collectors[i].allocatePercent);
        }
        require(totalAllocatePercent >= collectorAllocatePercent, "Invalid Allocate Percentage");
        
        uint256 transactionFees = _amount.mul(transactionFeesPercent).div(10000);
        uint256 paymentAmount = _amount.sub(transactionFees);
        uint256 balanceAllocatePercent = totalAllocatePercent;
        
        if(transactionFees > 0){
            paymentToken.safeTransferFrom(address(msg.sender), feesCollector, transactionFees);
            emit TransactionFee(feesCollector, transactionFees, transactionFeesPercent, paymentTimestamp);
        }

        for(uint256 i =0; i< collectors.length; i++){
            uint256 collectAmount = paymentAmount.mul(collectors[i].allocatePercent).div(10000);

            paymentToken.safeTransferFrom(address(msg.sender), collectors[i].receiverAddress, collectAmount);
            balanceAllocatePercent = balanceAllocatePercent.sub(collectors[i].allocatePercent);
            emit Collect(collectors[i].receiverAddress, collectAmount, collectors[i].allocatePercent, paymentTimestamp);
        }

        if(balanceAllocatePercent > 0){
            uint256 collectAmount = paymentAmount.mul(balanceAllocatePercent).div(10000);
            paymentToken.safeTransferFrom(address(msg.sender), address(this), collectAmount);
            emit Collect(address(this), collectAmount, balanceAllocatePercent, paymentTimestamp);
        }

        emit Payment(msg.sender, _receiverAddress, _amount, transactionFeesPercent, paymentTimestamp);
    }

    function refund(address receiverAddress, uint256 _amount) external onlyOwner {
        require(paymentToken.balanceOf(address(this)) > 0, "Insufficient fund");

        uint256 refundTimestamp = block.timestamp;
        paymentToken.safeTransfer(receiverAddress, _amount);
        emit Refund(msg.sender, _amount, refundTimestamp);
    }

    /*
     * @notice Stop rewards
     * @dev Only callable by owner. Needs to be for emergency.
     */
    function recoverCoin(uint256 _amount) external onlyOwner {
        paymentToken.safeTransfer(address(msg.sender), _amount);
    }

    function recoverToken(address _token) external onlyOwner {
        uint256 balance = IERC20Metadata(_token).balanceOf(address(this));
        require(balance != 0, "Operations: Cannot recover zero balance");

        IERC20Metadata(_token).safeTransfer(address(msg.sender), balance);

        emit TokenRecovery(_token, balance);
    }

    function addCollector(address _address, uint256 _allocatePercent) public onlyOwner {
        uint256 collectorAllocatePercent = _allocatePercent;
        uint256 totalAllocatePercent = 10000;
        bool collectorExists = false;

        for(uint256 i =0; i< collectors.length; i++){
            collectorAllocatePercent = collectorAllocatePercent.add(collectors[i].allocatePercent);
            if(collectors[i].receiverAddress == _address){
                collectorExists = true;
            }
        }
        require(!collectorExists, "Collector Exists");
        require(totalAllocatePercent >= collectorAllocatePercent, "Invalid Allocate Percentage");

        Collector memory collector = Collector(_address, _allocatePercent);
        collectors.push(collector);
    }

    function removeCollector(address _address) public onlyOwner {
        require(collectors.length > 0);
        for(uint256 i =0; i< collectors.length; i++){
           if(collectors[i].receiverAddress == _address){
              collectors[i] = collectors[collectors.length-1];
              delete collectors[collectors.length-1];
              collectors.pop();
              return;
            }
        }
        revert("Collector not exists");
    }

    function getCollectorsCount() public view returns (uint256 count) {
        return collectors.length;
    }

    function getCollectors() public view returns (address[] memory receiverAddress , uint256[] memory allocatePercent) {
        address[] memory _receiverAddress = new address[](collectors.length);
        uint256[] memory _allocatePercent = new uint256[](collectors.length);

        for(uint256 i =0; i< collectors.length; i++){
            _receiverAddress[i] = collectors[i].receiverAddress;
            _allocatePercent[i] = collectors[i].allocatePercent;
        }

        return (_receiverAddress, _allocatePercent);
    }

    function updateAdminAddr(address _admin) external onlyOwner {
        adminAddr = _admin;
    }

    function updateFeesCollector(address _feesCollector) external onlyOwner {
        feesCollector = _feesCollector;
    }

    function updateTransactionFeesPercent(uint256 _fees) external onlyOwner {
        transactionFeesPercent = _fees;
    }
}
