    "contracts/OlympusCharactersBridge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {Characters} from "./Characters.sol";
import {Rarities} from "./OldCharacters.sol";

struct CharacterStruct {
    uint256 id;
    Rarities rarity;
}

contract OlympusCharactersBridge is OwnableUpgradeable {
    Characters public characters;

    mapping(address => CharacterStruct[]) public charactersInGame;
    mapping(uint256 => address) public charactersOwners;

    event SentCharacterInGame(address user, uint256 transferId);
    event SentCharacterFromGame(address user, uint256 transferId);

    mapping(address => bool) public managers;

    function initialize(address _characters) public initializer {
        __Ownable_init();
        characters = Characters(_characters);
    }

    function setManager(address manager, bool status) public onlyOwner {
        managers[manager] = status;
    }

    function sendInGame(uint256 id, address from) public {
        require(
            characters.ownerOf(id) == msg.sender || managers[_msgSender()],
            "Not owner"
        );

        CharacterStruct[] storage inGame = charactersInGame[from];
        (uint256 nftType, , , Rarities rarity) = characters.characters(id);
        // for (uint256 i = 0; i < inGame.length; i++) {
        //     CharacterStruct memory inGameCharacter = inGame[i];
        //     if (
        //         inGameCharacter.id == nftType &&
        //         inGameCharacter.rarity == rarity
        //     ) {
        //         revert("Character already in game");
        //     }
        // }
        characters.transferFrom(_msgSender(), address(this), id);
        // add to charactersInGame
        CharacterStruct memory character = CharacterStruct(id, rarity);
        inGame.push(character);
        charactersInGame[from] = inGame;
        charactersOwners[id] = from;
        emit SentCharacterInGame(from, id);
    }

    function sendFromGame(uint256 id) public {
        require(charactersOwners[id] == msg.sender, "Not owner");

        (uint256 nftType, , , Rarities rarity) = characters.characters(id);
        CharacterStruct[] storage inGame = charactersInGame[msg.sender];
        for (uint256 i = 0; i < inGame.length; i++) {
            CharacterStruct memory inGameCharacter = inGame[i];
            if (
                inGameCharacter.id == nftType &&
                inGameCharacter.rarity == rarity
            ) {
                delete inGame[i];
            }
        }
        charactersInGame[msg.sender] = inGame;
        characters.transferFrom(address(this), msg.sender, id);
        emit SentCharacterFromGame(msg.sender, id);
    }

    function withdrawCharacter(uint256 id, address to) external onlyOwner {
      characters.transferFrom(address(this), to, id);
    }
}
"
