    "contracts/OldCharacters.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.15;

// Solmate
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import { SafeTransferLib } from './utils/SafeTransferLib.sol';
import { Auth, Authority } from './utils/Auth.sol';

enum Rarities {
	Normal,
	Gold,
	Diamond
}

// IDS:
// 0: Medusa
// 1: Apollo
// 2: Achilles
// 3: Titan
// 4: Chimera
// 5: Zeus
struct Character {
	uint256 id;
	string nickname;
	uint8 level;
	Rarities rarity;
}

contract OldCharacters is ERC721, Auth {
	event Minted(
		address indexed owner,
		uint256 indexed character,
		uint256 indexed id,
		Rarities rarity
	);
	event SetNickname(uint256 indexed id, string name);
	event Evolve(uint256 indexed id, uint256 newLevel);

	ERC20 public stones;
	Character[] public characters;
	uint8[] public levelCosts;

	constructor(
		string memory _name,
		string memory _symbol,
		address _owner,
		Authority _authority,
		ERC20 _stones,
		uint8[] memory _levelCosts
	) ERC721(_name, _symbol) Auth(_owner, _authority) {
		stones = _stones;
		levelCosts = _levelCosts;
	}

  	function mint(
		address to,
		uint256 id,
		Rarities rarity
	) public {
		_mint(to, characters.length);
		emit Minted(to, id, characters.length, rarity);

		characters.push(
			Character({ id: id, nickname: '', level: 1, rarity: rarity })
		);
	}

	function getMaxLevel(uint256 id) public view returns (uint8) {
		Character storage character = characters[id];

		if (character.rarity == Rarities.Diamond) {
			return 6;
		}

		if (character.rarity == Rarities.Gold) {
			return 5;
		}

		return 4;
	}

	// ERC721
	function tokenURI(
		uint256 /*id*/
	) public view virtual override returns (string memory) {
		return '';
	}
}"
    },
    "contracts/Characters.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.15;

// Solmate
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {LibString} from "./utils/LibString.sol";
import "./OldCharacters.sol";

contract Characters is Initializable, ERC721Upgradeable, OwnableUpgradeable {
    event Minted(
        address indexed owner,
        uint256 indexed character,
        uint256 indexed id,
        Rarities rarity,
        uint8 level,
        string name
    );
    event Migrated(
        address indexed owner,
        uint256 indexed character,
        bool indexed firstRound
    );
    event SetNickname(uint256 indexed id, string name);

    OldCharacters public firstRoundCharacters;
    OldCharacters public secondRoundCharacters;

    mapping(address => bool) public managers;

    Character[] public characters;
    uint8[] public levelCosts;

    // ERC721 config
    string private baseUri;

    mapping(uint256 => uint8) public roundStorage;

    function setBaseUri(string memory _baseUri) public onlyOwner {
        baseUri = _baseUri;
    }

    function initialize(
        string memory _baseUri,
        address _firstRoundCharacters,
        address _secondRoundCharacters
    ) public initializer {
        __ERC721_init("Olympus Game - Characters", "OLYMPC");
        __Ownable_init();
        baseUri = _baseUri;
        firstRoundCharacters = OldCharacters(_firstRoundCharacters);
        secondRoundCharacters = OldCharacters(_secondRoundCharacters);
    }

    modifier onlyManager() {
        require(managers[_msgSender()], "NOT_MANAGER");
        _;
    }

    function setManager(address manager, bool status) public onlyOwner {
        managers[manager] = status;
    }

    function mint(address to, uint256 id, Rarities rarity, uint8 level, string memory nickname) public onlyManager {
        _mint(to, characters.length);
        emit Minted(to, id, characters.length, rarity, level, nickname);

        characters.push(
            Character({id: id, nickname: nickname, level: level, rarity: rarity})
        );
    }

    function _migrate (address to, uint256 id, Rarities rarity, uint8 level, string memory nickname ) internal {
        _mint(to, characters.length);
        emit Minted(to, id, characters.length, rarity, level, nickname);

        characters.push(
            Character({id: id, nickname: nickname, level: level, rarity: rarity })
        );
    }

    function migrateCharacter(uint256 id, bool firstRound) public {
        OldCharacters oldCharacters = firstRound
            ? firstRoundCharacters
            : secondRoundCharacters;
        require(oldCharacters.ownerOf(id) == msg.sender, "UNAUTHORIZED");
        oldCharacters.transferFrom(msg.sender, address(this), id);
        (uint256 oldId, string memory nickname, uint8 level, Rarities rarity) = oldCharacters.characters(id);
        _migrate(msg.sender, oldId, rarity, level, nickname);
        roundStorage[characters.length] = firstRound ? 1 : 2;
        emit Migrated(msg.sender, id, firstRound);
    }

    // ERC721
    function tokenURI(
        uint256 id
    ) public view virtual override returns (string memory) {
        Character storage character = characters[id];

        return
            string.concat(
                baseUri,
                LibString.toString(character.id),
                "/",
                LibString.toString(uint256(character.rarity)),
                ".json"
            );
    }

    function getTotalCharacters() public view returns (uint256) {
        return characters.length;
    }
}
"
