pragma solidity >=0.4.22 <0.6.0;

contract ERC20 {
    function totalSupply() public view returns (uint supply);
    function balanceOf(address who) public view returns (uint value);
    function allowance(address owner, address spender) public view returns (uint remaining);

    function transfer(address to, uint value) public returns (bool ok);
    function transferFrom(address from, address to, uint value) public returns (bool ok);
    function approve(address spender, uint value) public returns (bool ok);

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);
}

contract DYORDEX is ERC20 {
    uint8 public constant decimals = 18;
    uint256 initialSupply = 1000000000 * 10**uint256(decimals);
    uint256 public buyTaxRate = 5; // 5% buy tax
    uint256 public sellTaxRate = 5; // 5% sell tax
    address payable public taxWallet = 0x236287628f7D47708e0686D82F69CDCDf81c5450;

    string public constant name = "DYORDEX";
    string public constant symbol = "DYX";

    address payable teamAddress;

    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;

    function totalSupply() public view returns (uint256) {
        return initialSupply;
    }

    function balanceOf(address owner) public view returns (uint256 balance) {
        return balances[owner];
    }

    function allowance(address owner, address spender) public view returns (uint remaining) {
        return allowed[owner][spender];
    }

    function transfer(address to, uint256 value) public returns (bool success) {
        uint256 taxedValue = applyTax(value, sellTaxRate);
        return _transfer(msg.sender, to, taxedValue);
    }

    function transferFrom(address from, address to, uint256 value) public returns (bool success) {
        uint256 taxedValue = applyTax(value, sellTaxRate);
        return _transfer(from, to, taxedValue);
    }

    function applyTax(uint256 value, uint256 taxRate) internal returns (uint256) {
        uint256 taxAmount = (value * taxRate) / 100;
        balances[taxWallet] += taxAmount;
        return value - taxAmount;
    }

    function approve(address spender, uint256 value) public returns (bool success) {
        allowed[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function _transfer(address from, address to, uint256 value) internal returns (bool success) {
        if (balances[from] >= value && value > 0) {
            balances[from] -= value;
            balances[to] += value;
            emit Transfer(from, to, value);
            return true;
        } else {
            return false;
        }
    }

    constructor() public payable {
        teamAddress = msg.sender;
        balances[teamAddress] = initialSupply;
    }

    function () external payable {
        teamAddress.transfer(msg.value);
    }
}
