pragma solidity >=0.5.0 <0.8.19;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@pancakeswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Math/SafeMathUint.sol";
import "../Stone/interface/IPancakeRouter02.sol";
import "./Math/SafeMathInt.sol";

contract DividendsPaying is Ownable {
    using SafeMath for uint256;
    using Address for address;
    using SafeMathUint for uint256;
    using SafeMathInt for int256;

    uint256 public constant magnitude = 2 ** 128;
    uint256 public magnifiedDividendPerShare;
    uint256 public totalSupply;
    uint256 public totalDividendsDistributed;
    address public _rewardErc20;

    mapping(address => int256) public magnifiedDividendCorrections;
    mapping(address => uint256) public withdrawnDividends;
    mapping(address => uint256) public balance;

    address public router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;

    constructor() {
        _rewardErc20 = 0x4d809F013F2ca8e158B2D54d8Ced670eF9A21cED;
    }

    receive() external payable {
        distributeDividends();
    }

    function distributeDividends() public payable {
        uint256 _oldBalance = IERC20(_rewardErc20).balanceOf(address(this));
        if (msg.value > 0) {
            address[] memory path = new address[](2);
            path[0] = IPancakeRouter02(router).WETH();
            path[1] = _rewardErc20;
            IPancakeRouter02(router)
                .swapExactETHForTokensSupportingFeeOnTransferTokens{
                value: msg.value
            }(0, path, address(this), block.timestamp);
            uint256 _newBalance = IERC20(_rewardErc20).balanceOf(address(this));
            uint256 _balance = _newBalance.sub(_oldBalance);

            magnifiedDividendPerShare = magnifiedDividendPerShare.add(
                (_balance).mul(magnitude) / totalSupply
            );
            totalDividendsDistributed = totalDividendsDistributed.add(_balance);
        }
    }

    function recoverEth() external onlyOwner {
        uint256 initialBalance = address(this).balance;
        TransferHelper.safeTransferETH(owner(), initialBalance);
    }

    function recoverErc20() external onlyOwner {
        uint256 _balance = IERC20(_rewardErc20).balanceOf(address(this));
        TransferHelper.safeTransfer(_rewardErc20, owner(), _balance);
        totalDividendsDistributed = 0;
    }

    function withdrawnDividendOf(address _owner) public view returns (uint256) {
        return withdrawnDividends[_owner];
    }

    function withdrawableDividendOf(
        address _owner
    ) public view returns (uint256) {
        return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
    }

    function dividendOf(address _owner) public view returns (uint256) {
        return withdrawableDividendOf(_owner);
    }

    function withdrawDividend() public {
        uint256 _withdrawableDividend = withdrawableDividendOf(msg.sender);
        if (_withdrawableDividend > 0) {
            withdrawnDividends[msg.sender] = withdrawnDividends[msg.sender].add(
                _withdrawableDividend
            );
            TransferHelper.safeTransfer(
                _rewardErc20,
                msg.sender,
                _withdrawableDividend
            );
        }
    }

    function accumulativeDividendOf(
        address _owner
    ) public view returns (uint256) {
        return
            magnifiedDividendPerShare
                .mul(balance[_owner])
                .toInt256Safe()
                .add(magnifiedDividendCorrections[_owner])
                .toUint256Safe() / magnitude;
    }

    function transfer(
        address from,
        address to,
        uint256 value
    ) external onlyOwner {
        balance[from] = balance[from].sub(value * 1e18);
        balance[to] = balance[to].add(value * 1e18);

        int256 _magCorrection = magnifiedDividendPerShare
            .mul(value * 1e18)
            .toInt256Safe();
        magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from]
            .add(_magCorrection);
        magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(
            _magCorrection
        );
    }

    function mint(address account, uint256 value) external onlyOwner {
        balance[account] = balance[account].add(value * 1e18);
        totalSupply = totalSupply.add(value * 1e18);
        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].sub((magnifiedDividendPerShare.mul(value * 1e18)).toInt256Safe());
    }

    function burn(address account, uint256 value) external onlyOwner {
        balance[account] = balance[account].sub(value * 1e18);
        totalSupply = totalSupply.sub(value * 1e18);
        magnifiedDividendCorrections[account] = magnifiedDividendCorrections[
            account
        ].add((magnifiedDividendPerShare.mul(value * 1e18)).toInt256Safe());
    }
}
