      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";


// This contract handles swapping to and from sXTM, Torum's Staking Token.
contract sXTM is  AccessControl,Pausable, ERC20("sXTM", "sXTM") {
    using SafeMath for uint256;
    IERC20 public xtm;
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    // Define the XTM token
    constructor(IERC20 _xtm){
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        isEnterEnabled = false;
        isLeaveEnabled = true;
        xtm = _xtm;
    }

    //Control to enter function
    bool public isEnterEnabled;

    //Control to leave function
    bool public isLeaveEnabled;

    /// @notice Creates `_amount` token to `_to`. Must only be called by MINTER_ROLE addresses.
    function mint(address _to, uint256 _amount) public{
         // Check that the calling account has the minter role
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");
        _mint(_to, _amount);
    }

    function setAdminControls(bool _isEnterEnabled, bool _isLeaveEnabled) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not a admin");
        isEnterEnabled = _isEnterEnabled;
        isLeaveEnabled = _isLeaveEnabled;
    }

    //XTM Locked in the contract
    function enter(uint256 _amount) public {
        if(!hasRole(DEFAULT_ADMIN_ROLE, msg.sender)){
            require(!paused(), "enter: entering while paused");
            require(isEnterEnabled, "enter: entering while disabled");
        }

        // Lock the XTM in the contract
        xtm.transferFrom(msg.sender, address(this), _amount);

        //Mint the sXTM Tokens
        _mint(msg.sender, _amount);
    }

    // Leaving as our Lander :( Claim back your XTM.
    function leave(uint256 _amount) public {
        if(!hasRole(DEFAULT_ADMIN_ROLE, msg.sender)){
            require(!paused(), "leave: leaving while paused");
            require(isLeaveEnabled, "leave: leaving while disabled");
        }
        
        //Burn sXTM
        _burn(msg.sender, _amount);

        //Transfer XTM
        xtm.transfer(msg.sender, _amount);
    }

    /* @dev Pauses all actions.
     *
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function pause() public virtual {
        require(
            hasRole(PAUSER_ROLE, _msgSender()),
            "sXTM: must have pauser role to pause"
        );
        _pause();
    }

    /* @dev UnPause all actions.
     *
     *
     * Requirements:
     *
     * - the caller must have the `PAUSER_ROLE`.
     */
    function unpause() public virtual {
        require(
            hasRole(PAUSER_ROLE, _msgSender()),
            "sXTM: must have pauser role to pause"
        );
        _unpause();
    }
}
"
