// File: fil2-dev/Auth.sol


pragma solidity 0.8.18;


abstract contract Auth is Ownable {

    function getAuthState(address _maker) external view returns (bool) {
        return isAuthListed[_maker];
    }

    mapping (address => bool) public isAuthListed;

    function addAuthList (address _user) public onlyOwner {
        isAuthListed[_user] = true;
        emit AddedAuthList(_user);
    }

    function removeAuthList (address _clearedUser) public onlyOwner {
        isAuthListed[_clearedUser] = false;
        emit RemovedAuthList(_clearedUser);
    }

    event AddedAuthList(address indexed _user);

    event RemovedAuthList(address indexed _user);

}
