    function getDays() public view returns (uint256){
        uint256  blockNumber = block.number;
        if(startBlock>blockNumber){
            return 3;
        }
        uint256 dayes=(blockNumber - startBlock)/day;
        if (blockNumber % startBlock > 0) {
            dayes++;
        }
        return dayes;
    }


