        IERC20  filCoin = IERC20(filAddress);
        uint256 allamount=   filCoin.allowance(msg.sender,address(this));
        require(allamount>amount, "Insufficient authorization limit");
        
        uint256 amount30 = amount*rate30/100;

        filCoin.transferFrom(msg.sender,filLockAddress,amount30);
        filLock.deposit(msg.sender,amount30);

        filCoin.transferFrom(msg.sender,wallet5,amount*rate5/100);
        filCoin.transferFrom(msg.sender,wallet3,amount*rate3/100);
        filCoin.transferFrom(msg.sender,wallet2,amount*rate2/100);

        uint256 amount60 = amount*rate60/100;
 require(amount>500, "amount need more 500");       
        uint256 filCenterPoolBalance=getTokenBalance(filAddress,filCentralPool);
        uint256 n = getDays();
        uint256  numerator = (amount60*80).div(100)*(deflationRatio**(n-1))/(1000**(n-2)); //deflationRatio=997
        
        IERC20  fil2Coin = IERC20(fil2Address);
        uint256 fil2Circulate = fil2Coin.totalSupply()-getTokenBalance(fil2Address,dEaDAddress);   
        uint256 mintTokens=(numerator*fil2Circulate*(10**18)).div(filCenterPoolBalance*1000);   
        
        uint256 price= getFilPrice();
        uint256 inf=inflationRatio**(n-1)/(100**(n-2));
        uint256  power = amount60*price*inf/100/(10**18); //inflationRatio=102

require(amount>700, "amount need more 700");

        fil2.mint(fil2CentralPool,mintTokens/(10**18)); 
        fil2SBT.mint(msg.sender,power);    
        filCoin.transferFrom(msg.sender,filCentralPool,amount60);
        return true;
    }


