        uint256 amount60 = amount*rate60/100;
        uint256 n = getDays();
        uint256 price=getFilPrice();
        uint256 inf=inflationRatio**(n-1)/(100**(n-2));
        uint256  power = amount60*price*inf/100/(10**18); //inflationRatio=102
        return power;
    }


    function getFilPrice() public view returns(uint256 price){                              //Magnified by (10**18) times
        uint256 USDTbalance=getTokenBalance(usdtAddress,filPriceAddr);
        uint256 FILbalance=getTokenBalance(filAddress,filPriceAddr);
        price=(USDTbalance*(10**18)).div(FILbalance);   
    }

    function getRatioOfFil2toFIL() public view returns(uint256){
        IERC20  fil2Coin = IERC20(fil2Address);
        uint256 centerPool= getTokenBalance(filAddress,filCentralPool);   
        uint256 fil2Circulate= fil2Coin.totalSupply()-getTokenBalance(fil2Address,dEaDAddress);     
        uint256 fil2Price=centerPool*(10**18)/fil2Circulate;
        return  fil2Price;
    }

