contract CreamToken is ERC20, Ownable {

    using SafeMath for uint256;

    address public uniswapPair;
    IUniswapV2Router02 public uniswapV2Router = IUniswapV2Router02(0xaB3e6E0e76AC558afDE881dCc9e5f8Ada8f82802);
    address public WETH;
    address public uniswapFactory = uniswapV2Router.factory();
    address public mainCurrency;

    bool private swapping;
    address public rewardToken;

    uint256 private constant MAX = ~uint256(0);
    uint256 public swapTokensAtAmount;

    uint256 public shareFee;
    uint256 public marketingFee;
    uint256 public refluxFee;
    uint256 public deaFee;

    uint256 public buyFee;
    uint256 public sellFee;
    uint256 public taxTotleFee = 10000;

    address public marketingAddress;
    address public deadAddress = 0x000000000000000000000000000000000000dEaD;

    TokenDistributor public tokenDistributor;

    uint256 public uintDecimals = 10 ** 18;

    uint256 public distributorLimitAmount;

    DividendDistributor public distributor;
    uint256 public gasForProcessing = 300000;

    bool public swapAndLiquifyEnabled = true;

     // exlcude from fees and max transaction amount
    mapping (address => bool) public isExcludedFromFees;
    mapping (address => bool) public isDividendExempt;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    constructor(
        string memory Name, string memory Symbol, uint256 TotalSupply, address OwnerAddress,
        address MainCurrency,
        uint256 BuyRate, uint256 SellRate, address RewardCurrency, uint256 ShareLimit, address MarketingAddress
    )  ERC20(Name,  Symbol)  {
        rewardToken = RewardCurrency;

        buyFee = BuyRate;
        sellFee = SellRate;
        marketingFee =60;         // 0.5 = 3%
        shareFee = 40;
        refluxFee = 0;            // 1.5
        deaFee = 0;               // 0.75

        uint256 totalSupply = TotalSupply * uintDecimals;
        swapTokensAtAmount = totalSupply.mul(2).div(10**6); // 0.002%
        mainCurrency=MainCurrency;
        marketingAddress = MarketingAddress;
        distributorLimitAmount =  ShareLimit * uintDecimals;

        _approve(address(this), address(uniswapV2Router), MAX);

        distributor = new DividendDistributor(address(uniswapV2Router), RewardCurrency);
        tokenDistributor = new TokenDistributor(RewardCurrency);

        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(MainCurrency, address(this));
        uniswapPair = _uniswapV2Pair;
        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);
        isDividendExempt[uniswapPair] = true;
        isDividendExempt[address(this)] = true;
        isDividendExempt[deadAddress] = true;

        excludedFromFees(owner(), true);
        excludedFromFees(OwnerAddress, true);
        excludedFromFees(marketingAddress, true);

        _mint(OwnerAddress, totalSupply);
    }

    receive() external payable {}


    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "The router already has that address");
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        uniswapPair = _uniswapV2Pair;
        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);
        isDividendExempt[uniswapPair] = true;
    }

    function excludedFromFees(address account, bool excluded) internal {
        isExcludedFromFees[account] = excluded;
    }
    function excludedFromFeesList(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isExcludedFromFees[accounts[i]] = excluded;
        }
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapPair, "The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");
        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
    }

    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(newValue >= 200000 && newValue <= 500000, "GasForProcessing must be between 200,000 and 500,000");
        require(newValue != gasForProcessing, "Cannot update gasForProcessing to same value");
        gasForProcessing = newValue;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
    }
    function setSwapTokensAtAmount(uint256 amount) public onlyOwner {
        swapTokensAtAmount = amount;
    }

    function setmarketingAddress(address _marketingAddress) public onlyOwner {
        excludedFromFees(marketingAddress, true);
        marketingAddress = _marketingAddress;
    }

    function setDistributionCriteria(uint256 _minPeriod, uint256 _minDistribution) external onlyOwner {
        distributor.setDistributionCriteria(_minPeriod, _minDistribution);
    }

    function setDistributorLimitAmount(uint256 _distributorLimitAmount) external onlyOwner {
       distributorLimitAmount = _distributorLimitAmount;
    }

   function setIsDividendExempt(address holder, bool exempt) external onlyOwner {
       require(holder != address(this) && holder != uniswapPair);
       isDividendExempt[holder] = exempt;
       if(exempt){
           distributor.setShare(holder, 0);
       }else{
           distributor.setShare(holder, balanceOf(holder));
       }
   }

    function setFee(uint256 _buy, uint256 _sell ) external onlyOwner {
        buyFee = _buy;
        sellFee = _sell;
    }

    function setTaxes(uint256 _shareFee, uint256 _marketingFee, uint256 _refluxFee, uint256 _deaFee) external onlyOwner {
        shareFee = _shareFee;
        marketingFee = _marketingFee;
        refluxFee = _refluxFee;
        deaFee = _deaFee;
    }

    function claimToken(address stoken, address recipient, uint256 amount) external onlyOwner {
      require(recipient != address(0), "zero_address");
      uint256 balance = IERC20(stoken).balanceOf(address(this));
      if (balance > 0) {
          IERC20(stoken).transfer(recipient, balance > amount? amount : balance);
      }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        if( canSwap && !swapping && !automatedMarketMakerPairs[from] && from != owner() && to != owner() && swapAndLiquifyEnabled && from != address(uniswapV2Router)) {
            swapping = true;
            uint256 SwapAmount = contractTokenBalance.mul(shareFee.add(marketingFee)).div(shareFee.add(marketingFee).add(refluxFee));
            uint256 LiquidityAmount = contractTokenBalance.sub(SwapAmount);
            if(SwapAmount > 0) swapAndSendToFee(SwapAmount);
            if(LiquidityAmount > 0) swapAndLiquify(LiquidityAmount);
            swapping = false;
        }

        bool takeFee = !swapping;
        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(isExcludedFromFees[from] || isExcludedFromFees[to] || to == address(uniswapV2Router)) {
            takeFee = false;
        }

        if(takeFee) {
            uint256 Fee;
            uint256 DeadFee;
            uint256 feeRate = sellFee;

            if(automatedMarketMakerPairs[from] || automatedMarketMakerPairs[to]) { feeRate = automatedMarketMakerPairs[from] ? buyFee : sellFee; }

            Fee = amount.mul(feeRate).div(taxTotleFee);
            DeadFee = Fee.mul(deaFee).div(getTotleFee());
            if(DeadFee > 0) {
              Fee = Fee.sub(DeadFee);
              super._transfer(from, deadAddress, DeadFee);
            }
            if(Fee > 0){
              super._transfer(from, address(this), Fee);
              amount = amount.sub(Fee);
            }
        }

        super._transfer(from, to, amount);

        if(!isDividendExempt[from]){ try distributor.setShare(from, balanceOf(from) >= distributorLimitAmount ? balanceOf(from) : 0) {} catch {} }
        if(!isDividendExempt[to]){ try distributor.setShare(to, balanceOf(to) >= distributorLimitAmount ? balanceOf(to) : 0) {} catch {} }

        if(!swapping) {
            try distributor.process(gasForProcessing) {} catch {}
        }
    }


    function getTotleFee() private view returns(uint256){
      return shareFee.add(marketingFee).add(refluxFee).add(deaFee);
    }

    function getBalanceOf(address _token, address account) private view returns(uint256) {
      if(_token == WETH) account.balance;
      return IERC20(_token).balanceOf(account);
    }

    function _safeTransfer(address _token, address account, uint256 amount) internal {
      if(_token == WETH) {
        TransferHelper.safeTransferETH(account, amount);
      }else{
        TransferHelper.safeTransfer(_token, account, amount);
      }
    }

    function swapAndLiquify(uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = getBalanceOf(mainCurrency, address(this) );
        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered
        // how much ETH did we just swap into?
        uint256 newBalance = getBalanceOf(mainCurrency, address(this) ).sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }




    function swapAndSendToFee(uint256 tokenAmount) private  {
        swapTokensForCake(tokenAmount);

        uint256 rewardBalance =  getBalanceOf(rewardToken, address(this));
        if(rewardBalance == 0) return;

        uint256 marketingAmount = rewardBalance.mul(marketingFee).div(shareFee.add(marketingFee));
        if(marketingAddress != address(0)){
          _safeTransfer(rewardToken, marketingAddress , marketingAmount);
        }

        uint256 shareAmount = rewardBalance.sub(marketingAmount);
        if(address(distributor) != address(0)){
          _safeTransfer(rewardToken, address(distributor), shareAmount);
          distributor.deposit(shareAmount);
        }
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios

        // _approve(address(this), address(uniswapV2Router), tokenAmount);
        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            deadAddress,
            block.timestamp
        );

    }

    function swapTokensForEth(uint256 tokenAmount) private {

        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        address factory = uniswapV2Router.factory();
        address[] memory factorysPath = new address[](1);
        factorysPath[0] = factory;
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(tokenAmount, 0, factorysPath , path, address(this), block.timestamp);

    }

    function swapTokensForCake(uint256 tokenAmount) public {

        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = rewardToken;

        address factory = uniswapV2Router.factory();
        address[] memory factorysPath = new address[](2);
        factorysPath[0] = factory;
        factorysPath[1] = factory;

        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens( tokenAmount, 0, factorysPath, path , address(tokenDistributor), block.timestamp  );
        uint256 initialUSDTBalance = IERC20(rewardToken).balanceOf(address(tokenDistributor));
        IERC20(rewardToken).transferFrom(
            address(tokenDistributor),
            address(this),
            initialUSDTBalance
        );
    }

}

