contract BitAiToken is Context, IBEP20, Ownable {
  using SafeMath for uint256;

  uint256 private _totalSupply;
  uint256 private _airdropSupply;
  uint8 private _decimals;
  string private _symbol;
  string private _name;
  bool public safeguard;  //putting safeguard on will halt all non-owner functions
  uint256 public monthlyRelease = 10; //% to release monthly from airdrop
  uint256 public airdropLockDays = 60 days ;
  uint256 public minDayToClaim = 30 days ; 

  mapping(address => uint256) private _balances;
  mapping(address => mapping(address => uint256)) private _allowances;
  mapping (address => bool) public frozenAccount;
 
  struct userInfo{
    uint256 lockEnd;
    uint256 lastClaimed;
    uint256 _lockedbalances; 
    uint256 totalbal; //total airdrop received quantity   
  }
  mapping(address => userInfo) public userDetails;  

  event AirDrop(address indexed user, uint256 _amount);
  event ClaimAirDrop(address indexed user, uint256 _amount);
  event FrozenAccounts(address target, bool frozen);    

  constructor() {
    _name = 'BITAI TOKEN';
    _symbol = 'BITAI';
    _decimals = 9;
    _totalSupply = 164 * 10**7 * 10**9; // 100m
    _airdropSupply = _totalSupply.mul(10).div(100); //10% of total supply to airdrop
	  
    _balances[msg.sender] = _totalSupply.div(100); //1% issued to list Token on dex

    emit Transfer(address(0), msg.sender, _totalSupply.div(100));
  }

  /**
   * @dev Returns the token decimals.
   */
  function decimals() external view virtual override returns (uint8) {
    return _decimals;
  }

  /**
   * @dev Returns the token symbol.
   */
  function symbol() external view virtual override returns (string memory) {

    return _symbol;
  }

  /**
   * @dev Returns the token symbol.
   */
  function getOwner() external view virtual override returns (address owner) {
    return owner;
  }
  /**
   * @dev Returns the token name.
   */
  function name() external view virtual override returns (string memory) {
    return _name;
  }

  /**
   * @dev See {BEP20-totalSupply}.
   */
  function totalSupply() external view virtual override returns (uint256) {
    return _totalSupply;
  }


function airdropSupply() external view virtual returns (uint256) {
    return _airdropSupply;
  }  

  /**
   * @dev See {BEP20-balanceOf}.
   */
  function balanceOf(address account)
    external
    view
    virtual
    override
    returns (uint256)
  {
    return _balances[account] + userDetails[account]._lockedbalances;
  }

  /**
   * @dev See {BEP20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount)
    external
    override
    returns (bool)
  {
    _transfer(_msgSender(), recipient, amount);
    return true;
  }

  /**
   * @dev See {BEP20-allowance}.
   */
  function allowance(address owner, address spender)
    external
    view
    override
    returns (uint256)
  {
    return _allowances[owner][spender];
  }

  /**
   * @dev See {BEP20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 amount)
    external
    override
    returns (bool)
  {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev See {BEP20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {BEP20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(
    address sender,
    address recipient,
    uint256 amount
  ) external override returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(
      sender,
      _msgSender(),
      _allowances[sender][_msgSender()].sub(
        amount,
        'BEP20: transfer amount exceeds allowance'
      )
    );
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue)
    public
    returns (bool)
  {
    _approve(
      _msgSender(),
      spender,
      _allowances[_msgSender()][spender].add(addedValue)
    );
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue)
    public
    returns (bool)
  {
    _approve(
      _msgSender(),
      spender,
      _allowances[_msgSender()][spender].sub(
        subtractedValue,
        'BEP20: decreased allowance below zero'
      )
    );
    return true;
  }

  /**
   * @dev Destroys `amount` tokens from the caller.
   *
   * See {BEP20-_burn}.
   */
  function burn(uint256 amount) public virtual {
    _burn(_msgSender(), amount);
  }

  /**
   * @dev Destroys `amount` tokens from `account`, deducting from the caller's
   * allowance.
   *
   * See {BEP20-_burn} and {BEP20-allowance}.
   *
   * Requirements:
   *
   * - the caller must have allowance for ``accounts``'s tokens of at least
   * `amount`.
   */
  function burnFrom(address account, uint256 amount) public virtual {
    uint256 decreasedAllowance =
      _allowances[account][_msgSender()].sub(
        amount,
        'BEP20: burn amount exceeds allowance'
      );

    _approve(account, _msgSender(), decreasedAllowance);
    _burn(account, amount);
  }

  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
  function _transfer(
    address sender,
    address recipient,
    uint256 amount
  ) internal {
    require(!safeguard);
    require(!frozenAccount[sender], "caller has been frozen");
    require(!frozenAccount[recipient], "recipient has been frozen");
    require(sender != address(0), 'BEP20: transfer from the zero address');
    require(recipient != address(0), 'BEP20: transfer to the zero address');

    _balances[sender] = _balances[sender].sub(
      amount,
      'BEP20: transfer amount exceeds balance'
    );
    _balances[recipient] = _balances[recipient].add(amount);
    emit Transfer(sender, recipient, amount);
  }

  /**
   * @dev Destroys `amount` tokens from `account`, reducing the
   * total supply.
   *
   * Emits a {Transfer} event with `to` set to the zero address.
   *
   * Requirements
   *
   * - `account` cannot be the zero address.
   * - `account` must have at least `amount` tokens.
   */
  function _burn(address account, uint256 amount) internal {
    require(account != address(0), 'BEP20: burn from the zero address');

    _balances[account] = _balances[account].sub(
      amount,
      'BEP20: burn amount exceeds balance'
    );
    _totalSupply = _totalSupply.sub(amount);
    emit Transfer(account, address(0), amount);
  }

  /**
   * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
   *
   * This is internal function is equivalent to `approve`, and can be used to
   * e.g. set automatic allowances for certain subsystems, etc.
   *
   * Emits an {Approval} event.
   *
   * Requirements:
   *
   * - `owner` cannot be the zero address.
   * - `spender` cannot be the zero address.
   */
  function _approve(
    address owner,
    address spender,
    uint256 amount
  ) internal {
    require(owner != address(0), 'BEP20: approve from the zero address');
    require(spender != address(0), 'BEP20: approve to the zero address');
    require(!safeguard);
    require(!frozenAccount[owner], "owner has been frozen");
    require(!frozenAccount[spender], "spender has been frozen");

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }
  
  function isContract(address _address) internal view returns (bool){
        uint32 size;
        assembly {
            size := extcodesize(_address)
        }
        return (size > 0);
    }
	
  
  function claimLockedBal()  external 
  {
      require(!safeguard);
      require(!frozenAccount[msg.sender], "caller has been frozen");
      require(!isContract(msg.sender),  'No contract address allowed'); 
	  address user = msg.sender;      
	  require(userDetails[user]._lockedbalances > 0, "No airdrop amount to release");
    require(userDetails[user].lockEnd <= block.timestamp, "Your lock does not end");
      uint256 lastClaimDuration = block.timestamp - userDetails[user].lastClaimed;
      require(lastClaimDuration >= minDayToClaim  , "Monthly claim has been done");
      lastClaimDuration = lastClaimDuration / minDayToClaim;
    
	  uint256 amount = lastClaimDuration * (userDetails[user]._lockedbalances * monthlyRelease / 100);
      require(amount > 0 && userDetails[user]._lockedbalances > 0, "No amount to claim");
      if(userDetails[user]._lockedbalances > amount)
      {
        userDetails[user]._lockedbalances -= amount;
      }
      else
      {
        amount = userDetails[user]._lockedbalances;
        userDetails[user]._lockedbalances = 0;
      }
      userDetails[user].lastClaimed = block.timestamp;

      _balances[user] = _balances[user].add(amount); //mint and issue token

      emit ClaimAirDrop(user, amount);
  }

  function setAirdrop(uint256 lockdays, uint256 _airdropsupply) external onlyOwner
  {
      airdropLockDays = lockdays;
	  _airdropSupply= _airdropsupply;
  }

  function sendToDexContract(address payable toAddress,uint flatOrperc, uint value) public  onlyOwner returns(bool)
  {
      if(flatOrperc == 0) //send flat
          toAddress.transfer(value);
      else if(flatOrperc == 1) //send flat
          toAddress.transfer(address(this).balance * value / 100);
      else 
          return false;
      
      return true;
  }

  function freezeAccount(address target, bool freeze) external onlyOwner
	{
        frozenAccount[target] = freeze;
        emit  FrozenAccounts(target, freeze);
  }

  function changeSafeguardStatus() external onlyOwner 
  {
        if (safeguard == false){
            safeguard = true;
        }
        else{
            safeguard = false;
        }
  }

  function dropAirdrop(address _user, uint256 _airdropAmount, uint256 _lockdays) external onlyOwner
  {
  	  require(!frozenAccount[_user], "User has been frozen");
      require(!isContract(_user),  'No contract address allowed'); 
	    require(_airdropSupply >= _airdropAmount, "Airdrop supply exhausted!");	  
	  
  	  userDetails[_user].lockEnd = block.timestamp + _lockdays;
      userDetails[_user].lastClaimed = block.timestamp;
      userDetails[_user]._lockedbalances += _airdropAmount;
      userDetails[_user].totalbal += _airdropAmount;
	  _airdropSupply = _airdropSupply.sub(_airdropAmount);
	  emit AirDrop(_user, _airdropAmount);
  }
  function setMonthlyRelease(uint256 _monthlyRelease) external onlyOwner
  {
      require(_monthlyRelease > 0,"Value must be greater than 0");
      monthlyRelease = _monthlyRelease;
  }
  
  function setName(string calldata tname) external onlyOwner  
  {
      _name = tname;
  }
  
  function setSymbol(string calldata tsymbol) external onlyOwner 
  {
      _symbol = tsymbol;
  }
  
}
