// File: contracts/PaymentManager.sol


pragma solidity 0.8.7;





contract PaymentManager is AccessControl, Pausable {

    using SafeMath for uint256;

    bytes32 public constant PAYER_ROLE = keccak256("PAYER_ROLE");
    bytes32 public constant CHARGER_ROLE = keccak256("CHARGER_ROLE");

    mapping(uint256 => bool) public usedNonces;

    uint256 public _chargeFee = 40 ether;
    address public _feeReceiver;
    uint256 public _pendingFees;

    address public _foa;

    constructor(address foa) {
        _foa = foa;
        _feeReceiver = msg.sender;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function pause() external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Only for role DEFAULT_ADMIN_ROLE');
        _pause();
    }

    function unpause() external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Only for role DEFAULT_ADMIN_ROLE');
        _unpause();
    }

    function charge(uint256 amount, address payer) external whenNotPaused {
        require(hasRole(CHARGER_ROLE, msg.sender), "Sender is not a charger");
        require(amount > _chargeFee, "Amount must be higher than the fee");
        require(IERC20(_foa).transferFrom(payer, address(this), amount), "Transfer from failed");
        _pendingFees = _pendingFees.add(_chargeFee);
    }

    function claimFees() external {
        uint256 amount = _pendingFees;
        _pendingFees = 0;
        require(IERC20(_foa).transfer(_feeReceiver, amount), "Transfer failed");
    }

    function setFeeAmount(uint256 amount) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Only for role DEFAULT_ADMIN_ROLE');
        _chargeFee = amount;
    }

    function setFeeReceiver(address wallet) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Only for role DEFAULT_ADMIN_ROLE');
        _feeReceiver = wallet;
    }

    function emergencyWithdraw() external {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), 'Only for role DEFAULT_ADMIN_ROLE');
        require(IERC20(_foa).transfer(msg.sender, IERC20(_foa).balanceOf(address(this))), "Transfer failed");
    }

    function claimPayment(uint256 amount, uint256 nonce, bytes calldata sig) external whenNotPaused {
        require(!usedNonces[nonce], "Nonce already used");
        require(IERC20(_foa).balanceOf(address(this)) >= amount, "Not enough liquidity");

        usedNonces[nonce] = true;

        // This recreates the message that was signed on the client.
        bytes32 message = prefixed(keccak256(abi.encodePacked(msg.sender, amount, nonce, this)));

        require(hasRole(PAYER_ROLE, recoverSigner(message, sig)), "Signature is not from a payer");

        require(IERC20(_foa).transfer(msg.sender, amount), "Transfer failed");
    }


    // Signature methods
    function splitSignature(bytes memory sig)
        internal
        pure
        returns (uint8, bytes32, bytes32)
    {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        internal
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    // Builds a prefixed hash to mimic the behavior of eth_sign.
    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", hash));
    }
}
