pragma solidity ^0.8.0;

import "./BEP20.sol";

/**
 * @dev HBSC Token
 */
contract HBSC is BEP20 {

	address private _adminWallet;
	address private _reservationContract;
    address private _stakingContract;
	address owner;
	string public constant NAME = "HBSC";
	string public constant SYMBOL = "HBSC";

	modifier reservationSet {
		require(_reservationContract != address(0), "Reservation contract must be set");
		_;
	}

	modifier onlyHBSC {
		require(msg.sender == _reservationContract || msg.sender == _stakingContract, 
            "Only HBSC reservation or staking contract can mint tokens"
        );
		_;
	}

    modifier stakingSet {
        require(_stakingContract != address(0), "Staking contract must be set");
        _;
    }

    modifier onlyStaking {
        require(msg.sender == _stakingContract, "Only staking contract can burn tokens");
        _;
    }

	modifier onlyAdmin {
		require(msg.sender == _adminWallet || msg.sender == owner, "Admin only function");
		_;
	}

	constructor(address adminWallet) 
	 	BEP20(NAME, SYMBOL) 
	{
		owner = msg.sender;
	 	_adminWallet =  adminWallet;
        _mint(_adminWallet, 2000 * 10 ** 18); //20% of supply to admin multisig wallet
    }

    /**
     * @dev Sets the HBSC reservation contract address
    */
    function setReservationContract(address newReservationContract) 
    	external
    	onlyAdmin
    {
        require(_reservationContract == address(0), "Reservation contract already set");
    	require(newReservationContract != address(0), "Invalid reservation contract");
    	_reservationContract = newReservationContract;
    }


    /**
     * @dev Sets the HBSC staking contract address
    */
    function setStakingContract(address newStakingContract) 
        external
        onlyAdmin
    {
        require(_stakingContract == address(0), "Staking contract already set");
        require(newStakingContract != address(0), "Invalid staking contract");
        _stakingContract = newStakingContract;
    }

    /**
     * @dev Allows reservation contract to mint tokens
    */
    function mintTo(address to, uint256 amount) 
    	external
    	reservationSet
        stakingSet
    	onlyHBSC
    	returns(bool)
    {
    	_mint(to, amount);
    	return true;
    }

    /**
     * @dev Allows staking contract to burn tokens
    */
    function burnTokens(uint256 amount)
        external
        stakingSet
        onlyStaking
        returns(bool)
    {
        _burn(_stakingContract, amount);
        return true;
    }
}
