pragma solidity ^0.8.3;

contract BinanceBridgePool is AccessControl, ReentrancyGuard {
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

  using SafeMath for uint256;

  IBEP20 public raini;

  event Deposited(address indexed spender, address recipient, uint256 amount, uint256 requestId);
  event Withdrawn(address indexed owner, uint256 amount, uint256 requestId);
  event BnbWithdrawn(uint256 amount);
  event FeeSet(uint256 fee, uint256 percentFee, uint256 percentFeeDecimals);
  event AutoWithdrawFeeSet(bool autoWithdrawFee);
  event RainiToBnbSet(uint256 rainiToBnb);
  event TreasuryAddressSet(address treasuryAddress);

  uint256 public requestId;
  uint256 public rainiToBnb;
  uint256 public fee;
  uint256 public percentFee;
  uint    public percentFeeDecimals;
  bool    public autoWithdrawFee;
  address public treasuryAddress;

  constructor(address _raini) {
    require(_raini != address(0), "BinanceBridgePool: _raini is zero address");
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    treasuryAddress = _msgSender();
    raini = IBEP20(_raini);
  }

  modifier onlyMinter() {
    require(hasRole(MINTER_ROLE, _msgSender()), "BinanceBridgePool: caller is not a minter");
    _;
  }

  modifier onlyOwner() {
    require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "BinanceBridgePool: caller is not an owner");
    _;
  }

  function getFee(uint _amount) public view returns (uint256) {
    return fee + _amount.div(rainiToBnb).mul(percentFee).div(10 ** percentFeeDecimals);
  }

  function setFee(uint256 _fee, uint256 _percentFee, uint256 _percentFeeDecimals)
    external onlyOwner {
      fee = _fee;
      percentFee = _percentFee;
      percentFeeDecimals = _percentFeeDecimals;
      emit FeeSet(_fee, _percentFee, _percentFeeDecimals);
  }

  function setRainiToBnb(uint256 _rainiToBnb)
    external onlyOwner {
      rainiToBnb = _rainiToBnb;
      emit RainiToBnbSet(_rainiToBnb);
  }

  function setAutoWithdrawFee(bool _autoWithdrawFee)
    external onlyOwner {
      autoWithdrawFee = _autoWithdrawFee;
      emit AutoWithdrawFeeSet(autoWithdrawFee);
  }

  function setTreasuryAddress(address _treasuryAddress)
    external onlyOwner {
      treasuryAddress = _treasuryAddress;
      emit TreasuryAddressSet(_treasuryAddress);
  }

  function deposit(address _recipient, uint256 _amount) 
    external payable nonReentrant {
      uint256 depositFee = getFee(_amount);
      require(msg.value >= depositFee, "BinanceBridgePool: not enough bnb");

      raini.transferFrom(_msgSender(), address(this), _amount);
      
      uint256 refund = msg.value - depositFee;
      if(refund > 0) {
        (bool refundSuccess, ) = _msgSender().call{ value: refund }("");
        require(refundSuccess, "BinanceBridgePool: refund transfer failed");
      }

      if (autoWithdrawFee) {
        (bool withdrawSuccess, ) = treasuryAddress.call{ value: depositFee }("");
        require(withdrawSuccess, "BinanceBridgePool: withdraw transfer failed");
      }

      requestId++;
      emit Deposited(_msgSender(), _recipient, _amount, requestId);
  }

  function withdraw(address[] memory _owners, uint256[] memory _amounts, uint256[] memory _requestsIds) 
    external onlyMinter {
      require(_owners.length == _amounts.length && _owners.length == _requestsIds.length, "BinanceBridgePool: Arrays length not equal");
      
      for (uint256 i; i < _owners.length; i++) {
        raini.transfer(_owners[i], _amounts[i]);
        emit Withdrawn(_owners[i], _amounts[i], _requestsIds[i]);
      }
  }

  function withdrawBnb(uint256 _amount)
    external onlyOwner {
      require(_amount <= address(this).balance, "BinanceBridgePool: not enough balance");
      (bool success, ) = _msgSender().call{ value: _amount }("");
      require(success, "BinanceBridgePool: transfer failed");
      emit BnbWithdrawn(_amount);
  }
}
