contract OperatorRestricted  is Ownable{    


    mapping(address => bool) private _operators;
    
    modifier onlyOperators() {
        require(
            owner() == _msgSender() || _operators[_msgSender()] == true,
            "CALLER_NOT_OPERATOR"
