    "contracts/ZCoreAI.sol": {
      "content": "// SPDX-License-Identifier: MIT
/*

    $$$$$$$$\  $$$$$$\   $$$$$$\  $$$$$$$\  $$$$$$$$\ 
    \____$$  |$$  __$$\ $$  __$$\ $$  __$$\ $$  _____|
        $$  / $$ /  \__|$$ /  $$ |$$ |  $$ |$$ |      
       $$  /  $$ |      $$ |  $$ |$$$$$$$  |$$$$$\    
      $$  /   $$ |      $$ |  $$ |$$  __$$< $$  __|   
     $$  /    $$ |  $$\ $$ |  $$ |$$ |  $$ |$$ |      
    $$$$$$$$\ \$$$$$$  | $$$$$$  |$$ |  $$ |$$$$$$$$\ 
    \________| \______/  \______/ \__|  \__|\________|

    https://ai.zcore.network

*/
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFV2WrapperConsumerBase.sol";

interface IPancakeRouter01 {

    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface IPancakeRouter02 is IPancakeRouter01 {
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}


interface IZCore {
    function burn(uint256 amount) external;
}

interface IPegSwap {
    function swap(uint256 amount, address source, address target) external;
}

contract ZCoreAI is ERC721URIStorage, ReentrancyGuard, Ownable, VRFV2WrapperConsumerBase {

    using Strings for uint256;
    using SafeERC20 for IERC20;

    address constant WBNB = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address constant UNIROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address constant USDT = 0x55d398326f99059fF775485246999027B3197955;
    address constant ZCORE = 0x0e8fE6eeD5342Ea9189D9268D088821f0532fb74;
    address constant LINKBSC = 0xF8A0BF9cF54Bb92F17374d9e9A321E6a111a51bD;
    address constant PEGSWAP = 0x1FCc3B22955e76Ca48bF025f1A6993685975Bb9e;

    address public dev;
    uint256 public price;
    uint256 public championship = 0;
    uint256 public lastGas;

    string public uriPrefix;
    string constant uriSuffix = ".json";

    address[] zcrToUsdtRoute;
    address[] bnbToUsdtRoute;
    address[] bnbToZcrRoute;
    address[] zcrToBnbRoute;
    address[] bnbToLinkRoute;


    struct Sorteio {
        address carteira;
        uint256 nft;
        uint256 usuario;
        uint256 sorteado;
    }


    struct TokenData {
        address[] routeBnb;
        address[] routeUsdt;
        address addPartner;
        uint16 nftprice;
    }    

    struct Ticket {
        address tokenAddress; // Address of the token
        uint quantity;        // Quantity of tickets of this type
    }

    // Mapping from user address to their tickets
    mapping(address => Ticket[]) private userTickets;        

    //mapping(uint => uint) private nftIdToIndex;
    //mapping(string => uint) private orderToIndex;
    mapping(address => uint[]) private tokenToNFTIDs; // Corrigido: agora mapeia um token para uma lista de NFTIDs

    uint256 _burnCounter;
    uint256 tokenCounter;
    mapping(address => TokenData) public tokensData;
    mapping(address => uint256) public winners;
    mapping(string => uint256) public orders;
    mapping(uint256 => string) public ordersbynft;
    mapping(string => address) public ordersTokens;
    mapping(uint256 => Sorteio) public sorteios; /* requestId --> requestStatus */

    // Eventos
    event TicketsPurchased(address indexed user, address indexed token, uint256 ticketCount);    
    event AddReferral(address indexed _buyer, address indexed _referrer, uint256 _value, uint _typeOfIndication);
    event NewWinner(uint256 indexed timestamp, address indexed user, uint256 value);
    event BuyTicket(address indexed user, uint256 amount, uint256 usdt, address referral, address token);
    event NFTMinted(address indexed minter, uint256 nftID, string uri, uint256 requestID);
    event WinnerPrize(uint256 timestamp, address indexed user, uint256 value);

    event RequestSent(uint256 requestId, uint256 nftID, uint32 numWords);
    event RequestFulfilled(
        uint256 requestId,
        uint256 randomNumber,
        uint256 nftId,
        uint256 payment
    );

    struct RequestStatus {
        uint256 paid;
        bool fulfilled;
        uint256 randomNumber;
        uint256 nftId;
    }
    mapping(uint256 => RequestStatus) public s_requests; /* requestId --> requestStatus */

    // past requests Id.
    uint256[] public requestIds;
    uint256 public lastRequestId;

    uint16 constant requestConfirmations = 3;
    uint32 constant numWords = 1;
    uint32 public callbackGasLimit;
    uint32 public maxNumber = 38;
    uint32 public slippageToken = 20;
    uint32 public porcChamp = 70;

    bool public autolink = true;

    address linkAddress;
    address wrapperAddress;

    constructor(
         address _dev,
         uint256 _price,
         string memory _uriPrefix,
         uint32 _callbackGasLimit,
         address _linkAddress,
         address _wrapperAddress,
         uint256 _lastGas
    )
        ERC721("ZCoreAI", "ZCRAI") 
        VRFV2WrapperConsumerBase(_linkAddress, _wrapperAddress)
        Ownable(msg.sender)
    {
        lastGas = _lastGas;
        callbackGasLimit = _callbackGasLimit;
        linkAddress = _linkAddress;
        wrapperAddress = _wrapperAddress;
        dev = _dev;
        price = _price;
        zcrToBnbRoute = [ZCORE, WBNB];
        zcrToUsdtRoute = [ZCORE, WBNB, USDT];
        bnbToUsdtRoute = [WBNB, USDT];
        bnbToZcrRoute = [WBNB, ZCORE];
        bnbToLinkRoute = [WBNB, LINKBSC];
        tokenCounter = 0;
        uriPrefix = _uriPrefix;
        _giveAllowances();
    }


    function getprice(uint256 _tickets) external view returns (uint256){
        uint256 _totprice = _tickets * price;
        uint256[] memory _tokenout = IPancakeRouter02(UNIROUTER).getAmountsIn(
            _totprice,
            zcrToUsdtRoute
        );
        return _tokenout[0];
    }


    function getpricetoken(uint256 _tickets, address _token) external view returns (uint256){
        require(tokensData[_token].routeUsdt.length > 0, "Invalid Token!");
        uint256 _totprice = _tickets * price;
        uint256[] memory _tokenout = IPancakeRouter02(UNIROUTER).getAmountsIn(
            _totprice,
            tokensData[_token].routeUsdt
        );
        return _tokenout[0];
    }


    function deposit(uint256 _amount, address _referral) external nonReentrant {   
        require(_amount > 0, "Invalid amount!");

        if(_referral == address(0)){
            _referral = dev;
        }

        // Optimize arithmetic operations
        uint256 _totprice = _amount * price; // Using multiplication instead of .mul()
        uint256[] memory _value = IPancakeRouter02(UNIROUTER).getAmountsIn(_totprice, zcrToUsdtRoute);
        IERC20(ZCORE).safeTransferFrom(msg.sender, address(this), _value[0]);

        uint256 _tousdt = _swapAndDistribute(_value[0], _referral);
        updateTicketQuantity(msg.sender, ZCORE, _amount, true);
        emit TicketsPurchased(msg.sender, ZCORE, _amount);
        emit BuyTicket(msg.sender, _amount, _tousdt, _referral, ZCORE);
    }


    function _swapAndDistribute(uint256 _value, address _referral) internal returns (uint256){
        // Directly calculate half of _value
        uint256 _toUsdt = _value / 2; // 50%

        // Perform the swap and get the USDT amount
        uint256[] memory _amounts = IPancakeRouter02(UNIROUTER).swapExactTokensForTokens(
            _toUsdt,
            0,
            zcrToUsdtRoute, 
            address(this), 
            block.timestamp + 360
        );

        uint256 currentValue = _amounts[2]; 

        // Calculate referral and developer amounts in a gas-efficient way
        uint256 referralAmount = currentValue * 1000 / 10000;
        uint256 developerAmount = currentValue * 3500 / 10000;

        // Save
        if(_referral != dev){
            emit AddReferral(msg.sender, _referral, referralAmount, 1);
        }

        // Directly update championship amount
        championship += currentValue - referralAmount - developerAmount;

        // Transfer USDT to referral and developer
        IERC20(USDT).safeTransfer(_referral, referralAmount);
        IERC20(USDT).safeTransfer(dev, developerAmount);

        // Burn the remaining ZCORE
        uint256 _burn = IERC20(ZCORE).balanceOf(address(this));
        IZCore(ZCORE).burn(_burn);

        return currentValue;
    }

    function getAllNFTsByToken(address _token) public view returns (uint[] memory) {
        return tokenToNFTIDs[_token];
    }

    function depositToken(uint256 _amount, address _referral, address _token) external nonReentrant {           
        require(_amount > 0, "Invalid amount!");
        require(tokensData[_token].routeBnb.length > 0 && tokensData[_token].routeUsdt.length > 0, "Invalid Token!");

        _referral = _referral == address(0) ? tokensData[_token].addPartner : _referral;

        uint256 _valuetoken = IPancakeRouter02(UNIROUTER).getAmountsIn(
            _amount * price, 
            tokensData[_token].routeUsdt
        )[0];

        uint256 _valuebnb = IPancakeRouter02(UNIROUTER).getAmountsOut(
            _valuetoken, 
            tokensData[_token].routeBnb
        )[(tokensData[_token].routeBnb.length - 1)];

        IERC20(_token).safeTransferFrom(msg.sender, address(this), _valuetoken);

        uint256 _tousdt = _swapAndDistributeToken(_valuebnb, _referral, _token);
        updateTicketQuantity(msg.sender, _token, _amount, true);
        emit TicketsPurchased(msg.sender, _token, _amount);
        emit BuyTicket(msg.sender, _amount, _tousdt, _referral, _token);
    }


    function _swapAndDistributeToken(uint256 _valuebnb, address _referral, address _token) internal returns (uint256) {
        uint256 tokenBalance = IERC20(_token).balanceOf(address(this));
        uint256 valueMinusSlippage = _valuebnb - _valuebnb * slippageToken / 100;

        IPancakeRouter02(UNIROUTER).swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenBalance,
            valueMinusSlippage,
            tokensData[_token].routeBnb,
            address(this), 
            block.timestamp + 360
        );

        uint256 _balwbnb = IERC20(WBNB).balanceOf(address(this));
        uint256 _toZcr = _balwbnb / 2;

        IPancakeRouter02(UNIROUTER).swapExactTokensForTokens(
            _toZcr,
            0,
            bnbToZcrRoute, 
            address(this), 
            block.timestamp + 360
        );

        uint256 _burn = IERC20(ZCORE).balanceOf(address(this));
        IZCore(ZCORE).burn(_burn);

        uint256[] memory _amounts = IPancakeRouter02(UNIROUTER).swapExactTokensForTokens(
            IERC20(WBNB).balanceOf(address(this)),
            0,
            bnbToUsdtRoute, 
            address(this), 
            block.timestamp + 360
        );

        uint256 currentValue = _amounts[1];
        uint256 partnerAmount = currentValue * 1000 / 10000;
        uint256 referralAmount = currentValue * 1000 / 10000;
        uint256 developerAmount = currentValue * 2500 / 10000;

        // Save        
        emit AddReferral(msg.sender, _referral, referralAmount, 1);
        emit AddReferral(msg.sender, tokensData[_token].addPartner, partnerAmount, 2);

        championship += currentValue - referralAmount - developerAmount - partnerAmount;

        IERC20(USDT).safeTransfer(tokensData[_token].addPartner, partnerAmount);
        IERC20(USDT).safeTransfer(_referral, referralAmount);
        IERC20(USDT).safeTransfer(dev, developerAmount);

        return currentValue;
    }


    function mint(
        string memory _order,
         uint256 userNumber, 
         address _token
    ) external payable nonReentrant returns (uint256) {
        uint16 _ticketprice = 1;
        if(_token != ZCORE){
            _ticketprice = tokensData[_token].nftprice;
        }

        require(userNumber >= 1 && userNumber <= maxNumber, "Number must be between 1 and MAX_NUMBER");
        require(getTicketQuantity(msg.sender, _token) >= _ticketprice, "No tickets for this token!");
        require(orders[_order] == 0, "This order already exists!");
        //require(orderToIndex[_order] == 0, "This order already exists!");
        require(msg.value >= linkgas(), "Invalid BNB sent!");

        LinkTokenInterface link = LinkTokenInterface(linkAddress);

        if(link.balanceOf(address(this)) < lastGas && autolink){
            bnbtolink();
        }

        uint256 _tokenId = ++tokenCounter; // Increment and assign in the same step

        _safeMint(msg.sender, _tokenId);
        _setTokenURI(_tokenId, string(abi.encodePacked(uriPrefix, _order, uriSuffix)));

        updateTicketQuantity(msg.sender, _token, _ticketprice, false);

        //addNFT(_tokenId, _order, _token);
        tokenToNFTIDs[_token].push(_tokenId);
        orders[_order] = _tokenId;
        ordersTokens[_order] = _token;
        ordersbynft[_tokenId] = _order;

        // Consolidate the struct assignment
        sorteios[_tokenId] = Sorteio({
            carteira: msg.sender, 
            nft: _tokenId, 
            usuario: userNumber, 
            sorteado: uint256(0)
        });
        emit NFTMinted(msg.sender, _tokenId, _order, userNumber == 0 ? uint256(0) : requestRandomnessProcess(_tokenId));

        return _tokenId;
    }

    function requestRandomnessProcess(uint256 _tokenId) internal returns (uint256) {
        uint256 requestId = requestRandomness(
            callbackGasLimit,
            requestConfirmations,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            paid: VRF_V2_WRAPPER.calculateRequestPrice(callbackGasLimit),
            randomNumber: uint256(0),
            nftId: _tokenId,
            fulfilled: false
        });
        lastGas = s_requests[requestId].paid;
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, _tokenId, numWords);

        return requestId;
    }


    function swapBNBtoLink() external nonReentrant {
        bnbtolink();
    }

    function bnbtolink() internal {
        uint256 balance = address(this).balance;
        require(balance > 0, "No BNB to swap!");

        IPancakeRouter02(UNIROUTER).swapExactETHForTokens{value: balance}(
            0,
            bnbToLinkRoute,
            address(this),
            block.timestamp + 300 // Deadline
        );

        uint256 linkBscAmount = IERC20(LINKBSC).balanceOf(address(this));
        if (linkBscAmount > 0) {
            IPegSwap(PEGSWAP).swap(linkBscAmount, LINKBSC, linkAddress);
        }
    }


    function burn(uint256 tokenId) external onlyOwner {
        _burnCounter += 1;
        super._burn(tokenId);
    }

    function totalSupply() public view returns (uint256) {
        return (tokenCounter - _burnCounter);
    }

    function addChamp(uint256 _amount) external nonReentrant {
        IERC20(USDT).safeTransferFrom(msg.sender, address(this), _amount);
        championship += _amount;      
    }

    function setMaxNumber(uint32 _maxNumber) external onlyOwner {
        maxNumber = _maxNumber;
    }

    function setAutoLink(bool _autolink) external onlyOwner {
        autolink = _autolink;
    }

    function setCallbackGas(uint32 _callbackGasLimit) external onlyOwner {
        callbackGasLimit = _callbackGasLimit;
    }

    function setSlippageToken(uint32 _slippageToken) external onlyOwner {
        slippageToken = _slippageToken;
    }

    function setPorcChamp(uint32 _porcChamp) external onlyOwner {
        porcChamp = _porcChamp;
    }

    function listToken(
        address _token,
        address[] memory routeBnb,
        address[] memory routeUsdt,
        address addPartner,
        uint16 nftprice
    ) public {
        IERC20(_token).approve(UNIROUTER, type(uint256).max);
        tokensData[_token] = TokenData({
            routeBnb: routeBnb,
            routeUsdt: routeUsdt,
            addPartner: addPartner,
            nftprice: nftprice
        });
    }

    function setDev(address _dev) external onlyOwner {
        dev = _dev;
    }

    function setPrice(uint256 _price) external onlyOwner {
        price = _price;
    }

    function setUriPrefix(string memory _uriPrefix) external onlyOwner {
        uriPrefix = _uriPrefix;
    }

    function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {        
        require(_ownerOf(_tokenId) != address(0), "ERC721Metadata: URI query for nonexistent token");
        //require(_exists(_tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory currentBaseURI = _baseURI();
        string memory _order = ordersbynft[_tokenId];
        //string memory _order = getOrderById(_tokenId);

        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, _order, uriSuffix))
            : '';
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uriPrefix;
    }

    function _giveAllowances() internal {
        IERC20(ZCORE).approve(ZCORE, type(uint256).max);
        IERC20(ZCORE).approve(UNIROUTER, type(uint256).max);
        IERC20(WBNB).approve(UNIROUTER, type(uint256).max);
        IERC20(USDT).approve(UNIROUTER, type(uint256).max);
        IERC20(LINKBSC).approve(UNIROUTER, type(uint256).max);
        IERC20(LINKBSC).approve(PEGSWAP, type(uint256).max);
    }

    function forceDraw(uint256 _nftid) external onlyOwner {
        Sorteio storage sorteio = sorteios[_nftid];
        require(sorteio.usuario > 0, "This user did not participate in the draw!");
        require(sorteio.sorteado == 0, "Number has already been drawn!");

        uint256 requestId = requestRandomness(callbackGasLimit, requestConfirmations, numWords);
        uint256 requestPrice = VRF_V2_WRAPPER.calculateRequestPrice(callbackGasLimit);
        s_requests[requestId] = RequestStatus({
            paid: requestPrice,
            randomNumber: 0,
            nftId: _nftid,
            fulfilled: false
        });

        lastGas = requestPrice;
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, _nftid, numWords);
    }


    function linkgas() internal view returns (uint256){
        uint256[] memory _bnbvalue = IPancakeRouter02(UNIROUTER).getAmountsIn(
            lastGas,
            bnbToLinkRoute
        );
        return _bnbvalue[0];
    }

    function getChampionship() external view returns (uint256){
        uint256 newchamp = (championship * porcChamp) / 100; //
        return newchamp;
    }

    function getGasBNB() external view returns (uint256){
        uint256 _getgas = linkgas();
        uint slippage = _getgas / 10;
        _getgas += slippage;
        return _getgas;
    }

    function requestRandomWords(uint256 _nftid) internal returns (uint256 requestId) {
        requestId = requestRandomness(callbackGasLimit, requestConfirmations, numWords);

        // Calculate the request price once and reuse it
        uint256 requestPrice = VRF_V2_WRAPPER.calculateRequestPrice(callbackGasLimit);

        // Update the struct in a single operation
        s_requests[requestId] = RequestStatus({
            paid: requestPrice,
            randomNumber: uint256(0),
            nftId: _nftid,
            fulfilled: false
        });

        // Update global variables only once
        lastGas = requestPrice;
        requestIds.push(requestId);
        lastRequestId = requestId;

        emit RequestSent(requestId, _nftid, numWords);
        return requestId;
    }    


    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        require(s_requests[_requestId].paid > 0, "request not found");
        uint256 _randomNumber = (_randomWords[0] % maxNumber) + 1;

        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomNumber = _randomNumber;

        Sorteio storage sorteio = sorteios[s_requests[_requestId].nftId];
        if (sorteio.sorteado == 0) {
            sorteio.sorteado = _randomNumber;
            if (sorteio.usuario == _randomNumber) {
                uint256 newchamp = championship * (porcChamp) / 100; //
                emit NewWinner(block.timestamp, sorteio.carteira, newchamp);
                winners[sorteio.carteira] += newchamp;
                championship -= newchamp;
            }
        }

        emit RequestFulfilled(_requestId, _randomNumber, s_requests[_requestId].nftId, s_requests[_requestId].paid);
    }


    function getPrize() external nonReentrant {
        require(winners[msg.sender] > 0, "no prize!");
        IERC20(USDT).safeTransfer(msg.sender, winners[msg.sender]);
        emit WinnerPrize(block.timestamp, msg.sender, winners[msg.sender]);
        winners[msg.sender] = 0;
    }


    function updateTicketQuantity(address user, address tokenAddress, uint quantity, bool add) internal {
        if(tokenAddress != ZCORE && (tokensData[tokenAddress].routeBnb.length == 0 || tokensData[tokenAddress].routeUsdt.length == 0)) {
            revert("Invalid Token!");
        }             
        uint len = userTickets[user].length;
        bool ticketFound = false;
        for (uint i = 0; i < len; i++) {
            if (userTickets[user][i].tokenAddress == tokenAddress) {
                Ticket storage ticket = userTickets[user][i];
                if (add) {
                    ticket.quantity += quantity;
                } else {
                    require(ticket.quantity >= quantity, "Not enough tickets");
                    ticket.quantity -= quantity;
                    if (ticket.quantity == 0) {
                        userTickets[user][i] = userTickets[user][len - 1];
                        userTickets[user].pop();
                    }
                }
                ticketFound = true;
                break;
            }
        }
        if (!ticketFound && add) {
            userTickets[user].push(Ticket(tokenAddress, quantity));
        }
    }


    function transferTickets(address to, address tokenAddress, uint quantity) external nonReentrant {
        require(msg.sender != to, "Cannot transfer tickets to yourself");
        require(hasEnoughTickets(msg.sender, tokenAddress, quantity), "Insufficient tickets");
        updateTicketQuantity(msg.sender, tokenAddress, quantity, false);
        updateTicketQuantity(to, tokenAddress, quantity, true);
    }

    function getTotalTickets(address user) public view returns (uint total) {
        uint len = userTickets[user].length;
        for (uint i = 0; i < len; i++) total += userTickets[user][i].quantity;
    }

    function getTicketQuantity(address user, address tokenAddress) public view returns (uint) {
        uint len = userTickets[user].length;
        for (uint i = 0; i < len; i++) {
            if (userTickets[user][i].tokenAddress == tokenAddress) return userTickets[user][i].quantity;
        }
        return 0;
    }

    function hasEnoughTickets(address user, address tokenAddress, uint quantity) internal view returns (bool) {
        uint len = userTickets[user].length;
        for (uint i = 0; i < len; i++) {
            if (userTickets[user][i].tokenAddress == tokenAddress && userTickets[user][i].quantity >= quantity) return true;
        }
        return false;
    }

    function withdrawLink() external onlyOwner {        
        LinkTokenInterface link = LinkTokenInterface(linkAddress);
        link.transfer(msg.sender, link.balanceOf(address(this)));
    }    

    receive() external payable {}
    fallback() external payable {}
