// Define the AsrCrowdSale contract
contract AsrTokenSale is ReentrancyGuard {
    using SafeMath for uint256;

    // The token that is being sold
    IERC20 public asrToken;

    // The USDT token that is used as payment
    IBEP20 public usdt;

    // The rate of tokens per USDT
    uint256 public rate;

    // The wallet that receives the funds
    address private wallet;

    address public owner;

    // The event that is emitted when tokens are purchased
    event TokensPurchased(address indexed buyer, uint256 amount);

    // The constructor that sets the token, usdt, rate and wallet
    constructor(IERC20 _asrToken, IBEP20 _usdt, uint256 _rate, address  _wallet) {
        require(address(_asrToken) != address(0), "AsrTokenSale: invalid token address");
        require(address(_usdt) != address(0), "AsrTokenSale: invalid usdt address");
        require(_rate > 0, "AsrTokenSale: invalid rate");
        require(_wallet != address(0), "AsrTokenSale: invalid wallet address");
        asrToken = _asrToken;
        usdt = _usdt;
        rate = _rate; // Set the rate of tokens per USDT
        wallet = _wallet;
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }


    // The function that allows users to purchase tokens by specifying the amount of USDT
    function buyTokens(address beneficiary, uint256 usdtAmount) public nonReentrant {
        require(beneficiary != address(0), "AsrTokenSale: beneficiary is zero address");
        require(usdtAmount > 0, "usdtAmount must be greater than 0");
        require(usdt.balanceOf(msg.sender) >= usdtAmount,"Insufficent Funds");

        // Calculate the amount of tokens to be purchased based on the rate
        uint256 tokens = usdtAmount.mul(rate);

        // Transfer the USDT from the sender to the wallet
        usdt.transferFrom(msg.sender, wallet, usdtAmount);

        // Transfer the tokens from the contract to the beneficiary
        asrToken.transfer(beneficiary, tokens);


        // Emit the event
        emit TokensPurchased(beneficiary, tokens);
    }

    function withdrawAsrTokens(uint256 amount) external onlyOwner  {
        require(amount > 0, "Amount must be greater than 0");
 
        require(asrToken.balanceOf(address(this)) >= amount, "Insufficient Asr token balance");
        
        // Transfer ASR tokens from this contract to the owner
        require(asrToken.transfer(owner, amount), "Asr token transfer failed");
    }

    function changeRate(uint256 newRate) external onlyOwner {
        require(newRate > 0);
        rate = newRate;
    }
}
