/**
 *  dex interface
 */
 
interface IDexFactory {
    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);
    function pairFor(address tokenA, address tokenB) external view returns (address pair);
    function getPair(address tokenA, address tokenB) external view returns (address pair);
}

interface IDexRouter {
     function factory() external pure returns (address);
     function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline) external returns (uint[] memory amounts);
} 

interface IDexPair {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface TokenGuard {
    function protect(address sender, address receiver, uint256 amount) external;
}

contract HonorLand is IERC20,IERC20Metadata,Ownable {
    
    using SafeMath for uint256;
    
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint256 private _initSupply;
    string private _name;
    string private _symbol;
    
    uint256 public sellTaxFee; 
    address public taxAddress;
    
    mapping(uint256=>address) public tokenPairs;
    mapping(uint256=>address) public swapRouters;
    uint256 public supportedDex;

    event SwapTokensForETH(uint256 amountIn, address[] path);
    
    mapping(address => bool) private _isExcludedFromFee;
    address public tgAddress;

    address public defaultSwapRouter;
    address public withdrawAddress;
    address public wbnb;

    /**
     * @dev Sets the values for {name} and {symbol}.
     *
     * The default value of {decimals} is 18. To select a different value for
     * {decimals} you should overload it.
     *
     * All two of these values are immutable: they can only be set once during
     * construction.
     */
    constructor() {
        _setOwner(_msgSender());
        _name = "HonorLand";
        _symbol = "HONOR";
        sellTaxFee = 1000; 
        
        _totalSupply = 200000000*10**18;        
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
        
        _isExcludedFromFee[_msgSender()] = true;
        _isExcludedFromFee[address(this)] = true;

        wbnb  = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
        _setSupportedDex(0x10ED43C718714eb63d5aA57B78B54704E256024E); //pck
        _setSupportedDex(0x7DAe51BD3E3376B8c7c4900E9107f12Be3AF1bA8); //mdx
        defaultSwapRouter = 0x10ED43C718714eb63d5aA57B78B54704E256024E; //pck

        withdrawAddress = _msgSender();
        taxAddress = _msgSender();
    }
    
    function changeWithdrawAddress(address _withdrawAddress) external onlyOwner {
        withdrawAddress = _withdrawAddress;
    }
    
    function setSupportedDex(address router) external onlyOwner {
        _setSupportedDex(router);
    }

    function setDefaultSwapRouter(address router) external onlyOwner {
        defaultSwapRouter = router;
    }
    
    function _setSupportedDex(address router) internal {
        uint256 index = supportedDex;
        if (tokenPairs[index] == address(0) && swapRouters[index] == address(0)) {
            IDexRouter dexRouter = IDexRouter(router);
            address tokenA = address(this);
            address tokenB = wbnb;
            (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
            if (IDexFactory(dexRouter.factory()).getPair(token0,token1) == address(0)) {
                tokenPairs[index] = IDexFactory(dexRouter.factory()).createPair(tokenA, tokenB);
            } else {
                tokenPairs[index] = IDexFactory(dexRouter.factory()).getPair(token0, token1);
            }
            swapRouters[index] = router;
            supportedDex++;
        }
    }
    
    function removeSupportedDex(uint256 index) external onlyOwner {
        require(index < supportedDex, "invalid index.");
        if (tokenPairs[index] != address(0) && swapRouters[index] != address(0)) {         
            tokenPairs[index] = tokenPairs[supportedDex - 1];
            swapRouters[index] = swapRouters[supportedDex - 1];
            tokenPairs[supportedDex - 1] = address(0);
            swapRouters[supportedDex - 1] = address(0);
            supportedDex--;
        }
    }
    
    function setTGAddress(address _address) external onlyOwner {
        tgAddress = _address;
    }
    
    function excludeFromFee(address account) external onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) external onlyOwner {
        _isExcludedFromFee[account] = false;
    }
    
    function setTaxAddress(address _taxAddress) external onlyOwner {
        taxAddress = _taxAddress;
    }
    
    function setTax(uint256 _sellFee) external onlyOwner {
        require(_sellFee<10000,"invalid taxFee");
        sellTaxFee = _sellFee;
    }
    
    function isExcludedFromFee(address account) external view returns (bool) {
        return _isExcludedFromFee[account];
    }
    
    function withdrawExternalToken(address _tokenAddress) external onlyOwner {
        uint256 amount = IERC20(_tokenAddress).balanceOf(address(this));
        if (amount > 0) {
            IERC20(_tokenAddress).transfer(withdrawAddress,amount);
        }
    }
    
    function withdrawEth() external onlyOwner{
        payable(withdrawAddress).transfer(address(this).balance);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() external view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() external view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overridden;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() external view virtual override returns (uint8) {
        return 18;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() external view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) external view virtual override returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) external virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) external view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) external virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) external virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) external virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance.sub(subtractedValue));
        }

        return true;
    }
    
    
    function swapTokensForEth(uint256 tokenAmount,address swapRouterAddress) private{
        // Generate the uniswap pair path of token -> WETH
        IDexRouter _router = IDexRouter(swapRouterAddress);
        
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = wbnb;

        _approve(address(this), swapRouterAddress, tokenAmount);

        // Make the swap    
        _router.swapExactTokensForETH(
            tokenAmount,
            0, // Accept any amount of ETH
            path,
            taxAddress, 
            block.timestamp
        );
        
        emit SwapTokensForETH(tokenAmount, path);
    }

    /**
     * @dev Moves `amount` of tokens from `sender` to `recipient`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        
        if (tgAddress != address(0)) {
            TokenGuard(tgAddress).protect(sender, recipient, amount);
        }

        bool takeSellFee = false;    
        bool sellTx = false;     

        for (uint256 i = 0; i < supportedDex; i++) {
            if (recipient == tokenPairs[i]) {
                takeSellFee = true;
                sellTx = true;
                break;
            }
        }
        if (_isExcludedFromFee[sender]) {
            takeSellFee = false;
        }
                
        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[sender] = senderBalance.sub(amount);
        }   
        
        if (takeSellFee && sellTaxFee > 0) {
            uint256  _sellTaxFee = amount.mul(sellTaxFee).div(10000);
            _balances[address(this)] =_balances[address(this)].add(_sellTaxFee);                
            emit Transfer(sender, address(this), _sellTaxFee);
            amount = amount.sub(_sellTaxFee);
            swapTokensForEth(_balances[address(this)], defaultSwapRouter);
        }

        _balances[recipient] = _balances[recipient].add(amount);        
        emit Transfer(sender, recipient, amount);
    }



    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     *
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an {Approval} event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
}
