pragma solidity 0.8.6;

contract NFTBLACKMARKET {
    
    mapping(address => uint) private confirmers ;

    constructor() {
    }
    
    function addConfirmer(address _address, uint _version) public {
        confirmers[_address] = _version;
    }
    
    function checkConfirmer(address _address, uint _version) public view returns (bool){
        if (confirmers[_address] == _version){
            return true;
        } else {
            return false;
        }
    }
}
