    "contracts/Sale/StockTokenSaleFactory.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "../Role/Operator.sol";
import "./AtlantisSaleProxy.sol";
import "./AtlantisSaleLogic.sol";


contract StockTokenSaleFactory is Context, Ownable, Operator {

    address public tokenSaleLogic;
    address public atlantisToken;
    address payable public fundingWallet;
    address public proxyAdmin;

    event NewTokenSaleCreated(
        address indexed theTokenSale,
        uint256 totalToSale,
        uint256 dollarRate,
        uint256 minimumBuyable,
        address fundingWallet,
        address[] baseTokens,
        address[] oracles
    );

    event FundingWalletUpdated(address indexed newFundingWallet);
    event TokenSaleLogicUpdated(address indexed newTokenSaleLogic);
    event ProxyAdminUpdated(address indexed newProxyAdmin);

    /**
     * @dev Set the beneficiary, start timestamp and vesting duration of the vesting wallet.
     */
    constructor(
        address _tokenSaleLogic,
        address _atlantisToken,
        address payable _fundingWallet,
        address _proxyAdmin
    ) {

        require(
            _tokenSaleLogic != address(0),
            "SaleFactory: logic address is zero"  
        );

        require(
            _atlantisToken != address(0),
            "SaleFactory: token address is zero"  
        );

        require(
            _fundingWallet != address(0),
            "SaleFactory: funding address is zero"  
        );

        require(
            _proxyAdmin != address(0),
            "SaleFactory: proxy admin address is zero"  
        );

        tokenSaleLogic = _tokenSaleLogic;
        atlantisToken = _atlantisToken;
        fundingWallet = _fundingWallet;
        proxyAdmin = _proxyAdmin;

    }

    /**
     * @dev create token sale contract
     */
    function createTokenSale(
        uint256 totalToSale,
        uint256 dollarRate,
        uint256 minimumBuyable,
        address[] memory baseTokens,
        address[] memory oracles
    ) external onlyOperator returns (address){

        bytes memory nullData;

        AtlantisSaleProxy theTokenSaleProxy = new AtlantisSaleProxy(
            tokenSaleLogic,
            proxyAdmin,
            nullData
        );

        address theSaleAddr = address(theTokenSaleProxy);

        AtlantisSaleLogic(theSaleAddr).initialize(
            atlantisToken,
            totalToSale,
            dollarRate,
            minimumBuyable,
            fundingWallet,
            baseTokens,
            oracles
        );

        AtlantisSaleLogic(theSaleAddr).transferOwnership(_msgSender());

        emit NewTokenSaleCreated(
            theSaleAddr,
            totalToSale,
            dollarRate,
            minimumBuyable,
            fundingWallet,
            baseTokens,
            oracles
        );
        
        return theSaleAddr;
    }

    /**
     * @dev Add new operator
     */
    function addNewOperator(address newOprtr) external onlyOwner {
        _addOperator(newOprtr);
    }

     /**
     * @dev Add new operator
     */
    function removeOperator(address theOprtr) external onlyOwner {
        _removeOperator(theOprtr);
    }


         /**
     * @dev Add new operator
     */
    function updateFundingWallet(address payable newFundingWallet) external onlyOwner {
        require(
            newFundingWallet != address(0),
            "SaleFactory: funding address is zero"  
        );

        fundingWallet = newFundingWallet;

        emit FundingWalletUpdated(newFundingWallet);
    }

    function updateTokenSaleLogic(address  newTokenSaleLogic) external onlyOwner {
        require(
            newTokenSaleLogic != address(0),
            "SaleFactory: logic address is zero"  
        );

        tokenSaleLogic = newTokenSaleLogic;

        emit TokenSaleLogicUpdated(newTokenSaleLogic);
    }

    function updateProxyAdmin(address  newProxyAdmin) external onlyOwner {
        require(
            newProxyAdmin != address(0),
            "SaleFactory: proxy admin address is zero"  
        );

        proxyAdmin = newProxyAdmin;

        emit ProxyAdminUpdated(newProxyAdmin);
    }


}
"
    },
