abstract contract ERC721Enumerable is ERC721, IERC721Enumerable {
    // Mapping from owner to list of owned token IDs
    mapping(address => mapping(uint256 => uint256)) private _ownedTokens;

    // Mapping from token ID to index of the owner tokens list
    mapping(uint256 => uint256) private _ownedTokensIndex;

    // Array with all token ids, used for enumeration
    uint256[] private _allTokens;

    // Mapping from token id to position in the allTokens array
    mapping(uint256 => uint256) private _allTokensIndex;

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(IERC165, ERC721)
        returns (bool)
    {
        return
            interfaceId == type(IERC721Enumerable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev See {IERC721Enumerable-tokenOfOwnerByIndex}.
     */
    function tokenOfOwnerByIndex(address owner, uint256 index)
        public
        view
        virtual
        override
        returns (uint256)
    {
        require(
            index < ERC721.balanceOf(owner),
            "ERC721Enumerable: owner index out of bounds"
        );
        return _ownedTokens[owner][index];
    }

    /**
     * @dev See {IERC721Enumerable-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _allTokens.length;
    }

    /**
     * @dev See {IERC721Enumerable-tokenByIndex}.
     */
    function tokenByIndex(uint256 index)
        public
        view
        virtual
        override
        returns (uint256)
    {
        require(
            index < ERC721Enumerable.totalSupply(),
            "ERC721Enumerable: global index out of bounds"
        );
        return _allTokens[index];
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);

        if (from == address(0)) {
            _addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0)) {
            _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    /**
     * @dev Private function to add a token to this extension's ownership-tracking data structures.
     * @param to address representing the new owner of the given token ID
     * @param tokenId uint256 ID of the token to be added to the tokens list of the given address
     */
    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = ERC721.balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    /**
     * @dev Private function to add a token to this extension's token tracking data structures.
     * @param tokenId uint256 ID of the token to be added to the tokens list
     */
    function _addTokenToAllTokensEnumeration(uint256 tokenId) private {
        _allTokensIndex[tokenId] = _allTokens.length;
        _allTokens.push(tokenId);
    }

    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId)
        private
    {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = ERC721.balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }

    function _removeTokenFromAllTokensEnumeration(uint256 tokenId) private {
        
        uint256 lastTokenIndex = _allTokens.length - 1;
        uint256 tokenIndex = _allTokensIndex[tokenId];
        uint256 lastTokenId = _allTokens[lastTokenIndex];

        _allTokens[tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
        _allTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index

        // This also deletes the contents at the last position of the array
        delete _allTokensIndex[tokenId];
        _allTokens.pop();
    }
}

contract BlackLkisted is Ownable {
    mapping(address => bool) isBlacklisted;

    function blackList(address _user) public onlyOwner {
        require(!isBlacklisted[_user], "user already blacklisted");
        isBlacklisted[_user] = true;
        // emit events as well
    }

    function removeFromBlacklist(address _user) public onlyOwner {
        require(isBlacklisted[_user], "user already whitelisted");
        isBlacklisted[_user] = false;
        // emit events as well
    }
}

contract Whitelist is BlackLkisted {
    mapping(address => bool) whitelist;
    event AddedToWhitelist(address indexed account);
    event RemovedFromWhitelist(address indexed account);

    modifier onlyWhitelisted() {
        require(isWhitelisted(msg.sender));
        _;
    }

    function addToWhiteList(address _address) public onlyOwner {
        whitelist[_address] = true;
        emit AddedToWhitelist(_address);
    }

    function removeToWhiteList(address _address) public onlyOwner {
        whitelist[_address] = false;
        emit RemovedFromWhitelist(_address);
    }

    function isWhitelisted(address _address) public view returns (bool) {
        return whitelist[_address];
    }
}

contract CRYPTOINK is ERC721Enumerable, Whitelist {
    using SafeMath for uint256;

    string private _baseTokenURI =
        "https://ciknft.online/API/metadata/";

    address payable public admin;
    bool public hasSaleStarted = true;
    uint256 public adminFees;
    uint256 public floorPrice;
    uint256 public floorPriceToken;
    constructor() ERC721("CRYPTOINK NFT Marketplace", "CRYPTOINKNFTs") {
        adminFees = 4;
        floorPrice = 0.00 ether;
        floorPriceToken = 5 gwei;
    }

    modifier onlyAdmin() {
        require(admin == _msgSender(), "Error: caller is not the admin");
        _;
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        _baseTokenURI = baseURI;
    }

    // Start Sale, we can start minting!
    function toggleSale(bool _sale) public onlyOwner {
        hasSaleStarted = _sale;
    }

    // Function for set the Development Fees
    function setContractFees(uint256 _sellingFees) public onlyOwner {
        adminFees = _sellingFees;
    }

    function updateAdmin(address payable newAdmin) public onlyOwner {
        admin = newAdmin;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    function setFloorPrice(uint256 FloorPrice) public onlyOwner {
        floorPrice = FloorPrice;
    }

    //set token mint fees
    function setFloorPriceCIK(uint256 FloorPriceCIK) public onlyOwner {
        floorPriceToken = FloorPriceCIK;
    }

    function getBaseURI() public view returns (string memory) {
        return _baseTokenURI;
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        return
            string(abi.encodePacked(_baseTokenURI, Strings.toString(_tokenId)));
    }

    function setTokenPrice(
        uint256 price,
        uint256 tokenId,
        bool sell,
        bool isToken,
        address currency
    ) public {
        require(
            msg.sender == NFTINFO[tokenId].ownerAddress,
            "Only NFT Owner Can Set The Price"
        );
        NFTINFO[tokenId].price = price;
        NFTINFO[tokenId].sell = sell;
        NFTINFO[tokenId].isToken = isToken;
        NFTINFO[tokenId].currency = currency;
    }

    function walletOfOwner(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }
    function mintPublic(
        address to,
        uint256 tokenId,
        uint256 royalties,
        bool isToken,
        uint256 amount,
        address paymentCurrency
    ) public payable {
        require(!isBlacklisted[to], "caller is backlisted");
        require(hasSaleStarted == true, "Sale hasn't started");
        if (isToken) {
            require(amount >= floorPriceToken, "Error: Low Miting Fees");
            IBEP20(paymentCurrency).transferFrom(msg.sender, owner(), amount);
            NFTINFO[tokenId].price = floorPriceToken;
        } else {
            require(msg.value >= floorPrice, "Error: Low Minting Fees");
            payable(owner()).transfer(msg.value);
            NFTINFO[tokenId].price = floorPrice;
        }

        _safeMint(to, tokenId);
        NFTINFO[tokenId].royalties = royalties;
        NFTINFO[tokenId].ownerAddress = payable(to);
        NFTINFO[tokenId].creatorAddress = payable(to);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].isToken = isToken;
        NFTINFO[tokenId].currency = paymentCurrency;
    }

    function mint(
        address to,
        uint256 tokenId,
        uint256 royalties,
        address creator,
        bool isToken,
        address paymentCurrency
    ) public onlyAdmin {
        _safeMint(to, tokenId);
        if (isToken) {
            NFTINFO[tokenId].price = floorPriceToken;
        } else {
            NFTINFO[tokenId].price = floorPrice;
        }
        NFTINFO[tokenId].royalties = royalties;
        NFTINFO[tokenId].ownerAddress = payable(to);
        NFTINFO[tokenId].creatorAddress = payable(creator);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].isToken = isToken;
        NFTINFO[tokenId].currency = paymentCurrency;
    }

    function burn(address to, uint256 tokenId) public onlyAdmin {
        _burn(to, tokenId);
        NFTINFO[tokenId].price = 0;
        NFTINFO[tokenId].ownerAddress = address(0);
        NFTINFO[tokenId].creatorAddress = address(0);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].isToken = false;
        NFTINFO[tokenId].currency = address(0);
    }

    function buy(
        address from,
        address to,
        uint256 tokenId,
        address currency,
        bool isToken
    ) public onlyAdmin {
        _transfer(from, to, tokenId);
        NFTINFO[tokenId].price = 0;
        NFTINFO[tokenId].ownerAddress = payable(to);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].isToken = isToken;
        NFTINFO[tokenId].currency = currency;
    }
    function transferNFT(uint256 tokenId, uint256 amount) public payable {
        if (NFTINFO[tokenId].isToken == true) {
            buyWithTokens(tokenId, amount);
        } else {
            buyWithBNB(tokenId);
        }
    }

    function buyWithTokens(uint256 tokenId, uint256 amount) internal {
        require(!isBlacklisted[msg.sender], "caller is backlisted");
        require(hasSaleStarted, "Sale hasn't started");
        require(NFTINFO[tokenId].sell, "Currently NFT is NOT For Sell");
        require(NFTINFO[tokenId].isToken, "This NFT Can not Sell With Tokens");

        require(
            amount >= NFTINFO[tokenId].price,
            "The Token submitted with this transaction is too low."
        );
        address _owner = ownerOf(tokenId);
        uint256 owner_fees = uint256(100) -
            adminFees -
            NFTINFO[tokenId].royalties;

        if (adminFees > 0) {
            IBEP20(NFTINFO[tokenId].currency).transferFrom(
                msg.sender,
                owner(),
                (amount * adminFees) / 100
            );
        }
        if (NFTINFO[tokenId].royalties > 0) {
            IBEP20(NFTINFO[tokenId].currency).transferFrom(
                msg.sender,
                NFTINFO[tokenId].creatorAddress,
                (amount * NFTINFO[tokenId].royalties) / 100
            );
        }
        if (owner_fees > 0) {
            IBEP20(NFTINFO[tokenId].currency).transferFrom(
                msg.sender,
                _owner,
                (amount * owner_fees) / 100
            );
        }
        _transfer(_owner, msg.sender, tokenId);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].ownerAddress = msg.sender;
    }

    function buyWithBNB(uint256 tokenId) public payable {
        require(!isBlacklisted[msg.sender], "caller is backlisted");
        require(hasSaleStarted, "Sale hasn't started");
        require(NFTINFO[tokenId].sell, "Currently NFT is NOT For Sell");
        require(!NFTINFO[tokenId].isToken, "This NFT Can not Sell With BNB");
        require(
            msg.value >= NFTINFO[tokenId].price,
            "The value submitted with this transaction is too low."
        );

        uint256 owner_fees = uint256(100) -
            adminFees -
            NFTINFO[tokenId].royalties;
        address payable _owner = payable(ownerOf(tokenId));
        _owner.transfer((msg.value * owner_fees) / (100));
        payable(owner()).transfer((msg.value * adminFees) / (100));
        payable(NFTINFO[tokenId].creatorAddress).transfer(
            (msg.value * NFTINFO[tokenId].royalties) / (100)
        );
        _transfer(_owner, msg.sender, tokenId);
        NFTINFO[tokenId].sell = false;
        NFTINFO[tokenId].ownerAddress = msg.sender;
    }
}
