pragma solidity ^0.8.4;
pragma experimental ABIEncoderV2;

import "libraries/LCorite_ERC1155.sol";

interface ICorite_ERC1155 {
    function ownedCollections(address _owner) external view returns (uint256[] memory);

    function ownedCampaigns(address _owner) external view returns (uint256[] memory);

    function currentNonce(address _user) external returns (uint256);

    function createCampaign(address _owner, uint256 _supplyCap, uint256 _toBackersCap) external returns (uint256);

    function closeCampaign(uint256 _campaign) external;

    function mintCampaignShares(uint256 _campaign, uint256 _amount, address _to) external;

    function mintExcessShares(uint _campaign, address _to) external;

    function setCampaignCancelled(uint256 _campaign, bool _cancelled) external;

