pragma solidity ^0.8.0;

import "./IERC20.sol";
import "./ERC20.sol";
import "./SafeMath.sol";
import "./ReentrancyGuard.sol";
import "./Ownable.sol";

contract BMKStakeBitMarketToken is ERC20, ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    IERC20 private _token;

    uint256 totalAmountStaked;
    uint256 maximumAmountStaked;
    uint256 minimumAmountToStake;
    bool paused;

    struct Stake {
        address user;
        uint256 amount;
        uint256 since;
    }

    struct Stakeholder {
        address user;
        Stake[] address_stakes;
    }

    event Staked(
        address indexed user,
        uint256 amount,
        uint256 index,
        uint256 timestamp
    );

    mapping(address => uint256) internal stakes;
    mapping(address => uint256) internal lastStakeId;
    Stakeholder[] internal stakeholders;

    constructor(address _ERC20Address) ERC20("BMK - Stake BitMarket token", "Stake BitMarket") {
        _token = IERC20(_ERC20Address);
        maximumAmountStaked = 100000 ether;
        minimumAmountToStake = 100 ether;
        paused = false;
        stakeholders.push();
    }

    function addStakeholder(address staker) internal returns (uint256) {
        stakeholders.push();
        uint256 userIndex = stakeholders.length - 1;
        stakeholders[userIndex].user = staker;
        stakes[staker] = userIndex;
        return userIndex;
    }

    function stake(uint256 _amount) external nonReentrant {
        require(!paused, "Staking is currently paused!");
        require(
            _amount >= minimumAmountToStake,
            "Minimum stake amount is 100 BMK."
        );
        require(
            (totalAmountStaked + _amount) <= maximumAmountStaked,
            "Staking limit exceeded. Lower your stake!"
        );
        require(_amount > 0, "Can't stake 0 tokens!");
        require(
            _amount <= _token.balanceOf(msg.sender),
            "Can't stake more than you own!"
        );

        uint256 index = stakes[msg.sender];
        uint256 timestamp = block.timestamp;

        if (index == 0) {
            index = addStakeholder(msg.sender);
        }

        Stake memory new_stake = Stake(msg.sender, _amount, timestamp);
        stakeholders[index].address_stakes.push(new_stake);
        lastStakeId[msg.sender]++;

        _token.transferFrom(msg.sender, address(this), _amount);
        totalAmountStaked = totalAmountStaked + _amount;
        emit Staked(msg.sender, _amount, index, timestamp);
    }

    function computeStakeReward(Stake memory _currentStake)
        internal
        view
        returns (uint256)
    {
        //11% ROI/an => rewardPerHour = 0.001255% ( 11 / (365 * 24))
        uint256 sinceIsStaking = _currentStake.since;
        uint256 currentTimestamp = block.timestamp;

        uint256 reward = currentTimestamp.sub(sinceIsStaking);
        reward = reward.div(1 hours);
        reward = reward.mul(_currentStake.amount);
        reward = reward.mul(1255);
        reward = reward.div(10**8);
        return reward;
    }

    function harvestReward(uint256 _stakeIndex) public nonReentrant {
        uint256 user_index = stakes[msg.sender];
        Stake memory currentStake = stakeholders[user_index].address_stakes[
            _stakeIndex
        ];
        uint256 currentTimestamp = block.timestamp;
        uint256 sinceIsStaking = currentStake.since;
        uint256 minimumAmount = 10 ether;
        uint256 reward = computeStakeReward(currentStake);

        require(reward >= minimumAmount, "Minimum harvest is 10 BMK");
        require(
            currentTimestamp - sinceIsStaking >= 1 weeks,
            "Minimum period until you can harvest your reward is 7 days"
        );

        stakeholders[user_index].address_stakes[_stakeIndex].since = block
            .timestamp;

        _token.transfer(msg.sender, reward);
    }

    function withdrawStake(uint256 _stakeIndex) public nonReentrant {
        uint256 user_index = stakes[msg.sender];

        Stake memory currentStake = stakeholders[user_index].address_stakes[
            _stakeIndex
        ];
        uint256 currentTimestamp = block.timestamp;
        uint256 sinceIsStaking = currentStake.since;
        uint256 minimumTime = 1 weeks;

        require(
            currentTimestamp - sinceIsStaking >= minimumTime,
            "Minimum period until you can withdraw is 7 days"
        );

        uint256 reward = computeStakeReward(currentStake);
        uint256 totalReward = currentStake.amount + reward;

        totalAmountStaked = totalAmountStaked - currentStake.amount;
        currentStake.amount = 0;
        delete stakeholders[user_index].address_stakes[_stakeIndex];

        _token.transfer(msg.sender, totalReward);
    }

    function withdrawTokens(uint256 _amount) external onlyOwner {
        _token.transfer(msg.sender, _amount);
    }

    function computeRewardPerStake(uint256 _stakeIndex)
        public
        view
        returns (uint256)
    {
        require(
            _stakeIndex >= 0 && _stakeIndex <= lastStakeId[msg.sender],
            "Wrong stake ID!"
        );

        uint256 user_index = stakes[msg.sender];
        Stake memory currentStake = stakeholders[user_index].address_stakes[
            _stakeIndex
        ];

        uint256 reward = computeStakeReward(currentStake);
        return reward;
    }

    function getTotalRewardOfStaker(address staker)
        public
        view
        onlyOwner
        returns (uint256)
    {
        require(
            staker != address(0),
            "Staker address must not be equal to zero-address!"
        );

        uint256 totalReward;
        uint256 user_index = stakes[staker];
        Stake[] memory currentStakes = stakeholders[user_index].address_stakes;
        for (uint256 i = 0; i < currentStakes.length; i++) {
            totalReward = totalReward + computeStakeReward(currentStakes[i]);
        }
        return totalReward;
    }

    function getTotalReward() public view onlyOwner returns (uint256) {
        uint256 totalReward;
        for (uint256 i = 0; i < stakeholders.length; i++) {
            for (
                uint256 j = 0;
                j < stakeholders[i].address_stakes.length;
                j++
            ) {
                totalReward =
                    totalReward +
                    computeStakeReward(stakeholders[i].address_stakes[j]);
            }
        }
        return totalReward;
    }

    function forceStop() external onlyOwner {
        uint256 stakeReward;
        uint256 stakeAmount;
        address staker;

        for (uint256 i = 1; i < stakeholders.length; i++) {
            stakeReward = 0;
            stakeAmount = 0;
            staker = stakeholders[i].user;
            for (
                uint256 j = 0;
                j < stakeholders[i].address_stakes.length;
                j++
            ) {
                stakeAmount =
                    stakeAmount +
                    stakeholders[i].address_stakes[j].amount;
                stakeReward =
                    stakeReward +
                    computeStakeReward(stakeholders[i].address_stakes[j]);
                stakeholders[i].address_stakes[j].amount = 0;
                delete stakeholders[i].address_stakes[j];
            }
            _token.transfer(staker, stakeAmount + stakeReward);
        }

        paused = true;
    }

    function getTotalStakedOfStaker(address _staker)
        external
        view
        onlyOwner
        returns (uint256)
    {
        uint256 totalStaked;
        uint256 user_index = stakes[_staker];

        Stake[] memory currentStakes = stakeholders[user_index].address_stakes;
        for (uint256 i = 0; i < currentStakes.length; i++) {
            totalStaked = totalStaked + currentStakes[i].amount;
        }

        return totalStaked;
    }

    function computeTotalReward() external view returns (uint256) {
        uint256 user_index = stakes[msg.sender];
        uint256 totalReward;

        Stake[] memory userStakes = stakeholders[user_index].address_stakes;
        for (uint256 i = 0; i < userStakes.length; i++) {
            totalReward = totalReward + computeStakeReward(userStakes[i]);
        }

        return totalReward;
    }

    function canStake(uint256 _amount) external view returns (bool, uint256) {
        if (totalAmountStaked + _amount <= maximumAmountStaked)
            return (true, _amount);
        return (false, maximumAmountStaked - totalAmountStaked);
    }

    function getStakesNumber() external view returns (uint256) {
        uint256 user_index = stakes[msg.sender];
        return stakeholders[user_index].address_stakes.length;
    }

    function getAllStakes() external view returns (Stake[] memory) {
        uint256 user_index = stakes[msg.sender];
        return stakeholders[user_index].address_stakes;
    }

    function getBMKBalance() external view returns (uint256) {
        return _token.balanceOf(msg.sender);
    }

    function getMaximumAmountStaked() external view returns (uint256) {
        return maximumAmountStaked;
    }

    function getTotalStaked() public view onlyOwner returns (uint256) {
        return totalAmountStaked;
    }

    function setPaused(bool _state) external onlyOwner {
        paused = _state;
    }

    function setMaximumAmountStaked(uint256 _amount) external onlyOwner {
        uint256 amountToEther = _amount * (10**uint256(decimals()));
        maximumAmountStaked = amountToEther;
    }

    function setMinimumAmountToStake(uint256 _amount) external onlyOwner {
        uint256 amountToEther = _amount * (10**uint256(decimals()));
        minimumAmountToStake = amountToEther;
    }
}
