pragma solidity ^0.8.2;
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

contract ECIONFT is Initializable, ERC1155Upgradeable, OwnableUpgradeable, PausableUpgradeable, ERC1155BurnableUpgradeable {

    using Counters for Counters.Counter;
    mapping(uint256 => mapping(address => uint256)) private ownerAirdrop;

    mapping(uint256 => mapping(address => uint256)) private ownerAirdropCount;
    mapping(uint256 => uint256) private airdropQuota;
    mapping(uint256 => Counters.Counter) private airdropIds;

    //Referral
    mapping(uint256 => address[])  public ownerAirdropAddress; 
    mapping(uint256 => mapping(address=>bool))  private hasRecordOwnerAirdropAddress; 
    mapping(uint256 => mapping(address => uint256)) public ownerReferralCount;

    event getNFTAirdropEvent(address indexed to, uint256 nftId, uint256 nftNumber, bytes data);

    function initialize() initializer public {
        __ERC1155_init("https://ecio.space/nfts/{id}.png");
        __Ownable_init();
        __Pausable_init();
        __ERC1155Burnable_init();
    }

    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setAirdropQuota(uint256 nftId, uint256 amounts)
        public
        onlyOwner
    {
         airdropQuota[nftId] = amounts;
    }


    function mint(address account, uint256 id, uint256 amount, bytes memory data)
        public
        onlyOwner
    {
        _mint(account, id, amount, data);
    }




    function toBytes(uint256 x) private pure returns (bytes memory b) {
         b = new bytes(32);
         assembly { mstore(add(b, 32), x) }
    }




    function getNFTAirdrop(uint256 nftId, address referralAddress) public {

        if(referralAddress != address(0)){
            ownerReferralCount[nftId][referralAddress] = ownerReferralCount[nftId][referralAddress] + 1;

             if(hasRecordOwnerAirdropAddress[nftId][referralAddress] != true){
                     hasRecordOwnerAirdropAddress[nftId][referralAddress] = true;
                     ownerAirdropAddress[nftId].push(referralAddress);
            }
        }

        require(airdropQuota[nftId] != 0 ,"Don't have quota");

        require(ownerAirdropCount[nftId][msg.sender] < airdropQuota[nftId], "You have received all rights.");


        uint256 nftNumber = airdropIds[nftId].current();
        airdropIds[nftId].increment();


        _safeTransferFrom(owner(), msg.sender, nftId, airdropQuota[nftId],  toBytes(nftNumber));

        ownerAirdropCount[nftId][msg.sender] = ownerAirdropCount[nftId][msg.sender] + 1;


        emit getNFTAirdropEvent(msg.sender,nftId, nftNumber,  toBytes(nftNumber));
    }


    function mintBatch(address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        public
        onlyOwner
    {
        _mintBatch(to, ids, amounts, data);
    }

    function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }


    function nftReferralScore(uint id)
        public
        view
        virtual
        returns (uint256[] memory,address[] memory)
    {

        uint256[] memory scores = new uint256[](ownerAirdropAddress[id].length);
        address[] memory accounts = new address[](ownerAirdropAddress[id].length);

        for (uint256 i = 0; i < ownerAirdropAddress[id].length; ++i) {
            scores[i] = ownerReferralCount[id][ownerAirdropAddress[id][i]];
            accounts[i] = ownerAirdropAddress[id][i];
        }

        return (scores,accounts);
    }
}



