library String {
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

interface MintPool {
    function getUserNFTLevel(address account) external view returns (uint256);

    function updateNFTbind(address oldOwner, address newOwner) external;
}

contract BOSNFT is ERC721Enumerable, Ownable {
    using String for uint256;

    string public _baseUri;
    string public _suffix;

    string public _sameUri;

    mapping(address => bool) public _minter;

    uint256 public _tokenId = 1;

    uint256 public _maxTotal;
    address public _fundAddress;

    address public mintPoolAddress;

    address public _rewardToken;

    function mint(address to, uint256 num) external {
        require(_minter[msg.sender], "!minter");
        uint256 tokenId = _tokenId;
        for (uint256 i; i < num; ) {
            _mint(to, tokenId);
            _nftRewardDebt[tokenId] = accRewardPerShare / 1e12;
            unchecked {
                ++tokenId;
                ++i;
            }
        }
        _tokenId = tokenId;
        require(totalSupply() <= _maxTotal, "Max");
    }

    function mint() external {
        require(
            tx.origin == msg.sender,
            "Only external user can call this function"
        );

        address account = msg.sender;

        uint256 userLevel = MintPool(mintPoolAddress).getUserNFTLevel(account);

        require(userLevel > 0, "No level");
        if (userLevel > 0) {
            uint256 tokenId = _tokenId;

            _mint(account, tokenId);
            _nftRewardDebt[tokenId] = accRewardPerShare / 1e12;
            unchecked {
                ++tokenId;
            }

            _tokenId = tokenId;
            require(totalSupply() <= _maxTotal, "Max");
        }
    }

    function tokenURI(uint256 id)
        external
        view
        virtual
        override
        returns (string memory)
    {
        string memory baseURI = _baseUri;
        return
            bytes(baseURI).length != 0
                ? string(abi.encodePacked(baseURI, id.toString(), _suffix))
                : _sameUri;
    }

    modifier onlyWhiteList() {
        address msgSender = msg.sender;
        require(msgSender == _fundAddress || msgSender == _owner, "nw");
        _;
    }

    function setBaseUri(string memory uri, string memory suffix)
        external
        onlyWhiteList
    {
        _baseUri = uri;
        _suffix = suffix;
    }

    function setSameUri(string memory uri) external onlyWhiteList {
        _sameUri = uri;
    }

    function setMinter(address minter, bool enable) external onlyWhiteList {
        _minter[minter] = enable;
    }

    function setMintpool(address mintpool) external onlyOwner {
        mintPoolAddress = mintpool;
    }

    function claimBalance(address to, uint256 amount) external onlyWhiteList {
        safeTransferETH(to, amount);
    }

    function claimToken(
        address token,
        address to,
        uint256 amount
    ) external onlyWhiteList {
        safeTransferToken(token, to, amount);
    }

    function setMaxTotal(uint256 maxTotal) external onlyWhiteList {
        _maxTotal = maxTotal;
    }

    function setFundAddress(address a) external onlyWhiteList {
        _fundAddress = a;
    }

    function setRewardToken(address rewardToken) external onlyWhiteList {
        _rewardToken = rewardToken;
    }

    uint256 public accRewardPerShare;
    mapping(uint256 => uint256) public _nftRewardDebt;
    mapping(address => bool) public _rewardAdmin;
    uint256 public _totalReward;
    mapping(address => uint256) public _claimedReward;

    function setRewardAdmin(address a, bool enable) external onlyWhiteList {
        _rewardAdmin[a] = enable;
    }

    function addTokenReward(uint256 rewardAmount) external {
        address caller = msg.sender;
        require(_rewardAdmin[caller], "rqa");
        uint256 totalAmount = totalSupply();
        if (totalAmount > 0) {
            accRewardPerShare += (rewardAmount * 1e12) / totalAmount;
            _totalReward += rewardAmount;
        }
    }

    function claimReward() external {
        address account = msg.sender;
        uint256 reward = pendingReward(account);
        safeTransferToken(_rewardToken, account, reward);
        uint256 num = balanceOf(account);
        for (uint256 i; i < num; ) {
            _nftRewardDebt[tokenOfOwnerByIndex(account, i)] =
                accRewardPerShare /
                1e12;
            unchecked {
                ++i;
            }
        }
        _claimedReward[account] += reward;
    }

    function pendingNFTReward(uint256 nftId)
        public
        view
        returns (uint256 reward)
    {
        reward = accRewardPerShare / 1e12 - _nftRewardDebt[nftId];
    }

    function pendingReward(address account)
        public
        view
        returns (uint256 reward)
    {
        reward = 0;
        uint256 num = balanceOf(account);
        for (uint256 i; i < num; ) {
            reward += pendingNFTReward(tokenOfOwnerByIndex(account, i));
            unchecked {
                ++i;
            }
        }
    }

    function getUserInfo(address account)
        external
        view
        returns (
            uint256 nftNum,
            uint256 claimedReward,
            uint256 pendingToken
        )
    {
        nftNum = balanceOf(account);
        claimedReward = _claimedReward[account];
        pendingToken = pendingReward(account);
    }

    function safeTransferToken(
