interface IPancakeRouter {
    function factory() external view returns (address);

    function WETH() external view returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (
            uint256 amountToken,
            uint256 amountETH,
            uint256 liquidity
        );

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path)
        external
        view
        returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path)
        external
        view
        returns (uint256[] memory amounts);

    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

interface IPancakeFactory {
    function INIT_CODE_PAIR_HASH() external pure returns (bytes32);

    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}

interface iFriends {
    function parentOf(address child) external view returns (address parent);
}

struct UsedTradeLimit {
    uint256 saled;
    uint256 purchased;
}

contract AIToken is IERC20, IERC20Metadata, Ownable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;

    // 10000 = 100%
    uint256 public feeRadio;
    address public feeSetter;
    address public router;
    address public pair;
    address public aiRouter;
    address public friends;

    // Limit
    bool public tradeLimitEnable;
    UsedTradeLimit public defaultLimit;
    mapping(address => UsedTradeLimit) internal _specialLimitOf;
    mapping(address => mapping(uint256 => UsedTradeLimit)) public usedLimitOf;

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 _sellFee,
        address _router,
        address _token,
        uint256 _total,
        address _recipient
    ) {
        _name = name_;
        _symbol = symbol_;
        feeSetter = _recipient;
        _mint(_recipient, _total);
        tradeLimitEnable = true;

        feeRadio = _sellFee;
        router = _router;
        IPancakeRouter _uniswapV2Router = IPancakeRouter(router);

        pair = IPancakeFactory(_uniswapV2Router.factory()).createPair(
            address(this),
            _token
        );

        _specialLimitOf[pair] = UsedTradeLimit({
            saled: type(uint256).max,
            purchased: type(uint256).max
        });

        _specialLimitOf[address(0xdead)] = UsedTradeLimit({
            saled: type(uint256).max,
            purchased: type(uint256).max
        });

        _specialLimitOf[address(0x0)] = UsedTradeLimit({
            saled: type(uint256).max,
            purchased: type(uint256).max
        });
    }

    function setDefaultTradeLimit(uint256 sale, uint256 purchase)
        external
        onlyOwner
    {
        defaultLimit.saled = sale;
        defaultLimit.purchased = purchase;
    }

    function cleanSpecialLimits(address[] memory owners) external onlyOwner {
        for (uint256 i = 0; i < owners.length; i++) {
            _specialLimitOf[owners[i]].saled = 0;
            _specialLimitOf[owners[i]].purchased = 0;
        }
    }

    function setSpecialLimits(
        address[] memory owners,
        uint256 sale,
        uint256 purchase
    ) external onlyOwner {
        for (uint256 i = 0; i < owners.length; i++) {
            _specialLimitOf[owners[i]].saled = sale;
            _specialLimitOf[owners[i]].purchased = purchase;
        }
    }

    function setTradeLimitEnable() external onlyOwner {
        tradeLimitEnable = true;
    }

    function setTradeLimitDisable() external onlyOwner {
        tradeLimitEnable = false;
    }

    function remainingLimit(address owner)
        public
        view
        returns (uint256 saleLimit, uint256 puarchaseLimit)
    {
        UsedTradeLimit memory limit = _specialLimitOf[owner];
        if (limit.saled == 0 && limit.purchased == 0) {
            limit = defaultLimit;
        }

        UsedTradeLimit memory used = usedLimitOf[owner][
            (block.timestamp / 1 days) * 1 days
        ];

        limit.saled = used.saled > limit.saled ? 0 : limit.saled - used.saled;
        limit.purchased = used.purchased > limit.purchased
            ? 0
            : limit.purchased - used.purchased;

        return (limit.saled, limit.purchased);
    }

    function setFriendsInterface(address friendsAddress) external onlyOwner {
        friends = friendsAddress;
    }

    function setAIRouter(address _router) external onlyOwner {
        aiRouter = _router;
    }

    function transferFeeSetterShip(address _to) external {
        require(msg.sender == feeSetter, "not fee setter");
        feeSetter = _to;
    }

    function setSellFee(uint256 _fee) external {
        require(msg.sender == feeSetter, "not fee to");
        require(_fee < 10000, "fee is not good");
        feeRadio = _fee;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _balances[account];
    }

    function transfer(address to, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        address owner = _msgSender();
        _approve(owner, spender, _allowances[owner][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        address owner = _msgSender();
        uint256 currentAllowance = _allowances[owner][spender];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function transferFromAIRouter(
        address from,
        address to,
        uint256 amount
    ) external returns (bool) {
        address spender = _msgSender();
        require(spender == aiRouter, "only ai router");
        _spendAllowance(from, spender, amount);
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);
        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[from] = fromBalance - amount;
        }

        _balances[to] += amount;
        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
        return true;
    }

    function _requirePurchaseLimit(address owner, uint256 amount) internal {
        if (!tradeLimitEnable) {
            return;
        }

        (, uint256 remainPurchaseLimit) = remainingLimit(owner);

        require(remainPurchaseLimit >= amount, "Insufficient trade limit");

        usedLimitOf[owner][(block.timestamp / 1 days) * 1 days]
            .purchased += amount;
    }

    function _requireSaleLimit(address owner, uint256 amount) internal {
        if (!tradeLimitEnable) {
            return;
        }

        (uint256 remainSaleLimit, ) = remainingLimit(owner);
        require(remainSaleLimit >= amount, "Insufficient trade limit");

        usedLimitOf[owner][(block.timestamp / 1 days) * 1 days].saled += amount;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[from] = fromBalance - amount;
        }

        uint256 fee = 0;
        uint256 toAmount = amount;
        if ((from == pair || to == pair) && feeRadio > 0) {
            fee = (toAmount * feeRadio) / 10000;
            toAmount = toAmount - fee;
        }

        _balances[to] += toAmount;
        emit Transfer(from, to, toAmount);

        if (fee > 0) {
            // 买入行为
            if (from == pair) {
                _requirePurchaseLimit(to, amount);
                if (
                    friends != address(0) &&
                    iFriends(friends).parentOf(to) != address(0xdead)
                ) {
                    address parent = iFriends(friends).parentOf(to);
                    _balances[parent] += fee;
                    emit Transfer(from, parent, fee);
                } else {
                    _balances[address(0)] += fee;
                    emit Transfer(from, address(0), fee);
                }
            }
            // 卖出行为
            else if (to == pair) {
                _requireSaleLimit(from, amount);
                _balances[address(0)] += fee;
                emit Transfer(from, address(0), fee);
            }
        }
        /////////////////////////////////////////////////////////////////

        _afterTokenTransfer(from, to, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }

        _balances[address(0)] += amount;
        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC20: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
