    "contracts/token/TransferWithMessage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./ERC20/IERC20.sol";

contract TransferWithMessage
{
    event NativeTransferMade(address indexed to, uint256 amount, string message);
    event TokenTransferMade(address indexed to, uint256 amount, string message, address indexed token);

    function transferNative(address payable _to, string memory _message) public payable
    {
        require(msg.value > 0, "Value should be more than zero");
        _to.transfer(msg.value);

        emit NativeTransferMade(_to, msg.value, _message);
    }

    function transferToken(address _to, string memory _message, IERC20 _token, uint256 _amount) public
    {
        require(_amount > 0, "Amount should be more than zero");
        require(_token.transferFrom(msg.sender, _to, _amount), "Token transfer failed");

        emit TokenTransferMade(_to, _amount, _message, address(_token));
    }
}"
