    "contracts/BSLFarm.sol": {
      "content": "// SPDX-License-Identifier: BSL
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract BSLFarm is Ownable {
	using SafeMath for uint256;
	using SafeERC20 for IERC20;

	struct UserInfo {
		uint256 amount; // How many LP tokens the user has provided.
		uint256 rewardDebt; // Ignore this reward
		uint256 rewardAmount;
		uint256 lastStakeTime;
	}

	// Info of each pool.
	struct PoolInfo {
		IERC20 poolToken; // Address of LP token contract.
		uint256 allocPoint; // How many allocation points assigned to this pool. BSLs to distribute per block.
		uint256 lastRewardBlock; // Last block number that BSLs distribution occurs.
		uint256 accRewardPerShare; // Accumulated BSLs per share, times 1e12. See below.
		uint256 lockDuration; // in seconds
		uint256 amount;
	}

	// The BSL TOKEN!
	ERC20 public rewardToken;
	uint256 public tokenPerBlock;
	mapping(address => bool) public addedLPs;
	uint256 public BONUS_MULTIPLIER = 1;

	// Info of each pool.
	PoolInfo[] public poolInfo;
	// Info of each user that stakes LP tokens.
	mapping(uint256 => mapping(address => UserInfo)) public userInfo;
	mapping(uint256 => mapping(address => uint256)) public addedUserPositions; // WARNING index from 1
	mapping(uint256 => address[]) public poolUsers;
	// Total allocation points. Must be the sum of all allocation points in all pools.
	uint256 public totalAllocPoint = 0;
	uint256 public startBlock;

	bool public allowEmergencyWithdraw;

	event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
	event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
	event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
	event Harvest(address indexed user, uint256 indexed pid, uint256 amount);

	constructor(
		address _rewardTokenAddress,
		uint256 _rewardPerBlock,
		uint256 _startBlock
	) {
		rewardToken = ERC20(_rewardTokenAddress);
		tokenPerBlock = _rewardPerBlock;
		startBlock = _startBlock;
	}

	modifier verifyPoolId(uint256 _pid) {
		require(_pid < poolInfo.length, "Pool is not exist");
		_;
	}

	function poolLength() external view returns (uint256) {
		return poolInfo.length;
	}

	// Return reward multiplier over the given _from to _to block.
	function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
		return _to.sub(_from).mul(BONUS_MULTIPLIER);
	}

	function getUserInfo(uint256 _pid, address _user)
		public
		view
		returns (
			uint256,
			uint256
		)
	{
		UserInfo memory user = userInfo[_pid][_user];
		return (user.amount, user.lastStakeTime);
	}

	function getUserStakeBalance(uint256 _pid, address _user) external view returns (uint256) {
		return userInfo[_pid][_user].amount;
	}

	// View function to see pending BSLs on frontend.
	function getRewardAmount(uint256 _pid, address _user) external view verifyPoolId(_pid) returns (uint256) {
		PoolInfo memory pool = poolInfo[_pid];
		UserInfo memory user = userInfo[_pid][_user];
		uint256 accRewardPerShare = pool.accRewardPerShare;
		uint256 lpSupply = pool.amount;
		if (block.number > pool.lastRewardBlock && lpSupply != 0) {
			uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
			uint256 rewardAmount = multiplier.mul(tokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
			accRewardPerShare = accRewardPerShare.add(rewardAmount.mul(1e12).div(lpSupply));
		}

		uint256 pendingAmount = user.amount.mul(accRewardPerShare).div(1e12).sub(user.rewardDebt);
		return user.rewardAmount.add(pendingAmount);
	}

	// Update reward variables for all pools. Be careful of gas spending!
	function massUpdatePools() public {
		for (uint256 pid = 0; pid < poolInfo.length; ++pid) {
			updatePool(pid);
		}
	}

	// Update reward variables of the given pool to be up-to-date.
	function updatePool(uint256 _pid) public verifyPoolId(_pid) {
		PoolInfo storage pool = poolInfo[_pid];
		if (block.number <= pool.lastRewardBlock) {
			return;
		}
		uint256 lpSupply = pool.amount;
		if (lpSupply == 0) {
			pool.lastRewardBlock = block.number;
			return;
		}
		uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
		uint256 rewardAmount = multiplier.mul(tokenPerBlock).mul(pool.allocPoint).div(totalAllocPoint);

		pool.accRewardPerShare = pool.accRewardPerShare.add(rewardAmount.mul(1e12).div(lpSupply));
		pool.lastRewardBlock = block.number;
	}

	function stakeLP(uint256 _pid, uint256 _amount) external verifyPoolId(_pid) {
		require(_amount > 0, "amount must be greater than 0");
		updatePool(_pid);

		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];

		// Update last reward
		uint256 pendingAmount = user.amount.mul(pool.accRewardPerShare).div(1e12).sub(user.rewardDebt);
		if (pendingAmount > 0) {
			user.rewardAmount = user.rewardAmount.add(pendingAmount);
		}

		// Add LP
		pool.poolToken.safeTransferFrom(address(msg.sender), address(this), _amount);
		pool.amount = pool.amount.add(_amount);
		user.amount = user.amount.add(_amount);
		user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
		user.lastStakeTime = block.timestamp;

		// Statistical
		if (addedUserPositions[_pid][msg.sender] == 0) {
			poolUsers[_pid].push(msg.sender);
			addedUserPositions[_pid][msg.sender] = poolUsers[_pid].length; // indexing from 1
		}

		emit Deposit(msg.sender, _pid, _amount);
	}

	// Withdraw LP tokens
	function unstakeLP(uint256 _pid, uint256 _amount) public verifyPoolId(_pid) {
		unstakeLPCheck(_pid, _amount, true);
	}

	function unstakeLPCheck(uint256 _pid, uint256 _amount, bool _check) public verifyPoolId(_pid) {
		UserInfo storage user = userInfo[_pid][msg.sender];
		PoolInfo storage pool = poolInfo[_pid];

		require(user.amount >= _amount && _amount > 0, "User is not in this farm or amount is not valid");
		require(user.lastStakeTime.add(pool.lockDuration) < block.timestamp, "Under locked");

		updatePool(_pid);
		uint256 pendingAmount = user.amount.mul(pool.accRewardPerShare).div(1e12).sub(user.rewardDebt);
		if (pendingAmount > 0) {
			user.rewardAmount = user.rewardAmount.add(pendingAmount);
		}

		user.lastStakeTime = block.timestamp;
		user.amount = user.amount.sub(_amount);
		pool.amount = pool.amount.sub(_amount);
		pool.poolToken.safeTransfer(address(msg.sender), _amount);
		user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
		emit Withdraw(msg.sender, _pid, _amount);

		if (_check) {
			checkAndRemovePoolUser(_pid);
		}
	}

	function checkAndRemovePoolUser(uint256 _pid) private {
		uint256 userPosition = addedUserPositions[_pid][msg.sender];
		if (userInfo[_pid][msg.sender].amount == 0 && userPosition > 0) {
			address[] storage users = poolUsers[_pid];
			if (users.length >= userPosition) {
				addedUserPositions[_pid][msg.sender] = 0;
				if (userPosition != users.length) {
					// swap with last if not last
					address lastUser = users[users.length - 1];
					users[userPosition - 1] = lastUser;
					addedUserPositions[_pid][lastUser] = userPosition;
				}
				users.pop(); // then remove last
			}
		}
	}

	function harvest(uint256 _pid) external verifyPoolId(_pid) returns (uint256) {
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];

		updatePool(_pid);

		uint256 pendingAmount = user.amount.mul(pool.accRewardPerShare).div(1e12).sub(user.rewardDebt);
		pendingAmount = user.rewardAmount.add(pendingAmount);
		if (pendingAmount > 0) {
			rewardToken.transfer(msg.sender, pendingAmount);
		}
		user.rewardAmount = 0;
		user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
		emit Harvest(msg.sender, _pid, pendingAmount);
		return pendingAmount;
	}

	// Withdraw without caring about rewards. EMERGENCY ONLY.
	function emergencyWithdraw(uint256 _pid) public verifyPoolId(_pid) {
		emergencyWithdrawCheck(_pid, true);
	}

	function emergencyWithdrawCheck(uint256 _pid, bool _check) public verifyPoolId(_pid) {
		require(allowEmergencyWithdraw, "Not allowed");
		PoolInfo storage pool = poolInfo[_pid];
		UserInfo storage user = userInfo[_pid][msg.sender];
		pool.poolToken.safeTransfer(address(msg.sender), user.amount);
		emit EmergencyWithdraw(msg.sender, _pid, user.amount);
		pool.amount = pool.amount.sub(user.amount);
		user.amount = 0;
		user.rewardDebt = 0;
		user.rewardAmount = 0;
		if (_check) {
			checkAndRemovePoolUser(_pid);
		}
	}

	function getPoolUserLength(uint256 _pid) external view returns (uint256) {
		return poolUsers[_pid].length;
	}

	function getPoolUsers(uint256 _pid) external view returns (address[] memory) {
		address[] memory users = poolUsers[_pid];
		return users;
	}

	function getPoolUserStakes(uint256 _pid) external view returns (address[] memory, uint256[] memory) {
		address[] memory users = poolUsers[_pid];
		uint256[] memory stakeAmount = new uint256[](users.length);
		for (uint256 index = 0; index < users.length; index++) {
			stakeAmount[index] = userInfo[_pid][users[index]].amount;
		}
		return (users, stakeAmount);
	}

	function getSubPoolUsers(
		uint256 _pid,
		uint256 _from,
		uint256 _length
	) external view returns (address[] memory) {
		address[] memory users = poolUsers[_pid];
		if (_from.add(_length) > users.length) {
			_length = users.length.sub(_from);
		}
		address[] memory results = new address[](_length);
		for (uint256 index = 0; index < _length; index++) {
			results[index] = users[_from + index];
		}
		return results;
	}

	function getSubPoolUserStakes(
		uint256 _pid,
		uint256 _from,
		uint256 _length
	) external view returns (address[] memory, uint256[] memory) {
		address[] memory users = poolUsers[_pid];
		if (_from.add(_length) > users.length) {
			_length = users.length.sub(_from);
		}
		address[] memory results = new address[](_length);
		uint256[] memory stakeAmount = new uint256[](_length);
		for (uint256 index = 0; index < _length; index++) {
			results[index] = users[_from + index];
			stakeAmount[index] = userInfo[_pid][results[index]].amount;
		}
		return (results, stakeAmount);
	}

	// ADMIN functions
	function updateMultiplier(uint256 multiplierNumber) external onlyOwner {
		BONUS_MULTIPLIER = multiplierNumber;
	}

	function setTokenPerBlock(uint256 _tokenPerBlock) external onlyOwner {
		massUpdatePools();
		tokenPerBlock = _tokenPerBlock;
	}

	function add(
		uint256 _allocPoint,
		IERC20 _poolToken,
		uint256 _lockDuration,
		bool _withUpdate
	) external onlyOwner {
		require(!addedLPs[address(_poolToken)], "LP Token is already added");
		if (_withUpdate) {
			massUpdatePools();
		}
		uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
		totalAllocPoint = totalAllocPoint.add(_allocPoint);
		poolInfo.push(
			PoolInfo({
				poolToken: _poolToken,
				allocPoint: _allocPoint,
				lastRewardBlock: lastRewardBlock,
				accRewardPerShare: 0,
				lockDuration: _lockDuration,
				amount: 0
			})
		);
		addedLPs[address(_poolToken)] = true;
	}

	function set(
		uint256 _pid,
		uint256 _allocPoint,
		bool _withUpdate
	) external onlyOwner verifyPoolId(_pid) {
		if (poolInfo[_pid].allocPoint != _allocPoint) {
			if (_withUpdate) {
				massUpdatePools();
			}
			totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
			poolInfo[_pid].allocPoint = _allocPoint;
		}
	}

	function setLockDuration(uint256 _pid, uint256 _lockDuration) external onlyOwner verifyPoolId(_pid) {
		if (poolInfo[_pid].lockDuration != _lockDuration) {
			poolInfo[_pid].lockDuration = _lockDuration;
		}
	}

	function removeBNB() external onlyOwner {
		(bool success, ) = payable(msg.sender).call{ value: address(this).balance }("");
		require(success, "Transfer failed");
	}

	function removeOtherToken(address _tokenAddress, address _to) external onlyOwner {
		ERC20 erc20Token = ERC20(_tokenAddress);
		require(erc20Token.transfer(_to, erc20Token.balanceOf(address(this))), "ERC20 Token transfer failed");
	}

	function removeOtherTokenWithAmount(
		address _tokenAddress,
		address _to,
		uint256 _amount
	) external onlyOwner {
		ERC20 erc20Token = ERC20(_tokenAddress);
		require(erc20Token.transfer(_to, _amount), "ERC20 Token transfer failed");
	}

	function changeAllowEmergencyWithdraw(bool _value) external onlyOwner {
		allowEmergencyWithdraw = _value;
	}
}
