    /// @param _mdAddress new MD wallet address
    event MDAddressUpdated(address _mdAddress);

    /// @dev Emitted when the owner update DAO wallet address
    /// @param _daoAddress new DAO wallet address
    event DAOAddressUpdated(address _daoAddress);

    /// @dev Mint GCRED via bridge
    /// @param to Address to mint
    /// @param amount Token amount to mint
    function bridgeMint(address to, uint256 amount) external;

    /// @dev Burn GCRED via bridge
    /// @param owner Address to burn
    /// @param amount Token amount to burn
    function bridgeBurn(address owner, uint256 amount) external;

    /// @dev Mint GCRED via EXO for daily reward
    /// @param to Address to mint
    /// @param amount Token amount to mint
    function mintForReward(address to, uint256 amount) external;

    /**
     * @dev Set Staking reward contract address
     * @param _stakingReward staking contract address
     *
     * Emits a {StakingRewardUpdated} event
     */
    function setStakingReward(address _stakingReward) external;

    /**
     * @dev Set MD(Metaverse Development) wallet address
     * @param _mdAddress MD wallet address
     *
     * Emits a {MDAddressUpdated} event
     */
    function setMDAddress(address _mdAddress) external;

    /**
     * @dev Set DAO wallet address
     * @param _daoAddress DAO wallet address
     *
     * Emits a {DAOAddressUpdated} event
     */
    function setDAOAddress(address _daoAddress) external;
}
"
    },
    "contracts/interface/IStakingReward.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/// @title interface for Staking reward logic
/// @author Tamer Fouad
interface IStakingReward {
    /// @notice Struct for staker's info
    /// @param holder Staking holder address
    /// @param amount Staked amount
    /// @param startDate start date of staking
    /// @param expireDate expire date of staking
    /// @param duration Stake duration
    /// @param latestClaimDate Timestamp for the latest claimed date
    /// @param interestRate Interest rate
    struct StakingInfo {
        address holder;
        uint256 amount;
        uint256 startDate;
        uint256 expireDate;
        uint256 duration;
        uint16 claimedDays;
        uint256 fsnClaimedIdx;
        uint256 lastClaimedDay;
        uint8 interestRate;
        uint256 ExoClaimed;
        uint256 GcreditCalimed;
    }
    // Struct to represent a Claim
    struct Claims {
        uint256 exoAmount;
        uint256 gcredAmount;
        uint256 timestamp;
    }

    /// @dev Emitted when user stake
    /// @param from Staker address
    /// @param amount Staking token amount
    event Stake(address indexed from, uint256 amount, uint8 interestRate);

    /// @notice Claim EXO Rewards by staking EXO
    /// @dev Emitted when the user claim reward
    /// @param to address of the claimant
    /// @param stakedAmount staked amount
    /// @param ExoReward Exo reward amount 
    /// @param GcreditReward GCredit reward amount 
    /// @param claimedDays claimed days 
    event Claim(address indexed to, uint256 stakedAmount, uint256 ExoReward, uint256 GcreditReward, uint8 indexed interestRate, uint256 claimedDays);

    /// @dev Emitted when a stake holder unstakes
    /// @param from address of the unstaking holder
    /// @param amount token amount
    event Unstake(address indexed from, uint256 amount, uint8 interestRate);

    /// @notice Claim GCRED by holding EXO
    /// @dev Emitted when the user claim GCRED reward per day
    /// @param to address of the claimant
    /// @param amount GCRED reward amount
    event ClaimGCRED(address indexed to, uint256 amount);

    /// @notice Claim EXO which is releasing from Foundation Node to prevent inflation
    /// @dev Emitted when the user claim FN reward
    /// @param to address of the claimant
    /// @param amount reward amount from FN
    event ClaimFN(address indexed to, uint256 amount);

    /// @dev Emitted when the owner update EXO token address
    /// @param _exoAddress new EXO token address
    event EXOAddressUpdated(address _exoAddress);

    /// @dev Emitted when the owner update GCRED token address
    /// @param _gcredAddress new GCRED token address
    event GCREDAddressUpdated(address _gcredAddress);

    /// @dev Emitted when the owner update FN wallet address
    /// @param _foundationNode new foundation node wallet address
    event FoundationNodeUpdated(address _foundationNode);

    /**
    * @dev Emitted when the owner change the daily amount for the bonus.
    * @param beforeAmount previous daily amount.
    * @param newAmount new daily amount
     */
    event FSNRewardUpdated(uint256 beforeAmount, uint256 newAmount);
    /**
     * @notice Stake EXO tokens
     * @param _amount Token amount
     * @param _duration staking lock-up period type
     *
     * Requirements
     *
     * - Validate the balance of EXO holdings
     * - Validate lock-up duration type
     *    0: Soft lock
     *    1: 30 days
     *    2: 60 days
     *    3: 90 days
     *    4: 360 days
     *
     * Emits a {Stake} event
     */
    function stake(uint256 _amount, uint8 _duration) external;

    /**
     * @dev Claim reward
     * @param _stakingIndex staking index
     *
     * Emits a {Claim} event
     */
    function claim(uint256 _stakingIndex) external;

    /**
     * @dev Unstake
     * @param _stakingIndex staking index
     *
     * Emits a {Unstake} event
     */
    function unstake(uint256 _stakingIndex) external;

    /// @dev Set new `_tier` of `_holder`
    /// @param _holder foundation node address
    /// @param _tier foundation node address
    function setTier(address _holder, uint8 _tier) external;

    /**
     * @dev Set EXO token address
     * @param _exoAddress EXO token address
     *
     * Emits a {EXOAddressUpdated} event
     */
    function setEXOAddress(address _exoAddress) external;

    /**
     * @dev Set GCRED token address
     * @param _gcredAddress GCRED token address
     *
     * Emits a {GCREDAddressUpdated} event
     */
    function setGCREDAddress(address _gcredAddress) external;

    /**
     * @dev Set Foundation Node address
     * @param _foundationNode foundation node address
     *
     * Emits a {FoundationNodeUpdated} event
     */
    function setFNAddress(address _foundationNode) external;

    /**
     * @dev Retunrs staking infos of `_holder`
     * @param _holder holder address
     */
    function getStakingInfos(
        address _holder
    ) external view returns (StakingInfo[] memory);

    /**
     * @dev Retunrs staked token amount of `_holder`
     * @param _holder holder address
     */
    function getTotalStakedAmount(
        address _holder
    ) external view returns (uint256);

    /**
     * @dev Returns user's tier
     * @param _holder Staking holder address
     */
    function getTier(address _holder) external view returns (uint8);

    /**
     * @dev Returns minimum token amount in tier
     */
    function getTierMinAmount() external view returns (uint88[5] memory);

    /**
     * @dev Returns The acrued staking rewards for a given timeframe
     */
    function getTotalStakingRewards(
        address _user, uint256 _fromTimestamp, uint256 _toTimestamp
    ) external view returns (uint256[] memory, uint256[] memory);

    /**
     * @dev Returns All Claims for a given timeframe
     */
    function getClaims(
        address _user, uint256 _fromTimestamp, uint256 _toTimestamp
    ) external view returns (Claims[] memory);

}
"
    },
    "contracts/staking/StakingReward.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "../interface/IStakingReward.sol";
import "../interface/IEXOToken.sol";
import "../interface/IGCREDToken.sol";
import "../interface/IBonusNFT.sol";

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "hardhat/console.sol";

contract StakingReward is
    Initializable,
    IStakingReward,
    PausableUpgradeable,
    AccessControlUpgradeable
{
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 public constant EXO_ROLE = keccak256("EXO_ROLE");

    uint256 public constant MAX_REWARD = 35e26;
    uint256 public totalRewardAmount;
    // EXO token address
    address public exoAddress;
    // GCRED token address
    address public gcredAddress;
    // Foundation Node wallet which is releasing EXO to prevent inflation
    address public foundationNode;
    // Reward amount from FN wallet
    uint256 public fnReward;
    // All staking infors
    mapping(address => StakingInfo[]) private _stakingInfos;
    // Holder counter in each tier
    uint256[25] private _interestHolderCounter;
    // Tier of the user; Tier 0 ~ 3
    mapping(address => uint8) public tier;
    // Whether holder can upgrade tier status
    mapping(address => bool) public tierCandidate;

    uint256 internal totalSum;
    mapping (uint256 => uint256) public rates;

    address public bonusNFTAddress;
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // Mapping from user's address to all their stakes
    mapping(address => StakingInfo[]) private _allStakes;



    // Mapping from user's address to all their claims
    mapping(address => Claims[]) private _allClaims;



    /// @inheritdoc	IStakingReward
    function stake(
        uint256 _amount,
        uint8 _duration
    ) external override whenNotPaused {
        address holder = _msgSender();
        require(
            _amount <= IERC20Upgradeable(exoAddress).balanceOf(holder),
            "STReward: Insufficient tokens"
        );
        require(_duration < 5, "STReward: Duration doesn't match");

        uint8 interestRate = tier[holder] * 5 + _duration;

        uint88[5] memory minAmount = getTierMinAmount();
        uint32[5] memory period = _getStakingPeriod();

        _stakingInfos[holder].push(
            StakingInfo(
                holder,
                _amount,
                block.timestamp,
                block.timestamp + uint256(period[_duration]),
                _duration,
                0,
                0,
                block.timestamp / 1 days,
                interestRate,
                0,
                0
            )
        );
        _allStakes[holder].push(_stakingInfos[holder][_stakingInfos[holder].length - 1]);
        _interestHolderCounter[interestRate]++;
        setFSNRate(_duration, interestRate, true);
        // Check user can upgrade tier
        if (
            tier[holder] < 4 &&
            _amount >= uint256(minAmount[tier[holder] + 1]) &&
            _duration > tier[holder]
        ) tierCandidate[holder] = true;

        IERC20Upgradeable(exoAddress).transferFrom(
            holder,
            address(this),
            _amount
        );
        emit Stake(holder, _amount, interestRate);
    }

    /// @inheritdoc	IStakingReward
    function unstake(uint256 _stakingIndex) external whenNotPaused {
        address holder = _msgSender();
        require(
            _stakingInfos[holder].length > _stakingIndex,
            "STReward: Invalid staking index"
        );

        StakingInfo memory targetStaking = _stakingInfos[holder][_stakingIndex];
        // Unstake only soft lock or expired staking
        require(
            block.timestamp > targetStaking.expireDate ||
                targetStaking.interestRate % 5 == 0,
            "STReward: Cannot unstake"
        );

        // Last Claim
        claim(_stakingIndex);

        /* The staking date is expired */
        // Upgrade holder's tier
        if (targetStaking.duration >= tier[holder] && tierCandidate[holder]) {
            if (tier[holder] < 4) {
                tier[holder] += 1;
            }
            tierCandidate[holder] = false;
        }
        _interestHolderCounter[targetStaking.interestRate]--;

        setFSNRate(targetStaking.duration, targetStaking.interestRate, false);

        _stakingInfos[holder][_stakingIndex] = _stakingInfos[holder][
            _stakingInfos[holder].length - 1
        ];
        _stakingInfos[holder].pop();

        // Return staked EXO to holder
        IERC20Upgradeable(exoAddress).transfer(holder, targetStaking.amount);
        emit Unstake(holder, targetStaking.amount, targetStaking.interestRate);
    }

    function setFSNRate(uint256 _duration, uint256 _interest, bool isStake) internal {
        uint16[25] memory  _FsnPercents = _getFNRewardPercent();
        if(_duration > 1 && _interest !=2) {
            isStake?totalSum += _FsnPercents[_interest] : totalSum -= _FsnPercents[_interest];
            uint256 _rate = 0;
            if(totalSum != 0) {
                _rate = fnReward / totalSum;
            }
            rates[block.timestamp / 1 days] = _rate;
        }
    }

    /// @inheritdoc IStakingReward
    function setEXOAddress(
        address _exoAddress
    ) external override onlyRole(OWNER_ROLE) {
        require(_exoAddress != address(0), "STReward: Set to zero Address");

        exoAddress = _exoAddress;

        emit EXOAddressUpdated(exoAddress);
    }

    /// @inheritdoc IStakingReward
    function setGCREDAddress(
        address _gcredAddress
    ) external override onlyRole(OWNER_ROLE) {
        require(_gcredAddress != address(0), "STReward: Set to zero Address");

        gcredAddress = _gcredAddress;

        emit GCREDAddressUpdated(gcredAddress);
    }

    /// @inheritdoc	IStakingReward
    function setFNAddress(
        address _foundationAddress
    ) external override onlyRole(OWNER_ROLE) {
        require(
            _foundationAddress != address(0),
            "STReward: Set to zero Address"
        );

        foundationNode = _foundationAddress;

        emit FoundationNodeUpdated(foundationNode);
    }

    /// @inheritdoc	IStakingReward
    function setTier(
        address _holder,
        uint8 _tier
    ) external override onlyRole(EXO_ROLE) {
        tier[_holder] = _tier;
    }

    function pause() external onlyRole(OWNER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(OWNER_ROLE) {
        _unpause();
    }

    /// @inheritdoc IStakingReward
    function getTier(address _holder) external view returns (uint8) {
        return tier[_holder];
    }

    /// @inheritdoc IStakingReward
    function getStakingInfos(
        address _holder
    ) external view returns (StakingInfo[] memory) {
        return _stakingInfos[_holder];
    }

    /// @inheritdoc IStakingReward
    function getTotalStakedAmount(
        address _holder
    ) public view returns (uint256) {
        StakingInfo[] memory stakings = _stakingInfos[_holder];
        uint256 totalStakedAmount = 0;
        for (uint256 i = 0; i < stakings.length; i++) {
            totalStakedAmount += stakings[i].amount;
        }
        return totalStakedAmount;
    }

    function initialize(
        address _exoAddress,
        address _gcredAddress
    ) public initializer {
        __Pausable_init();
        __AccessControl_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OWNER_ROLE, msg.sender);
        _grantRole(EXO_ROLE, _exoAddress);

        exoAddress = _exoAddress;
        gcredAddress = _gcredAddress;
    }

    /// @inheritdoc IStakingReward
    function claim(uint256 _stakingIndex) public whenNotPaused {
        require(
            _stakingInfos[msg.sender].length > _stakingIndex,
            "STReward: Invalid staking index"
        );

        StakingInfo storage targetStaking = _stakingInfos[msg.sender][_stakingIndex];

        /* ---- Claim reward ----*/
        uint256 curDate = block.timestamp >= targetStaking.expireDate
            ? targetStaking.expireDate
            : block.timestamp;
        if(targetStaking.startDate == targetStaking.expireDate) {
            curDate = block.timestamp;
        }
        uint256 stakedDays = (curDate - targetStaking.startDate) / 1 days;

        if (stakedDays > targetStaking.claimedDays) {
            uint16 rewardDays = uint16(stakedDays - targetStaking.claimedDays);
            // Calculate reward EXO amount
            uint256 rewardAPR = _getEXORewardAPR(targetStaking.interestRate);
            uint256 reward = _calcReward(
                targetStaking.amount,
                rewardAPR,
                rewardDays
            );
            (uint256 fsnRewardAmount , uint256 rateIndex) = calcFSNReward(msg.sender, _stakingIndex);
            targetStaking.fsnClaimedIdx = rateIndex;
            uint256 _reward = reward + fsnRewardAmount;
            (uint256 _extraRewardPercent, , , , ,) = IBonusNFT(bonusNFTAddress).getBonusInfoByUser(msg.sender);
            uint256 _bonusAmount = _reward * _extraRewardPercent /10000;
            // Mint reward to targetStaking msg.sender
            IEXOToken(exoAddress).mint(msg.sender, _reward + _bonusAmount );
            totalRewardAmount += _reward + _bonusAmount;

            // Calculate GCRED daily reward
            uint256 gcredReward = (targetStaking.amount *
                _getGCREDReturn(targetStaking.interestRate) *
                rewardDays) / 1e8;
            // send GCRED to msg.sender
            _sendGCRED(msg.sender, gcredReward);

            // Update claimed days
            targetStaking.claimedDays += rewardDays;
            targetStaking.ExoClaimed += _reward + _bonusAmount;
            targetStaking.GcreditCalimed += gcredReward;

            // Add claim to all claims
            _allClaims[msg.sender].push(Claims(_reward + _bonusAmount, gcredReward, block.timestamp));

            // emit Claim(msg.sender, reward, fsnRewardAmount, targetStaking.interestRate);
            emit Claim(
                msg.sender, 
                targetStaking.amount, 
                _reward ,
                gcredReward, 
                targetStaking.interestRate, 
                rewardDays
            );
        }
    }

    function claimBatch(uint256[] memory index) external whenNotPaused {
        for(uint256 i = 0 ; i < index.length; i++) {
            claim(index[i]);
        }
    }
    function setBonusNFTAddress(address _bonusNFT) external onlyRole(OWNER_ROLE) {
        bonusNFTAddress = _bonusNFT;
    }
    /// @dev Minimum EXO amount in tier
    function getTierMinAmount()
        public
        pure
        override
        returns (uint88[5] memory)
    {
        uint88[5] memory tierMinimumAmount = [
            0,
            2_0000_0000_0000_0000_0000_0000,
            4_0000_0000_0000_0000_0000_0000,
            8_0000_0000_0000_0000_0000_0000,
            15_0000_0000_0000_0000_0000_0000
        ];
        return tierMinimumAmount;
    }
    /// @dev Staking period
    function _getStakingPeriod() internal pure returns (uint32[5] memory) {
        uint32[5] memory stakingPeriod = [0, 30 days, 60 days, 90 days, 365 days];
        return stakingPeriod;
    }

    /// @dev EXO Staking reward APR
    function _getEXORewardAPR(
        uint8 _interestRate
    ) internal pure returns (uint8) {
        uint8[25] memory exoRewardAPR = [
            20,
            30,
            40,
            50,
            60,

            25,
            40,
            50,
            60,
            70,

            30,
            50,
            60,
            70,
            85,

            35,
            60,
            70,
            80,
            100,

            60,
            80,
            90,
            100,
            140

        ];
        return exoRewardAPR[_interestRate];
    }

    /// @dev Foundation Node Reward Percent Array
    function _getFNRewardPercent() internal pure returns (uint16[25] memory) {
        uint16[25] memory fnRewardPercent = [
            0,
            0,
            0,
            100,
            250,

            0,
            0,
            125,
            225,
            300,

            0,
            0,
            150,
            275,
            375,

            0,
            0,
            175,
            325,
            400,

            0,
            0,
            200,
            350,
            450
        ];
        return fnRewardPercent;
    }

    /// @dev GCRED reward per day
    function _getGCREDReturn(uint8 _interest) internal pure returns (uint8) {
        uint8[25] memory gcredReturn = [
            0,
            0,
            0,
            90,
            100,

            0,
            0,
            90,
            90,
            100,

            0,
            0,
            90,
            90,
            100,

            0,
            90,
            90,
            90,
            100,

            0,
            90,
            90,
            90,
            100
        ];
        return gcredReturn[_interest];
    }

    /**
     * @dev Claim GCRED as Staking Reward
     * @param _address Target address to claim GCRED
     * @param _amount Value of GCRED to be claimed
     *
     */
    function _sendGCRED(address _address, uint256 _amount) internal {
        IGCREDToken(gcredAddress).mintForReward(_address, _amount);
        emit ClaimGCRED(_address, _amount);
    }

    /**
     * @dev Calculate Reward of days from Anual Reward Value
     * @param _amount Anual Amount of Reward
     * @param _percent Percent Value for Claimable Reward according to Tier status
     * @param _days Number of Days to get claimed
     *
     */
    function _calcReward(
        uint256 _amount,
        uint256 _percent,
        uint16 _days
    ) internal pure returns (uint256) {
        return ((_amount * _percent) * _days) / 365000;
    }

    /**
    * @dev change the daily total amount for the bonus from FSN wallet.
     */
    function setFSNReward(uint256 _amount) external onlyRole(OWNER_ROLE) {
        uint256 _previousFSNReward = fnReward;
        fnReward = _amount;
        emit FSNRewardUpdated(_previousFSNReward, _amount);
    }
    /**
    * @dev calcucate the bonus reward from fsn wallet.
    * @param staker address of a staker.
    * @param stakingIndex skaker's staking index.
     */
    function calcFSNReward(address staker, uint256 stakingIndex) public view returns(uint256 rewardAmount, uint256 rateIndex) {
        uint16[25] memory fnRewardPercent = _getFNRewardPercent();
        if(_stakingInfos[staker].length > stakingIndex) {
            StakingInfo storage targetStaking = _stakingInfos[staker][stakingIndex];
            uint256 _lastDay = targetStaking.lastClaimedDay;
            uint256 _idx = targetStaking.fsnClaimedIdx;
            uint256 curDate = block.timestamp >= targetStaking.expireDate
                ? targetStaking.expireDate / 1 days
                : block.timestamp / 1 days ;
            uint256 temp = rates[_idx];
            for (uint i = _lastDay ; i < curDate ; i++) {
                if(rates[i] !=0 ) {
                    rewardAmount += rates[i] * fnRewardPercent[targetStaking.interestRate];
                    temp = rates[i];
                    rateIndex = i;
                } else {
                    rewardAmount += temp * fnRewardPercent[targetStaking.interestRate];
                }
            }
        }
    }

    function getClaimAmount(address _user, uint256 _index) public view returns(uint256 claimExoAmount, uint256 claimGcreditAmount) {
        address holder = _user;
        if(_stakingInfos[holder].length <= _index) {
            claimExoAmount = 0;
            claimGcreditAmount= 0 ;
        } else {
            StakingInfo storage targetStaking = _stakingInfos[holder][_index];

            /* ---- Claim reward ----*/
            uint256 curDate = block.timestamp >= targetStaking.expireDate
                ? targetStaking.expireDate
                : block.timestamp;
            if(targetStaking.startDate == targetStaking.expireDate ) {
                curDate = block.timestamp;
            }
            uint256 stakedDays = (curDate - targetStaking.startDate) / 1 days;

            if (stakedDays > targetStaking.claimedDays) {
                uint16 rewardDays = uint16(stakedDays - targetStaking.claimedDays);
                // Calculate reward EXO amount
                uint256 rewardAPR = _getEXORewardAPR(targetStaking.interestRate);
                uint256 reward = _calcReward(
                    targetStaking.amount,
                    rewardAPR,
                    rewardDays
                );
                (uint256 fsnRewardAmount , ) = calcFSNReward(holder, _index);
                uint256 _reward = reward + fsnRewardAmount;
                (uint256 _extraRewardPercent, , , , ,) = IBonusNFT(bonusNFTAddress).getBonusInfoByUser(holder);
                uint256 _bonusAmount = _reward * _extraRewardPercent /10000;
                claimExoAmount = _reward + _bonusAmount;
                // Calculate GCRED daily reward
                claimGcreditAmount = (targetStaking.amount *
                    _getGCREDReturn(targetStaking.interestRate) *
                    rewardDays) / 1e8;
            }else {
                claimExoAmount = 0;
                claimGcreditAmount = 0;
            }
        }

    }

    // Function to get the total staking rewards for a user during a period
    function getTotalStakingRewards(address _user, uint256 _fromTimestamp, uint256 _toTimestamp) public view returns (uint256[] memory exoRewards, uint256[] memory gcredRewards) {
        StakingInfo[] memory stakes = _allStakes[_user];
        uint256 dayss = (_toTimestamp - _fromTimestamp) / 1 days;
        exoRewards = new uint256[](dayss);
        gcredRewards = new uint256[](dayss);

        for (uint256 i = 0; i < stakes.length; i++) {
            StakingInfo memory stakeInfo = stakes[i];

            if (stakeInfo.startDate < _toTimestamp && stakeInfo.expireDate > _fromTimestamp) {
                uint256 overlapStart = stakeInfo.startDate > _fromTimestamp ? stakeInfo.startDate : _fromTimestamp;
                uint256 overlapEnd = stakeInfo.expireDate < _toTimestamp ? stakeInfo.expireDate : _toTimestamp;

                for (uint256 j = overlapStart; j < overlapEnd; j += 1 days) {
                    uint256 dayIndex = (j - _fromTimestamp) / 1 days;
                    uint256 exoStakeReward = _calcReward(stakeInfo.amount, stakeInfo.interestRate, 1);
                    uint256 gcredStakeReward = (stakeInfo.amount * _getGCREDReturn(stakeInfo.interestRate)) / 1e8;

                    exoRewards[dayIndex] += exoStakeReward;
                    gcredRewards[dayIndex] += gcredStakeReward;
                }
            }
        }

        return (exoRewards, gcredRewards);
    }

    // Function to get all claims for a user during a period
    function getClaims(address _user, uint256 _fromTimestamp, uint256 _toTimestamp) public view returns (Claims[] memory) {
        Claims[] memory allClaims = _allClaims[_user];
        Claims[] memory claimsInRange = new Claims[](allClaims.length);

        uint256 count = 0;
        for (uint256 i = 0; i < allClaims.length; i++) {
            if (allClaims[i].timestamp >= _fromTimestamp && allClaims[i].timestamp <= _toTimestamp) {
                claimsInRange[count] = allClaims[i];
                count++;
            }
        }

        return claimsInRange;
    }
}
