      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

import './stake.sol';
import './treasury.sol';

contract XFree is ERC20, ERC20Burnable, Stake, Treasury {
    using SafeMath for uint256;
    IERC20 private STAKE_TOKEN;
    constructor() 
        ERC20("XFree coin", "XFREE") 
        Stake(0x12e34cDf6A031a10FE241864c32fB03a4FDaD739)       
        Admin(msg.sender) 
        Treasury(msg.sender, 1000) // 10%
        {
            STAKE_TOKEN = IERC20(stake_token);
        }
    function mintXFREE(address _to,uint256 _amount) private {
        require(_to != address(0), "address can't be ZERO");
        require(getTreasuryAddress() != address(0), "Treasury address can't be ZERO");
        _mint(_to, _amount);
        if (getTreasuryFee() > 0) {
            // MINT SOME TOKENS TO TreasuryAddress if fee > 0
            _mint(getTreasuryAddress(), _amount.mul(getTreasuryFee()).div(10000));            
        }
    }

    function deposit(uint256 _amount, uint256 _days) public isStakeActive() {
        // DEPOSIT
        _deposit(_amount);
        
        // GET INFO
        ( uint256 interest, uint256 xfree, uint256 xbonus ) = stake_interest(_days, _amount);

        // SAFE STAKING
        stake_new(interest, xfree, xbonus, _days, _amount);

        // MINT XFREE BONUS
        mintXFREE( msg.sender, xbonus );
    }

    function withdraw(uint256 uid) public {
        (uint256 free, uint256 xfree) = unstake(uid);
        require(free > 0, "nothing to unstake"); 
        _withdraw(free);
        mintXFREE( msg.sender, xfree );
    }

    // BURN FREE AND GET XFREE
    function convert(uint256 _amount) public isSwapActive() {
        _burnfree(_amount);
        uint256 xfree = _amount.div(xfree_burn_course);
        mintXFREE(msg.sender, xfree);
        emit SWAP_WITH_BURN (
            msg.sender,
            _amount,
            xfree);
    }
    event SWAP_WITH_BURN (
        address indexed account,
        uint256 FREE_BURNED,
        uint256 XFREE_PAID
    );

    // BURN FREE TEST
    function _burnfree(uint256 _amount) private  {
        require(STAKE_TOKEN.balanceOf(address(this)) >= _amount, "not enough tokens in the pool");
        _deposit(_amount);
        ERC20Burnable(stake_token).burn(_amount);
    }

    // DEPOSIT OR WITHDRAW STAKE TOKEN
    function _deposit(uint256 _amount) private {
        require(_amount >= stake_min_amount, "lower then min amount");
        (uint256 ST_balance, uint256 ST_allowance) = stake_token_info(msg.sender);
        require(ST_balance >= _amount, "not enough tokens on the balance");
        require(ST_allowance >= _amount, "allowance is to low");        
        require(STAKE_TOKEN.transferFrom(msg.sender,address(this),_amount), "transferFrom error");
    }
    function _withdraw(uint256 _amount) private {
        require( STAKE_TOKEN.balanceOf(address(this)) >= _amount, "not enough tokens in the pool"); // that's just incase
        require( STAKE_TOKEN.transfer(msg.sender,_amount), "transfer error"); 
    }

    // HELPERS
    function stake_token_info(address _staker) private view returns(uint256 ST_balance, uint256 ST_allowance) {
        // STAKE_TOKEN
        ST_balance = STAKE_TOKEN.balanceOf(_staker);
        ST_allowance = STAKE_TOKEN.allowance(_staker, address(this));
    }
}
"
