    "contracts/stake.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/utils/structs/EnumerableSet.sol';
import "@openzeppelin/contracts/utils/Counters.sol";

import "./interfaces/IStake.sol";
import "./interfaces/IFreePriceOracle.sol";
import "./admin.sol";

struct UserStaking {
    address account;
    uint256 frozendays;
    uint256 stakingId;
    uint256 startDate;
    uint256 unstakeDate;
    uint256 FREE_froze; 
    uint256 XFREE_amount;
    uint256 XFREE_bonus;
    uint256 interest;
    bool STAKE_ACTIVE;
}

abstract contract Stake is IStake, Admin {
    using SafeMath for uint256;    
    using EnumerableSet for EnumerableSet.UintSet;
    using Counters for Counters.Counter;

    uint256 private OneDay = 86400;

    uint256 public stake_min_amount = 500000 * (10 ** 18);
    uint256 public stake_min_time = 30 * OneDay;
    uint256 public stake_max_time = 365 * OneDay;
    address public stake_token;
    address public price_oracle = 0x8afcF0C6561f8c8732da0C3E4C038207210D57E3;

    // USE ORACLE CONTRACT FOR PRICE AMOUNT
    uint256 public xfree_course = IFreePriceOracle(price_oracle).get_stake_free_price();
    uint256 public xfree_burn_course = IFreePriceOracle(price_oracle).get_bearn_free_price();
    uint256 public xfree_bonus = 10;

    bool public stake_active = true;
    bool public swap_active = true;

    mapping(uint256 => UserStaking) public userStakingOf;

    // -> [ percent , days ]
    uint256[] private stake_percent_lvl1 = [1000, stake_min_time];     // start days
    uint256[] private stake_percent_lvl2 = [2000, 180 * OneDay ]; // middle days 182 but we use 180
    uint256[] private stake_percent_lvl3 = [4000, stake_max_time];     // end days

    constructor (address _token) {
        stake_token = _token;
    }
    modifier isStakeActive() {
        require(stake_active, "isStakeActive: currently staking is not available");
        _;
    }
    modifier isSwapActive() {
        require(swap_active, "isSwapActive: currently swap is not available");
        _;
    }

    //
    // ADMIN FUNCTIONS => START
    //
    function set_stake_min_amount(uint256 _free) override public onlyOwner() {
        stake_min_amount = _free;
    }

    // turn on/off new staking
    function set_allow_staking(bool _active) override public onlyOwner() {
        stake_active = _active;
    }

    // turn on/off new swap = burn free for xfree 
    function set_allow_swap(bool _active) override public onlyOwner() {
        swap_active = _active;
    }

    // by def 10 = 0.1%
    function set_xfree_bonus(uint256 _bonus) public onlyOwner() {
        xfree_bonus = _bonus; 
    }

    function set_price_oracle(address _oracle) public onlyOwner() {
        price_oracle = _oracle;
    }

    //
    // ADMIN FUNCTIONS => END
    //
    function _intrest(uint _days) private view returns (uint256) {
            uint256 time = _days * OneDay;
            if (time == stake_percent_lvl3[1]) {
                return stake_percent_lvl3[0];
            } else if (time < stake_percent_lvl2[1]) {
                uint256 index = (stake_percent_lvl2[0] - stake_percent_lvl1[0])
                    .mul(100000)
                    .div(stake_percent_lvl2[1].div(OneDay) - stake_percent_lvl1[1].div(OneDay));
                uint256 extra = (_days - stake_percent_lvl1[1].div(OneDay)) * index;                
                return extra.div(100000) + stake_percent_lvl1[0];
            } else {
                uint256 index = (stake_percent_lvl3[0] - stake_percent_lvl2[0])
                    .mul(100000)
                    .div(stake_percent_lvl3[1].div(OneDay) - stake_percent_lvl2[1].div(OneDay));
                uint256 extra = (_days - stake_percent_lvl2[1].div(OneDay)) * index;
                return extra.div(100000) + stake_percent_lvl2[0];
            }
    }
    function stake_interest(uint _days, uint256 _deposit) public view returns ( uint256 interest, uint256 xfree, uint256 xbonus ) {
        uint256 time = _days * OneDay;
        require(stake_min_time <= time, "stake_interest: lower than minimum time");
        require(stake_max_time >= time, "stake_interest: higher than maximum time");
        interest = _intrest(_days);
        xfree = interest.mul(_days).mul(_deposit).div(10000).div(365).div(xfree_course);
        xbonus = xfree_bonus.mul(_days).mul(_deposit).div(10000).div(365).div(xfree_course);
    }

    Counters.Counter public currentStakingId;
    uint256 totalStakedXCOIN;
    mapping(address => EnumerableSet.UintSet) private stakingIdsOf;
    function stake_new(uint256 interest, uint256 xfree, uint256 xbonus, uint _days, uint256 free) internal {
        currentStakingId.increment();
        userStakingOf[currentStakingId.current()] = UserStaking({
            account: msg.sender,
            frozendays: _days,
            stakingId: currentStakingId.current(),
            startDate: block.timestamp, 
            unstakeDate: block.timestamp.add(_days.mul(OneDay)),
            FREE_froze: free,
            XFREE_amount: xfree,
            XFREE_bonus: xbonus,
            interest: interest,
            STAKE_ACTIVE: true
        });
        stakingIdsOf[msg.sender].add(currentStakingId.current());
        totalStakedXCOIN = totalStakedXCOIN.add(xfree);
        emit STAKE(
            msg.sender,
            _days,
            currentStakingId.current(),
            block.timestamp, 
            block.timestamp.add(_days.mul(OneDay)),
            free,
            xfree,
            xbonus,
            interest
        );
    }
    event STAKE(
        address indexed account,
        uint256 frozendays,
        uint256 indexed stakingId,
        uint256 startDate,
        uint256 unstakeDate,
        uint256 FREE_froze,
        uint256 XFREE_amount,
        uint256 XFREE_bonus,
        uint256 interest       
    );

    function unstake(uint256 stakingId) internal returns(uint256 free, uint256 xfree) {
        UserStaking storage userStaking = userStakingOf[stakingId];
        address userAccount = msg.sender;
        require(userStaking.account == userAccount, 'stake account access error');
        require(userStaking.STAKE_ACTIVE, 'current stake not active');
        require(userStaking.unstakeDate <= block.timestamp, 'unstake date in the future');
        userStaking.STAKE_ACTIVE = false;
        free = userStaking.FREE_froze;
        xfree = userStaking.XFREE_amount;
        totalStakedXCOIN = totalStakedXCOIN.sub(xfree);
        emit UNSTAKE(
            userAccount,
            userStaking.stakingId,
            block.timestamp,
            free,
            xfree);
    }
    event UNSTAKE(
        address indexed account,
        uint256 indexed stakingId,
        uint256 unstakeDate,
        uint256 FREE_PAID,
        uint256 XFREE_PAID
    );

}
"
