    "contracts/treasury.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./admin.sol";

abstract contract Treasury is Admin {

    address private TreasuryAddress;
    uint256 private TreasuryFee = 1000; // 100 is 1% || 10000 is 100%
    uint256 public MaximumFee = 3000; // 30%

    function setTreasuryFee(uint256 _percent) public onlyOwner() {
        require(_percent <= MaximumFee, "setTreasuryFee: maximum treasury fee percent is 30%");
        TreasuryFee = _percent;
    }
    function setTreasuryAddress(address _treasury) public onlyOwner() {
        require(_treasury != address(0), "setTreasuryAddress: is zero address");
        TreasuryAddress = _treasury;
    }
    
    function getTreasuryAddress() public view returns (address) {
        return TreasuryAddress;
    }
    function getTreasuryFee() public view returns (uint256) {
        return TreasuryFee;
    }

    constructor (address _treasury, uint _percent) {
        setTreasuryAddress(_treasury);
        setTreasuryFee(_percent); 
    }

}"
