    "src/CurrencyConverter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import { IERC20Metadata } from "openzeppelin-contracts/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { Context } from "openzeppelin-contracts/contracts/utils/Context.sol";
import { ICurrencyConverter } from "./interfaces/ICurrencyConverter.sol";
import { IProtocolConfig } from "./interfaces/IProtocolConfig.sol";
import { Base } from "./Base.sol";

/// @title CurrencyConverter
/// @notice This smart contract is responsible for converting between different currencies in a financial ecosystem.
/// It allows for the conversion of USD to another currency (e.g., won) and vice versa, using a specified exchange rate.
/// The main functions include setting the exchange rate, retrieving the current exchange rate and its last update
/// timestamp, converting a specified amount from USD to the target currency, and converting a specified amount from the
/// target
/// currency to USD.
/// @dev Use decimal 18 for won.
contract CurrencyConverter is Base, ICurrencyConverter {
    uint256 public constant WON_DECIMALS = 18;
    uint256 public constant EXCHANGE_RATE_DECIMALS = 2;

    uint256 public exchangeRate; // decimal 2 e.g. 1320.25 won -> 132025
    uint256 public updatedTs;
    uint256 public maxExchangeRate = 300_000;

    constructor(
        IProtocolConfig _protocolConfig,
        address _manager,
        uint256 _exchangeRate
    )
        Base(_protocolConfig.protocolAdmin(), _protocolConfig.pauser())
    {
        _grantManagerRole(_manager);
        _setExchangeRate(_exchangeRate);
    }

    /// @inheritdoc ICurrencyConverter
    function setExchangeRate(uint256 _exchangeRate) external {
        _requireManagerRole();
        if (_exchangeRate > maxExchangeRate) {
            revert InvalidExchangeRate(_exchangeRate, maxExchangeRate);
        }
        _setExchangeRate(_exchangeRate);
    }

    function setMaxExchangeRate(uint256 _maxExchangeRate) external {
        _requireManagerRole();
        maxExchangeRate = _maxExchangeRate;
        emit MaxExchangeRateUpdated(_maxExchangeRate);
    }

    /// @inheritdoc ICurrencyConverter
    function getExchangeRate() external view returns (uint256, uint256) {
        return (exchangeRate, updatedTs);
    }

    /// @inheritdoc ICurrencyConverter
    function convertFromUSD(uint256 usdAmount, uint8 usdDecimals) external view returns (uint256 wonAmount) {
        if (WON_DECIMALS < usdDecimals) {
            revert InvalidTargetDecimals();
        }
        wonAmount = usdAmount * exchangeRate * (10 ** (WON_DECIMALS - usdDecimals)) / (10 ** EXCHANGE_RATE_DECIMALS);
    }

    /// @inheritdoc ICurrencyConverter
    function convertToUSD(uint256 wonAmount, uint8 usdDecimals) external view returns (uint256 usdAmount) {
        usdAmount = wonAmount * (10 ** EXCHANGE_RATE_DECIMALS) / exchangeRate / 10 ** (WON_DECIMALS - usdDecimals);
    }

    function _setExchangeRate(uint256 _exchangeRate) internal {
        exchangeRate = _exchangeRate;
        updatedTs = block.timestamp;
        emit ExchangeRateUpdated(_exchangeRate);
    }
}
"
