// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


contract FlyingToken {
    // 代币名称
    string public name = "Aim to fly";
    // 代币符号
    string public symbol = "ATF";
    // 小数点位数
    uint8 public decimals = 4;
    // 总供应量
    uint256 public totalSupply = 10000000 * 10 ** uint256(decimals);
    // 初始分配
    uint256 public initialSupply = totalSupply;
    // 燃烧机制（每次转账金额的百分比）
    uint8 public burnRate = 2;
    // Buy and sell slippage percentage
    uint8 public slippageRate = 5;
    address public owner;

    address private feeAddress = 0x71c196B7B33cCf05b2c3C27eceb62F7aC1525a34;

    // 用于记录每个地址的代币余额
    mapping(address => uint256) private balances;
    // 用于记录每个地址可以从其他地址转移的代币数量
    mapping(address => mapping(address => uint256)) private allowed;

    // 事件：当代币转移发生时触发
    event Transfer(address indexed from, address indexed to, uint256 value);
    // 事件：当代币授权发生时触发
    event Approval(address indexed owner, address indexed spender, uint256 value);

    // 构造函数，初始化合约，将初始供应量分配给合约部署者
    constructor() {
        balances[msg.sender] = initialSupply;
        owner = msg.sender;
        emit Transfer(address(0), msg.sender, initialSupply);
    }
    //设置手续费地址
    function setFeeAddress(address _feeAddress) public {
        feeAddress = _feeAddress;
    }

    // 返回指定地址的代币余额
    function balanceOf(address _owner) public view returns (uint256) {
        return balances[_owner];
    }

    // 从调用者地址向目标地址转移代币
    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to != address(0), "Invalid address");
        require(_value <= balances[msg.sender], "Insufficient balance");

        // 计算燃烧的代币数量
        uint256 burnAmount = (_value * burnRate) / 100;
        // 计算滑点手续费比例
        uint256 slippageAmount = (_value * slippageRate) / 100;
        // 计算实际转移的代币数量
        uint256 transferAmount = _value - burnAmount - slippageAmount;

        // 更新发送者和接收者的代币余额
        balances[msg.sender] -= _value;
        balances[_to] += transferAmount;

        // 触发Transfer事件
        emit Transfer(msg.sender, _to, transferAmount);

        //费用消耗
        _depleteFee(msg.sender, burnAmount, slippageAmount);
        return true;
    }

    // 从一个地址向另一个地址转移代币（需要提前授权）
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_to != address(0), "Invalid address");
        require(_value <= balances[_from], "Insufficient balance");
        require(_value <= allowed[_from][msg.sender], "Insufficient allowance");

        // 计算燃烧的代币数量
        uint256 burnAmount = (_value * burnRate) / 100;
        // 计算滑点手续费比例
        uint256 slippageAmount = (_value * slippageRate) / 100;
        // 计算实际转移的代币数量
        uint256 transferAmount = _value - burnAmount - slippageAmount;

        // 更新发送者、接收者和授权者的代币余额
        balances[_from] -= _value;
        balances[_to] += transferAmount;
        allowed[_from][msg.sender] -= _value;
        // 触发Transfer事件
        emit Transfer(_from, _to, transferAmount);
        //费用消耗
        _depleteFee(_from, burnAmount, slippageAmount);
        return true;
    }

    function _depleteFee(address _from, uint256 _burnAmount, uint256 _slippageAmount) private {
        if (_burnAmount > 0) {
            balances[owner] += _burnAmount;
            emit Transfer(_from, owner, _burnAmount);
        }
        //手续费
        if (_slippageAmount > 0) {
            // 手续费
            balances[feeAddress] += _slippageAmount;
            emit Transfer(_from, feeAddress, _slippageAmount);
        }
    }

    // 授权一个地址可以从发送者地址转移指定数量的代币
    function approve(address _spender, uint256 _value) public returns (bool) {
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    // 返回发送者地址授权给指定地址的代币数量
    function allowance(address _owner, address _spender) public view returns (uint256) {
        return allowed[_owner][_spender];
    }
}
