     * @param lockLiquidity_ A boolean indicating if LP tokens will be locked in contract or sent to contract owner
     */
    function _initLiquidity(uint256 initialLiquidity_, bool lockLiquidity_) internal {
        require(!_liquidityInitialized, "ERC20Liquid: already initialized");
        require(initialLiquidity_ > 0, "ERC20Liquid: initial liquidity cannot be zero");
        require(initialLiquidity_ <= totalSupply(), "ERC20Liquid: liquidity cannot be greater than supply");

