// File contracts/token/UCONToken.sol

pragma solidity ^0.8.17;




contract UCONToken is ERC20Burnable {
  using EnumerableSet for EnumerableSet.AddressSet;

  //////////////////// constant
  // 1 million
  uint256 constant public MaxSupply = 1000000e18;

  // hash("ROLE.MINTER_ROLE");
  bytes32 constant private MINTER_ROLE = 0x8221b61aff6f4968af9d42afad465dfec2aa5fe436937592c3ce866e0e1a3336;

  // hash("KEY.MINER_TARGET_ADDR");
  bytes32 constant private MINER_TARGET_ADDR = 0x36222b0ad8d04ef40d7fed9fc3c033045ccd6a2aa07cf52f2a976795588a7654;

  IConfig immutable public CONFIG;

  //////////////////// storage
  // burnTotalAmount + _totalSupply == realMintAmount;
  uint256 public burnTotalAmount;
  uint256 public realMintAmount;

  EnumerableSet.AddressSet private reverters;
  
  //////////////////// function
  constructor(IConfig config_, string memory name_, string memory symbol_) ERC20(name_, symbol_) {
    CONFIG=config_;
  }

  function isAdmin(address account) internal view returns(bool has){
    return CONFIG.hasRole(Registry.ADMIN_ROLE, account);
  }

  modifier mustAdmin(){
    require(isAdmin(_msgSender()), "not admin");
    _;
  }

  function addReverter(address reverter) external mustAdmin{
    reverters.add(reverter);
  }

  function delReverter(address reverter) external mustAdmin{
    reverters.remove(reverter);
  }

  function getRevertersLength() external view returns(uint256 length){
    return reverters.length();
  }

  function getRevertersAt(uint256 index) external view returns(address addr){
    return reverters.at(index);
  }

  function totalSupply() public view override returns(uint256 total){
    total = super.totalSupply();
    for(uint256 i = 0; i < reverters.length(); i++){
      uint256 rb = IReverter(reverters.at(i)).revertBalance();
      require(rb <= balanceOf(reverters.at(i)), "err rb");
      total -= rb;
    }

    return total;
  }

  function fullTotalSupply() public view returns(uint256 total){
    return super.totalSupply();
  }

  function mint(uint256 amount) public virtual {
    require(CONFIG.hasRole(MINTER_ROLE, _msgSender()), "not minter");
            
    realMintAmount += amount;
    require(realMintAmount <= MaxSupply, "mint exceeds");

    (bytes32 typeID, bytes memory data) = CONFIG.getRawValue(MINER_TARGET_ADDR);
    require(typeID == bytes32(0x421683f821a0574472445355be6d2b769119e8515f8376a1d7878523dfdecf7b), "not addr encode");
    address targetAddr = abi.decode(data, (address));
    _mint(targetAddr, amount);
  }

  function _burn(address account, uint256 amount) internal override {
    super._burn(account, amount);
    burnTotalAmount += amount;    
  }
}
