/** @title Gambet Lottery.
 * @notice It is a contract for a lottery system using
 * randomness provided externally.
 */
contract GemBetLottery is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;
    
    address public operatorAddress;
    struct Ticket {
        uint32 number;
        address owner;
    }
    uint256 public ticketPriceInUsdt;
    uint256 public maxTickets;
    uint256 public soldOutTickets;
    uint256[] public winnerPrizeList;
    uint256[] public winnerCountList;
    uint32 currentTicketId;
    uint256 public totalWinners;
    address public operationWallet;
    address public inflationWallet;
    uint256 public totalPrize;
    uint256 public activateDate;
    uint256 public endDate;
    uint256 public drawDate;
    uint256 public inflationControlFee;
    Ticket[] soldTokenList;
    
    IERC20 public usdtToken;
    IERC20 public rewardToken;

    mapping(uint256 => Ticket) public _tickets;
    mapping(uint256 => uint256) public  winners;
    mapping(address => uint256[]) public _userTicketIds;
    enum Status {
        Pending,
        Open,
        Close,
        Claimable,
        Failed
    }
    Status public lotteryStatus = Status.Pending;

    modifier notContract() {
        require(!_isContract(msg.sender), "Contract not allowed");
        require(msg.sender == tx.origin, "Proxy contract not allowed");
        _;
    }

    modifier onlyOperator() {
        require(msg.sender == operatorAddress, "Not operator");
        _;
    }

    constructor(
        address _usdtTokenAddress, 
        address _rewardTokenAddress, 
        address _operationWallet,
        address _inflationWallet,
        uint256 _ticketPriceInUsdt, 
        uint256 _maxTickets, 
        uint256 _inflationControlFee,
        uint256[] memory _winnerPrizeList, 
        uint256[] memory _winnersCountPerPrize) 
    {
        require(_winnersCountPerPrize.length > 0, "No Winner");
        require(_winnersCountPerPrize.length == _winnerPrizeList.length, "No Winner");
        require(_usdtTokenAddress != address(0), "Invalid Address");
        require(_rewardTokenAddress != address(0), "Invalid Address");
        require(_operationWallet != address(0), "Invalid Address");
        require(_inflationWallet != address(0), "Invalid Address");
        totalPrize = 0;
        if(_usdtTokenAddress == _rewardTokenAddress) {
            for(uint i = 0; i < _winnersCountPerPrize.length; i++) {
                totalPrize = totalPrize + (_winnersCountPerPrize[i] * _winnerPrizeList[i]);
            }

            require(totalPrize <= _maxTickets * _ticketPriceInUsdt * (100 - _inflationControlFee) / 100, "Total Prize should be below than Max tickets amount");
        }
        usdtToken = IERC20(_usdtTokenAddress);
        rewardToken = IERC20(_rewardTokenAddress);
        ticketPriceInUsdt = _ticketPriceInUsdt;
        maxTickets = _maxTickets;
        winnerPrizeList = _winnerPrizeList;
        winnerCountList = _winnersCountPerPrize;
        operationWallet = _operationWallet;
        inflationWallet = _inflationWallet;
        inflationControlFee = _inflationControlFee;
        operatorAddress = msg.sender;
        for(uint32 i = 0; i < winnerCountList.length; i++) {
            totalWinners += winnerCountList[i];
        }
        activate();
    }

    function activate() public onlyOperator {
        require(lotteryStatus == Status.Pending, "Lottery is already actiaved");
        activateDate = block.timestamp;
        lotteryStatus = Status.Open;
    }

    function drawLottery() external onlyOperator {
        require(lotteryStatus == Status.Close, "Lottery is not Opened");
        drawDate = block.timestamp;
        if(rewardToken != usdtToken) {
            usdtToken.safeTransfer(inflationWallet, usdtToken.balanceOf(address(this)));
        } else {
            uint256 amountForInflation = maxTickets * ticketPriceInUsdt * (100 - inflationControlFee) / 100;
            if(amountForInflation > 0) {
                rewardToken.safeTransfer(inflationWallet, amountForInflation);
            }
            uint256 amountForOperationWallet = rewardToken.balanceOf(address(this)) - totalPrize;
            if(amountForOperationWallet > 0) {
                rewardToken.safeTransfer(operationWallet, amountForOperationWallet);
            }
        }
        lotteryStatus = Status.Claimable;
    }

    /**
     * @notice Buy tickets for the current lottery
     * @param _ticketNumbers: array of ticket numbers between 1,000,000 and 1,999,999
     * @dev Callable by users
     */
    function buyTickets(uint32[] calldata _ticketNumbers)
        external
        notContract
        nonReentrant
    {
        require(lotteryStatus == Status.Open, "Lottery is not started yet");
        require(_ticketNumbers.length + currentTicketId <= maxTickets, "Total Tickets are over Max tickets.");
        require(_ticketNumbers.length != 0, "No ticket specified");        

        // Calculate number of USDT to this contract
        uint256 amountUsdtToTransfer = _calculateTotalPriceForBulkTickets(
            _ticketNumbers.length
        );

        // Transfer USDT tokens to this contract
        usdtToken.safeTransferFrom(address(msg.sender), address(this), amountUsdtToTransfer);
        for (uint256 i = 0; i < _ticketNumbers.length; i++) {
            uint32 thisTicketNumber = _ticketNumbers[i];

            // require((thisTicketNumber >= 1000000) && (thisTicketNumber <= 1999999), "Outside range");

            _userTicketIds[msg.sender].push(currentTicketId);

            _tickets[currentTicketId] = Ticket({number: thisTicketNumber, owner: msg.sender});

            // Increase lottery ticket number 
            currentTicketId++;
            soldOutTickets++;
        }

        if(currentTicketId == maxTickets) {
            closeLottery();
        }

    }

    /**
     * @notice Claim a set of winning tickets for a lottery
     * @param _ticketIds: array of ticket ids
     * @dev Callable by users only, not contract!
     */
    function claimTickets(
        uint256[] calldata _ticketIds
    ) external notContract nonReentrant {
        require(_ticketIds.length != 0, "Length must be >0");
        require(lotteryStatus == Status.Claimable, "Lottery not claimable");

        // Initializes the rewardInUsdtToTransfer
        uint256 rewardInUsdtToTransfer;

        for (uint256 i = 0; i < _ticketIds.length; i++) {          
            uint256 thisTicketId = _ticketIds[i];
            require(currentTicketId > thisTicketId, "TicketId too high");
            require(msg.sender == _tickets[thisTicketId].owner, "Not the owner");

            // Update the lottery ticket owner to 0x address
            _tickets[thisTicketId].owner = address(0);

            uint256 rewardForTicketId = _calculateRewardsForTicketId(thisTicketId);

            // Increment the reward to transfer
            rewardInUsdtToTransfer += rewardForTicketId;
        }
       
        // Transfer money to msg.sender
        if(rewardInUsdtToTransfer > 0) {
            rewardToken.safeTransfer(msg.sender, rewardInUsdtToTransfer);
        }
        

    }

    function claimUsdt() external {
        require(_userTicketIds[msg.sender].length > 0, "No tickets for this user");
        require(lotteryStatus == Status.Failed, "Lottery is not failed");
        uint256 _amountToSendUser = _userTicketIds[msg.sender].length * ticketPriceInUsdt;
        rewardToken.safeTransfer(msg.sender, _amountToSendUser);  
    }
    /**
     * @notice Close lottery
     */
    function closeLottery() internal  {
        require(soldOutTickets == maxTickets, "Lottery not over");
        for(uint32 i = 0; i < totalWinners; i++) {
                winners[i] = getRandomNumber(i);
        }
        endDate = block.timestamp;
        lotteryStatus = Status.Close;
    }

    function closeLotteryByAdmin() external onlyOperator {
        require(lotteryStatus == Status.Open);
        endDate = block.timestamp;
        lotteryStatus = Status.Failed;
    }

    function getRandomNumber(uint number) public view returns(uint){
        uint randomNumber;
        uint index = 0;
        while(true) {
            bool isDuplicated = false;
            uint _random = uint(keccak256(abi.encodePacked(block.timestamp, block.prevrandao,  
            msg.sender, number, index))) % maxTickets;
            for(uint i = 0; i < number; i++){
                if(winners[i] == _random) {
                    isDuplicated = true;
                    break;
                }
            }
            if(!isDuplicated) {
                randomNumber = _random;
                break;
            }
            index++;
        }
        
        return randomNumber;
    }

    function getWinnerPrizeList() public view returns(uint256[] memory){       
        return winnerPrizeList;
    }

    function getWinnerCountList() public view returns(uint256[] memory) {
        return winnerCountList;
    }

    function getUserTickets(address account) public view returns(uint256[] memory) {
        return _userTicketIds[account];
    }

    function getTicketIndex(uint256 ticketNumber) public view returns (uint256)  {
        uint256 index = type(uint256).max;
        for(uint256 i = 0; i < currentTicketId; i++) {
            if(ticketNumber == _tickets[i].number) {
                index = i;
            }
        }
        return index;
    }

    function getWinners() external view returns(uint256[] memory) {
        uint256[] memory _winners = new uint256[](totalWinners);
        for(uint256 i = 0; i < totalWinners; i++) {
            _winners[i] = _tickets[winners[i]].number;
        }
        return _winners;
    }

    function isClaimedTicketNumber(uint256 ticketNumber) external view returns(bool) {
        uint256 index = getTicketIndex(ticketNumber);
        require(index < currentTicketId, "Overflow");
        bool isClaimed = false;
        if(_tickets[index].owner == address(0)) {
            isClaimed = true;
        }
        return isClaimed;
    }


    /**
     * @notice It allows the admin to recover wrong tokens sent to the contract
     * @param _tokenAddress: the address of the token to withdraw
     * @param _tokenAmount: the number of token amount to withdraw
     * @dev Only callable by owner.
     */
    function recoverWrongTokens(address _tokenAddress, uint256 _tokenAmount) external onlyOwner {
        require(_tokenAddress != address(usdtToken), "Cannot be USDT token");

        IERC20(_tokenAddress).safeTransfer(address(msg.sender), _tokenAmount);

    }


    function claimablePrize(address _address) external view returns (uint256) {
        uint256[] memory ticketIDs = _userTicketIds[_address];
        uint256 totalClaimable;
        for(uint i = 0; i <  ticketIDs.length; i++) {
            totalClaimable += _calculateRewardsForTicketId(ticketIDs[i]);
        }
        return totalClaimable;
    }
    /**
     * @notice Calculate rewards for a given ticket
     * @param _ticketId: ticket id
     */
    function _calculateRewardsForTicketId(
        uint256 _ticketId
    ) internal view returns (uint256) {
        
        for(uint32 i = 0; i < totalWinners; i++){
            if(winners[i] == _ticketId) {
                uint256 winnersCount = 0;
                for(uint32 j = 0; j < winnerCountList.length; j++) {
                    winnersCount = winnersCount + winnerCountList[j];
                    if(i < winnersCount) {
                        return winnerPrizeList[j];
                    }
                }
            }
        }
        return 0;
    }


    function _calculateTotalPriceForBulkTickets(
        uint256 _numberTickets
    ) internal view returns (uint256) {
        return (ticketPriceInUsdt.mul(_numberTickets));
    }

    /**
     * @notice Check if an address is a contract
     */
    function _isContract(address _addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(_addr)
        }
        return size > 0;
    }

}
