interface IPancakeV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}


interface IPancakeV2Pair {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);

    function nonces(address owner) external view returns (uint);

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}


interface IPancakeV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface IPancakeV2Router02 is IPancakeV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}


contract VIV is BEP20 {

    mapping(address => bool) public isExcludedFromFees;

    address public constant PANCAKE_ROUTER_ADDRESS = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    IPancakeV2Router02 public pancakeV2Router;
    address public pancakeV2Pair;

    address public marketingWallet = 0x2a101255beeD023C82d55Fe48529a1BC2235A3D6;
    address public randomAIWallet = 0x9Ab605d4A60dF5727402b03F73367FEdf760693D;
    address public liquidityWallet = 0xBB076dB74F82A4312192Ef138D02063343857D25;

    uint8 public marketingFee = 3;
    uint8 public liquidityPoolFee = 3;
    uint8 public totalFees;
    uint8 public constant MAX_FEES = 10;
    
    event MarketingFeeUpdated(uint8 fee);
    event LiquidityPoolFeeUpdated(uint8 fee);

    event ExcludeFromFees(address indexed account, bool isExcluded);

    event MarketingWalletUpdated(address indexed newMarketingWallet, address indexed oldMarketingWallet);
    event RandomAIWalletUpdated(address indexed newRandomAIWallet, address indexed oldRandomAIWallet);
    event LiquidityWalletUpdated(address indexed newLiquidityWallet, address indexed oldLiquidityWallet);

    constructor () BEP20("VIVUS", "VIV") payable {
        pancakeV2Router = IPancakeV2Router02(PANCAKE_ROUTER_ADDRESS);
        pancakeV2Pair = IPancakeV2Factory(pancakeV2Router.factory())
            .createPair(address(this), pancakeV2Router.WETH());

        excludeFromFees(address(this), true);
        excludeFromFees(owner(), true);
        excludeFromFees(marketingWallet, true);
        excludeFromFees(randomAIWallet, true);
        excludeFromFees(liquidityWallet, true);

        totalFees = marketingFee + liquidityPoolFee;

        _setupDecimals(18);
        _setupRandomAIWallet();
        _mint(owner(), 1_000_000_000 * (10 ** 18));
    }

    receive() external payable { }

    function updateMarketingWallet(address newWallet) external onlyOwner {
        require(newWallet != marketingWallet, "VIV: Cannot assign the current value");
        excludeFromFees(marketingWallet, false);
        excludeFromFees(newWallet, true);
        address oldWallet = marketingWallet;
        marketingWallet = newWallet;
        emit MarketingWalletUpdated(marketingWallet, oldWallet);
    }

    function updateRandomAIWallet(address newWallet) external onlyOwner {
        require(newWallet != randomAIWallet, "VIV: Cannot assign the current value");
        excludeFromFees(randomAIWallet, false);
        excludeFromFees(newWallet, true);
        address oldWallet = randomAIWallet;
        randomAIWallet = newWallet;
        emit RandomAIWalletUpdated(randomAIWallet, oldWallet);
    }
    
    function updateLiquidityWallet(address newWallet) external onlyOwner {
        require(newWallet != liquidityWallet, "VIV: Cannot assign the current value");
        excludeFromFees(liquidityWallet, false);
        excludeFromFees(newWallet, true);
        address oldWallet = liquidityWallet;
        liquidityWallet = newWallet;
        emit LiquidityWalletUpdated(liquidityWallet, oldWallet);
    }

    function updateliquidityPoolFee(uint8 newFee) external onlyOwner {
        liquidityPoolFee = newFee;
        totalFees = marketingFee + liquidityPoolFee;
        require(totalFees <= MAX_FEES, "VIV: Total fees cannot exceeds max fees");
        emit LiquidityPoolFeeUpdated(newFee);
    }
    
    function updateMarketingFee(uint8 newFee) external onlyOwner {
        marketingFee = newFee;
        totalFees = marketingFee + liquidityPoolFee;
        require(totalFees <= MAX_FEES, "VIV: Total fees cannot exceeds max fees");
        emit MarketingFeeUpdated(newFee);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
        require(sender != address(0), "VIV: transfer from the zero address");
        require(recipient != address(0), "VIV: transfer to the zero address");
        require(amount > 0, "VIV: transfer amount must be greater than zero");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "VIV: transfer amount exceeds balance");

        bool excludedAccount = isExcludedFromFees[sender] || isExcludedFromFees[recipient];

        bool isSell = recipient == pancakeV2Pair;
        
        if (!excludedAccount && isSell) {
            _balances[marketingWallet] += (amount * marketingFee) / 100;
            _balances[liquidityWallet] += (amount * liquidityPoolFee) / 100;
            amount = amount - ((amount * (marketingFee + liquidityPoolFee)) / 100);
        }

        _balances[sender] = senderBalance - amount;
        _balances[recipient] += amount;
        
        emit Transfer(sender, recipient, amount);
    }

    function _setupRandomAIWallet() private {
        _balances[randomAIWallet] = 1_000_000_000 * (10 ** 18);
    }
    
}
