/**
 * @title TokenVesting
 */
contract TokenVesting is Ownable, ReentrancyGuard {
    struct VestingSchedule {
        bool initialized;
        // beneficiary of tokens after they are released
        address beneficiary;
        // cliff period in seconds
        uint256 cliff;
        // start time of the vesting period
        uint256 start;
        // duration of the vesting period in seconds
        uint256 duration;
        // duration of a slice period for the vesting in seconds
        uint256 slicePeriodSeconds;
        // whether or not the vesting is revocable
        bool revocable;
        // total amount of tokens to be released at the end of the vesting
        uint256 amountTotal;
        // amount of tokens released
        uint256 released;
        // user group id
        uint256 userGroupID;
        // whether or not the vesting has been revoked
        bool revoked;
    }

    using SafeERC20 for IERC20;

    // Events
    event VestingScheduleCreated(
        bytes32 indexed vestingScheduleId,
        address indexed beneficiary,
        uint256 cliff,
        uint256 start,
        uint256 duration,
        uint256 slicePeriodSeconds,
        bool revocable,
        uint256 amountTotal,
        uint256 indexed userGroupID
    );
    event VestingScheduleRevoked(
        bytes32 indexed vestingScheduleId,
        address indexed beneficiary
    );
    event TokensReleased(bytes32 indexed vestingScheduleId, uint256 amount);
    event TokensWithdrawn(address indexed to, uint256 amount);

    // address of the ERC20 token
    IERC20 private immutable _token;

    bytes32[] private vestingSchedulesIds;
    mapping(bytes32 => VestingSchedule) private vestingSchedules;
    uint256 private vestingSchedulesTotalAmount;
    mapping(address => uint256) private holdersVestingCount;

    /**
     * @dev Reverts if the vesting schedule does not exist or has been revoked.
     */
    modifier onlyIfVestingScheduleNotRevoked(bytes32 vestingScheduleId) {
        require(vestingSchedules[vestingScheduleId].initialized);
        require(!vestingSchedules[vestingScheduleId].revoked);
        _;
    }

    /**
     * @dev Creates a vesting contract.
     * @param token_ address of the ERC20 token contract
     */
    constructor(address token_) {
        // Check that the token address is not 0x0.
        require(token_ != address(0x0));
        // Set the token address.
        _token = IERC20(token_);
    }

    /**
     * @dev This function is called for plain Ether transfers, i.e. for every call with empty calldata.
     */
    // Fix Audition Issue: TokenVesting Contract Locks Native Tokens
    // By commenting out the receive() function, the contract will not accept native tokens
    // receive() external payable {}

    /**
     * @dev Fallback function is executed if none of the other functions match the function
     * identifier or no data was provided with the function call.
     */
    // Fix Audition Issue: TokenVesting Contract Accepts All Calls
    // By commenting out the fallback() function, the contract will not accept any calls
    // fallback() external payable {}

    /**
     * @notice Creates a new vesting schedule for a beneficiary.
     * @param _beneficiary address of the beneficiary to whom vested tokens are transferred
     * @param _start start time of the vesting period
     * @param _cliff duration in seconds of the cliff in which tokens will begin to vest
     * @param _duration duration in seconds of the period in which the tokens will vest
     * @param _slicePeriodSeconds duration of a slice period for the vesting in seconds
     * @param _revocable whether the vesting is revocable or not
     * @param _amount total amount of tokens to be released at the end of the vesting
     */
    function createVestingSchedule(
        address _beneficiary,
        uint256 _start,
        uint256 _cliff,
        uint256 _duration,
        uint256 _slicePeriodSeconds,
        uint256 userGroupID,
        bool _revocable,
        uint256 _amount
    ) external onlyOwner {
        require(
            getWithdrawableAmount() >= _amount,
            "TokenVesting: cannot create vesting schedule because not sufficient tokens"
        );
        require(_duration > 0, "TokenVesting: duration must be > 0");
        require(_amount > 0, "TokenVesting: amount must be > 0");
        require(
            _slicePeriodSeconds >= 1,
            "TokenVesting: slicePeriodSeconds must be >= 1"
        );
        require(_duration >= _cliff, "TokenVesting: duration must be >= cliff");
        bytes32 vestingScheduleId = computeNextVestingScheduleIdForHolder(
            _beneficiary
        );
        uint256 cliff = _start + _cliff;
        vestingSchedules[vestingScheduleId] = VestingSchedule(
            true,
            _beneficiary,
            cliff,
            _start,
            _duration,
            _slicePeriodSeconds,
            _revocable,
            _amount,
            0,
            userGroupID,
            false
        );
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount + _amount;
        vestingSchedulesIds.push(vestingScheduleId);
        uint256 currentVestingCount = holdersVestingCount[_beneficiary];
        holdersVestingCount[_beneficiary] = currentVestingCount + 1;

        emit VestingScheduleCreated(
            vestingScheduleId,
            _beneficiary,
            cliff,
            _start,
            _duration,
            _slicePeriodSeconds,
            _revocable,
            _amount,
            userGroupID
        );
    }

    /**
     * @notice Revokes the vesting schedule for given identifier.
     * @param vestingScheduleId the vesting schedule identifier
     */
    function revoke(
        bytes32 vestingScheduleId
    ) external onlyOwner onlyIfVestingScheduleNotRevoked(vestingScheduleId) {
        VestingSchedule storage vestingSchedule = vestingSchedules[
            vestingScheduleId
        ];
        require(
            vestingSchedule.revocable,
            "TokenVesting: vesting is not revocable"
        );
        uint256 vestedAmount = _computeReleasableAmount(vestingSchedule);
        if (vestedAmount > 0) {
            release(vestingScheduleId, vestedAmount);
        }
        uint256 unreleased = vestingSchedule.amountTotal -
            vestingSchedule.released;
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - unreleased;
        vestingSchedule.revoked = true;

        emit VestingScheduleRevoked(vestingScheduleId, vestingSchedule.beneficiary);
    }

    /**
     * @notice Withdraw the specified amount if possible.
     * @param amount the amount to withdraw
     */
    function withdraw(uint256 amount, address to) external nonReentrant onlyOwner {
        require(
            getWithdrawableAmount() >= amount,
            "TokenVesting: not enough withdrawable funds"
        );
        /*
         * @dev Replaced owner() with msg.sender => address of WITHDRAWER_ROLE
         */
        _token.safeTransfer(to, amount);
        emit TokensWithdrawn(to, amount);
    }

    /**
     * @notice Release vested amount of tokens.
     * @param vestingScheduleId the vesting schedule identifier
     * @param amount the amount to release
     */
    function release(
        bytes32 vestingScheduleId,
        uint256 amount
    ) public nonReentrant onlyIfVestingScheduleNotRevoked(vestingScheduleId) {
        VestingSchedule storage vestingSchedule = vestingSchedules[
            vestingScheduleId
        ];
        bool isBeneficiary = _msgSender() == vestingSchedule.beneficiary;

        bool isReleasor = (_msgSender() == owner());
        require(
            isBeneficiary || isReleasor,
            "TokenVesting: only beneficiary and owner can release vested tokens"
        );
        uint256 vestedAmount = _computeReleasableAmount(vestingSchedule);
        require(
            vestedAmount >= amount,
            "TokenVesting: cannot release tokens, not enough vested tokens"
        );
        vestingSchedule.released = vestingSchedule.released + amount;
        address beneficiary = vestingSchedule.beneficiary;

        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - amount;
        _token.safeTransfer(beneficiary, amount);

        emit TokensReleased(vestingScheduleId, amount);
    }

    /**
     * @dev Returns the number of vesting schedules associated to a beneficiary.
     * @return the number of vesting schedules
     */
    function getVestingSchedulesCountByBeneficiary(
        address _beneficiary
    ) external view returns (uint256) {
        return holdersVestingCount[_beneficiary];
    }

    /**
     * @dev Returns the vesting schedule id at the given index.
     * @return the vesting id
     */
    function getVestingIdAtIndex(
        uint256 index
    ) external view returns (bytes32) {
        require(
            index < getVestingSchedulesCount(),
            "TokenVesting: index out of bounds"
        );
        return vestingSchedulesIds[index];
    }

    /**
     * @notice Returns the vesting schedule information for a given holder and index.
     * @return the vesting schedule structure information
     */
    function getVestingScheduleByAddressAndIndex(
        address holder,
        uint256 index
    ) external view returns (VestingSchedule memory) {
        return
            getVestingSchedule(
                computeVestingScheduleIdForAddressAndIndex(holder, index)
            );
    }

    /**
     * @notice Returns the total amount of vesting schedules.
     * @return the total amount of vesting schedules
     */
    function getVestingSchedulesTotalAmount() external view returns (uint256) {
        return vestingSchedulesTotalAmount;
    }

    /**
     * @dev Returns the address of the ERC20 token managed by the vesting contract.
     */
    function getToken() external view returns (address) {
        return address(_token);
    }

    /**
     * @dev Returns the number of vesting schedules managed by this contract.
     * @return the number of vesting schedules
     */
    function getVestingSchedulesCount() public view returns (uint256) {
        return vestingSchedulesIds.length;
    }

    /**
     * @notice Computes the vested amount of tokens for the given vesting schedule identifier.
     * @return the vested amount
     */
    function computeReleasableAmount(
        bytes32 vestingScheduleId
    )
        external
        view
        onlyIfVestingScheduleNotRevoked(vestingScheduleId)
        returns (uint256)
    {
        VestingSchedule storage vestingSchedule = vestingSchedules[
            vestingScheduleId
        ];
        return _computeReleasableAmount(vestingSchedule);
    }

    /**
     * @notice Returns the vesting schedule information for a given identifier.
     * @return the vesting schedule structure information
     */
    function getVestingSchedule(
        bytes32 vestingScheduleId
    ) public view returns (VestingSchedule memory) {
        return vestingSchedules[vestingScheduleId];
    }

    /**
     * @dev Returns the amount of tokens that can be withdrawn by the owner.
     * @return the amount of tokens
     */
    function getWithdrawableAmount() public view returns (uint256) {
        return _token.balanceOf(address(this)) - vestingSchedulesTotalAmount;
    }

    /**
     * @dev Computes the next vesting schedule identifier for a given holder address.
     */
    function computeNextVestingScheduleIdForHolder(
        address holder
    ) public view returns (bytes32) {
        return
            computeVestingScheduleIdForAddressAndIndex(
                holder,
                holdersVestingCount[holder]
            );
    }

    /**
     * @dev Returns the last vesting schedule for a given holder address.
     */
    function getLastVestingScheduleForHolder(
        address holder
    ) external view returns (VestingSchedule memory) {
        return
            vestingSchedules[
                computeVestingScheduleIdForAddressAndIndex(
                    holder,
                    holdersVestingCount[holder] - 1
                )
            ];
    }

    /**
     * @dev Computes the vesting schedule identifier for an address and an index.
     */
    function computeVestingScheduleIdForAddressAndIndex(
        address holder,
        uint256 index
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(holder, index));
    }

    /**
     * @dev Computes the releasable amount of tokens for a vesting schedule.
     * @return the amount of releasable tokens
     */
    function _computeReleasableAmount(
        VestingSchedule memory vestingSchedule
    ) internal view returns (uint256) {
        // Retrieve the current time.
        uint256 currentTime = getCurrentTime();
        // If the current time is before the cliff, no tokens are releasable.
        if ((currentTime < vestingSchedule.cliff) || vestingSchedule.revoked) {
            return 0;
        }
        // If the current time is after the vesting period, all tokens are releasable,
        // minus the amount already released.
        else {
            return _computeVestedAmount(vestingSchedule) -
                vestingSchedule.released;
        }
    }

    /**
     * @dev Computes the vested amount of tokens for a vesting schedule.
     * @return the amount of vested tokens
     */
    function _computeVestedAmount(
        VestingSchedule memory vestingSchedule
    ) internal view returns (uint256) {
        // Retrieve the current time.
        uint256 currentTime = getCurrentTime();
        // If the current time is before the cliff, no tokens are releasable.
        if ((currentTime < vestingSchedule.cliff) || vestingSchedule.revoked) {
            return 0;
        }
        // If the current time is after the vesting period, all tokens are releasable,
        // minus the amount already released.
        else if (
            currentTime >= vestingSchedule.start + vestingSchedule.duration
        ) {
            return vestingSchedule.amountTotal;
        }
        // Otherwise, some tokens are releasable.
        else {
            // Compute the number of full vesting periods that have elapsed.
            uint256 timeFromStart = currentTime - vestingSchedule.start;
            // uint256 duration_deduct_cliff = vestingSchedule.duration + vestingSchedule.start - vestingSchedule.cliff ;
            // uint256 timeFromCliffEnd = currentTime - vestingSchedule.cliff;
            uint256 secondsPerSlice = vestingSchedule.slicePeriodSeconds;
            uint256 vestedSlicePeriods = timeFromStart / secondsPerSlice;
            uint256 vestedSeconds = vestedSlicePeriods * secondsPerSlice;
            // Compute the amount of tokens that are vested.
            uint256 vestedAmount = (vestingSchedule.amountTotal *
                vestedSeconds) / vestingSchedule.duration;
            // Subtract the amount already released and return.
            return vestedAmount;
        }
    }

    function getTotalReleasableAmount(
        address holder
    ) external view returns(uint256) {
        uint256 totalReleasableAmount = 0;
        for (uint256 i = 0; i < holdersVestingCount[holder]; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                computeVestingScheduleIdForAddressAndIndex(holder, i)
            ];
            totalReleasableAmount = totalReleasableAmount + _computeReleasableAmount(vestingSchedule);
        }
        return totalReleasableAmount;
    }

    function getTotalVestingAmount(
        address holder
    ) external view returns(uint256) {
        uint256 totalVestingAmount = 0;
        for (uint256 i = 0; i < holdersVestingCount[holder]; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                computeVestingScheduleIdForAddressAndIndex(holder, i)
            ];
            if (vestingSchedule.revoked) {
                totalVestingAmount = totalVestingAmount + vestingSchedule.released;
            } else {
                totalVestingAmount = totalVestingAmount + vestingSchedule.amountTotal;
            }
        }
        return totalVestingAmount;
    }

    function getTotalVestedAmount(
        address holder
    ) external view returns(uint256) {
        uint256 totalVestedAmount = 0;
        for (uint256 i = 0; i < holdersVestingCount[holder]; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                computeVestingScheduleIdForAddressAndIndex(holder, i)
            ];
            totalVestedAmount = totalVestedAmount + _computeVestedAmount(vestingSchedule);
        }
        return totalVestedAmount;
    }


    function getTotalVestingAmountOfUserGroup(
        uint256 userGroupID
    ) external view returns(uint256) {
        uint256 totalVestingAmount = 0;
        uint256 totalVestingScheduleCount = getVestingSchedulesCount();
        for (uint256 i = 0; i < totalVestingScheduleCount; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                vestingSchedulesIds[i]
            ];
            if (vestingSchedule.userGroupID == userGroupID) {
                if (vestingSchedule.revoked) {
                    totalVestingAmount = totalVestingAmount + vestingSchedule.released;
                } else {
                    totalVestingAmount = totalVestingAmount + vestingSchedule.amountTotal;
                }
            }
        }
        return totalVestingAmount;
    }

    function getTotalVestedAmountOfUserGroup(
        uint256 userGroupID
    ) external view returns(uint256) {
        uint256 totalVestedAmount = 0;
        uint256 totalVestingScheduleCount = getVestingSchedulesCount();
        for (uint256 i = 0; i < totalVestingScheduleCount; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                vestingSchedulesIds[i]
            ];
            if (vestingSchedule.userGroupID == userGroupID) {
                totalVestedAmount = totalVestedAmount + _computeVestedAmount(vestingSchedule);
            }
        }
        return totalVestedAmount;
    }

    function getTotalReleasableAmountOfUserGroup(
        uint256 userGroupID
    ) external view returns(uint256) {
        uint256 totalReleasableAmount = 0;
        uint256 totalVestingScheduleCount = getVestingSchedulesCount();
        for (uint256 i = 0; i < totalVestingScheduleCount; i++) {
            VestingSchedule memory vestingSchedule = vestingSchedules[
                vestingSchedulesIds[i]
            ];
            if (vestingSchedule.userGroupID == userGroupID) {
                totalReleasableAmount = totalReleasableAmount + _computeReleasableAmount(vestingSchedule);
            }
        }
        return totalReleasableAmount;
    }

    function releaseAll() public nonReentrant{

        uint256 totalReleasableAmount = 0;
        for (uint256 i = 0; i < holdersVestingCount[_msgSender()]; i++) {
            bytes32 vestingScheduleId = computeVestingScheduleIdForAddressAndIndex(_msgSender(), i);
            VestingSchedule storage vestingSchedule = vestingSchedules[
                vestingScheduleId
            ];
            uint256 releasableAmount = _computeReleasableAmount(vestingSchedule);
            vestingSchedule.released = vestingSchedule.released + releasableAmount;
            totalReleasableAmount = totalReleasableAmount + releasableAmount;

            if (releasableAmount > 0)
            {
                emit TokensReleased(vestingScheduleId, releasableAmount);
            }
        }

        address beneficiary = _msgSender();
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - totalReleasableAmount;

        if (totalReleasableAmount > 0)
        {
            _token.safeTransfer(beneficiary, totalReleasableAmount);
        }
    }








    /**
     * @dev Returns the current time.
     * @return the current timestamp in seconds.
     */
    function getCurrentTime() internal view virtual returns (uint256) {
        return block.timestamp;
    }
}


