// The TokenVesting contract is a modified version of https://github.com/abdelhamidbakhta/token-vesting-contracts, which is licensed under the Apache-2.0 license.


/// @custom:security-contact dev@atem.io
contract AtemToken is ERC20, ERC20Burnable, AccessControl, ReentrancyGuard {
    struct VestingScheduleParams {
        // TGE unlocked ratio in 1/10000
        uint256 tgeRatio;
        // cliff period in seconds
        uint256 cliff;
        // start time of the vesting period
        uint256 start;
        // duration of the vesting period in seconds
        uint256 duration;
        // duration of a slice period for the vesting in seconds
        uint256 slicePeriodSeconds;
        // the user group this vesting schedule is for
        uint256 userGroupID;
        // whether or not the vesting is revocable
        bool revocable;
    }

    struct UserGroupParams {
        uint256 userGroupID;
        string name;
        uint256 maxAmount;
    }

    using ECDSA for bytes32;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant WITHDRAWER_ROLE = keccak256("WITHDRAWER_ROLE");

    uint256 public immutable chainId_;

    // Events
    event TokenClaimed(address indexed account, uint256 amount);
    event PlatformAuthorizeAccountChanged(address indexed oldAccount, address indexed newAccount);
    event MerkleRootChanged(bytes32 indexed oldRoot, bytes32 indexed newRoot);
    event UserGroupAdded(uint256 indexed userGroupID, string name, uint256 maxAmount);
    event UserGroupModified(uint256 indexed userGroupID, string old_name, uint256 old_maxAmount, string name, uint256 maxAmount);
    event VestingScheduleAdded(
        uint256 indexed vestingScheduleID,
        uint256 tgeRatio,
        uint256 cliff,
        uint256 start,
        uint256 duration,
        uint256 slicePeriodSeconds,
        uint256 indexed userGroupID,
        bool revocable
    );
    event VestingScheduleModified(
        uint256 indexed vestingScheduleID,
        uint256 old_tgeRatio,
        uint256 old_cliff,
        uint256 old_start,
        uint256 old_duration,
        uint256 old_slicePeriodSeconds,
        uint256 indexed old_userGroupID,
        bool old_revocable,
        uint256 tgeRatio,
        uint256 cliff,
        uint256 start,
        uint256 duration,
        uint256 slicePeriodSeconds,
        uint256 indexed userGroupID,
        bool revocable);

    event VestingContractChanged(address indexed oldAddress, address indexed newAddress);

    // Root of the merkle tree
    bytes32 public whitelistRoot;
    // Depth of the merkle tree
    uint256 public whitelistDepth;
    // Authentication account for the post creation
    address private _platformAuthorizeAccount;

    // Max supply of the token
    uint256 public immutable maxSupply;

    // address of the vesting contract
    address public vestingContractAddress;
    TokenVesting private _vestingContract;

    // number of claimed tokens
    mapping(address => uint256) public claimedNum;

    // vesting schedules
    VestingScheduleParams[] public vestingSchedules;

    // user groups
    UserGroupParams[] public userGroups;

    constructor(
        address initialPlatformAuthorizeAccount,
        bytes32 initialWhitelistRoot,
        uint256 initialWhitelistDepth,
        uint256 chainId,
        uint256 maxSupply_
    ) ERC20("Atem Network", "ATEM") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(WITHDRAWER_ROLE, msg.sender);
        _vestingContract = new TokenVesting(address(this));
        vestingContractAddress = address(_vestingContract);

        _platformAuthorizeAccount = initialPlatformAuthorizeAccount;
        whitelistRoot = initialWhitelistRoot;
        whitelistDepth = initialWhitelistDepth;
        chainId_ = chainId;

        maxSupply = maxSupply_;
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        require(to != address(0), "AtemToken#mint: cannot mint to the zero address");
        require(totalSupply() + amount <= maxSupply, "AtemToken#mint: exceeding max supply");
        _mint(to, amount);
    }

    function mintBatch(address[] memory to, uint256[] memory amounts) public onlyRole(MINTER_ROLE) {
        require(to.length == amounts.length, "AtemToken#mintBatch: to and amounts length mismatch");
        for (uint256 i = 0; i < to.length; ++i) {
            mint(to[i], amounts[i]);
        }
    }

    function claimWithProof(uint256 amount, uint256 max_amount, uint256 user_type, bytes32[] calldata proof)
    external nonReentrant()
    {
        address account = _msgSender();
        require(amount > 0, "AtemToken#claimWithProof: amount must be greater than 0");
        require(totalSupply() + amount <= maxSupply, "AtemToken#claimWithProof: exceeding max supply");
        require(_verify(_leaf(account, max_amount, user_type), proof), "AtemToken#claimWithProof: invalid merkle proof");
        require(amount + claimedNum[account] <= max_amount , "AtemToken#claimWithProof: exceeding your max claimable num");
        require(user_type < vestingSchedules.length, "AtemToken#claimWithProof: invalid user type");
        claimedNum[account] += amount;

        uint256 tgeAmount = 0;
        // Calculate the amount of tokens to be unlocked or vested
        if (vestingSchedules[user_type].tgeRatio > 0) {
            tgeAmount = amount * vestingSchedules[user_type].tgeRatio / 10000;
            amount = amount - tgeAmount;
        }

        if (amount > 0) {
            // Mint to the vesting manager contract
            _mint(vestingContractAddress, amount);
            // Create a vesting schedule for the user
            _vestingContract.createVestingSchedule(
                _msgSender(),
                vestingSchedules[user_type].start,
                vestingSchedules[user_type].cliff,
                vestingSchedules[user_type].duration,
                vestingSchedules[user_type].slicePeriodSeconds,
                vestingSchedules[user_type].userGroupID,
                vestingSchedules[user_type].revocable,
                amount
            );
        }


        // Mint the unlocked tokens to the user
        if (tgeAmount > 0) {
            _mint(_msgSender(), tgeAmount);
        }

        emit TokenClaimed(account, amount);
    }

    function claimWithSignature(
        uint256 amount,
        uint256 max_amount,
        uint256 user_type,
        bytes memory signature_
    ) external nonReentrant() {
        address account = _msgSender();
        require(amount > 0, "AtemToken#claimWithSignature: amount must be greater than 0");
        require(totalSupply() + amount <= maxSupply, "AtemToken#claimWithSignature: exceeding max supply");

        require(
                _verifySig(
                    keccak256(abi.encodePacked(account, max_amount, user_type, chainId_)),  // We'd better use EIP712, but we do not have time for the front- and back-end to coordinate
                    signature_,
                    _platformAuthorizeAccount),
                'AtemToken#claimWithSignature: invalid signature of platform authorizer'
            );

        require(amount + claimedNum[account] <= max_amount , "AtemToken#claimWithSignature: exceeding your max claimable num");
        require(user_type < vestingSchedules.length, "AtemToken#claimWithSignature: invalid user type");
        claimedNum[account] += amount;

        uint256 tgeAmount = 0;
        // Calculate the amount of tokens to be unlocked or vested
        if (vestingSchedules[user_type].tgeRatio > 0) {
            tgeAmount = amount * vestingSchedules[user_type].tgeRatio / 10000;
            amount = amount - tgeAmount;
        }

        if (amount > 0) {
            // Mint to the vesting manager contract
            _mint(vestingContractAddress, amount);
            // Create a vesting schedule for the user
            _vestingContract.createVestingSchedule(
                _msgSender(),
                vestingSchedules[user_type].start,
                vestingSchedules[user_type].cliff,
                vestingSchedules[user_type].duration,
                vestingSchedules[user_type].slicePeriodSeconds,
                vestingSchedules[user_type].userGroupID,
                vestingSchedules[user_type].revocable,
                amount
            );
        }


        // Mint the unlocked tokens to the user
        if (tgeAmount > 0) {
            _mint(_msgSender(), tgeAmount);
        }

        emit TokenClaimed(account, amount);
    }



    /** --------------------------------------------
     * Merkle tree helpers
     * --------------------------------------------- */

    function _leaf(address account, uint256 max_amount, uint256 user_type)
    internal pure returns (bytes32)
    {
        return keccak256(abi.encodePacked(account, max_amount, user_type));
    }

    function _verify(bytes32 leaf, bytes32[] memory proof)
    internal view returns (bool)
    {
        // We confirm that the length of proof is equal to the depth of the merkle tree
        require(proof.length == whitelistDepth, "AtemToken#_verify: invalid proof length");
        return MerkleProof.verify(proof, whitelistRoot, leaf);
    }


    /** --------------------------------------------
     * Signature verification
     * --------------------------------------------- */
    function _verifySig(bytes32 data, bytes memory signature, address account) internal pure returns (bool) 
    {
        return signatureRecover(data, signature) == account;
    }

    function signatureRecover(bytes32 data, bytes memory signature) public pure returns (address) {
        return data
            .toEthSignedMessageHash()
            .recover(signature);
    }

    /** --------------------------------------------
     * Authorization related utility functions
     * --------------------------------------------- */


    function platformAuthorizeAccount() external view returns (address)
    {
        return _platformAuthorizeAccount;
    }

    function setPlatformAuthorizeAccount(address addr) external onlyRole(MINTER_ROLE)
    {
        require(addr != address(0), "AtemToken#setPlatformAuthorizeAccount: cannot set to the zero address");
        address old = _platformAuthorizeAccount;
        _platformAuthorizeAccount = addr;
        emit PlatformAuthorizeAccountChanged(old, addr);
    }


    function setMerkleRoot(bytes32 newRoot, uint256 newDepth) onlyRole(MINTER_ROLE) external {
        bytes32 oldRoot = whitelistRoot;
        whitelistRoot = newRoot;
        whitelistDepth = newDepth;
        emit MerkleRootChanged(oldRoot, newRoot);
    }

    /** --------------------------------------------
     * Vesting schedules 
     * --------------------------------------------- */

    function getVestingScheduleNum() external view returns (uint256) {
        return vestingSchedules.length;
    }

    function addVestingSchedule(
        uint256 tgeRatio,
        uint256 cliff,
        uint256 start,
        uint256 duration,
        uint256 slicePeriodSeconds,
        uint256 userGroupID,
        bool revocable
    ) external onlyRole(MINTER_ROLE) {

        require(userGroupID < userGroups.length, "AtemToken#addVestingSchedule: invalid user group ID");
        require(slicePeriodSeconds > 0, "AtemToken#addVestingSchedule: slice period must be greater than 0");
        require(tgeRatio <= 10000, "AtemToken#addVestingSchedule: TGE ratio must be less than or equal to 10000");
        require(duration > 0, "AtemToken#addVestingSchedule: duration must be greater than 0");

        uint256 vestingScheduleNum = vestingSchedules.length;

        vestingSchedules.push(VestingScheduleParams({
            tgeRatio: tgeRatio,
            cliff: cliff,
            start: start,
            duration: duration,
            slicePeriodSeconds: slicePeriodSeconds,
            userGroupID: userGroupID,
            revocable: revocable
        }));
        emit VestingScheduleAdded(
            vestingScheduleNum,
            tgeRatio,
            cliff,
            start,
            duration,
            slicePeriodSeconds,
            userGroupID,
            revocable
        );
    }

    function modifyVestingSchedule(
        uint256 index,
        uint256 tgeRatio,
        uint256 cliff,
        uint256 start,
        uint256 duration,
        uint256 slicePeriodSeconds,
        uint256 userGroupID,
        bool revocable
    ) external onlyRole(MINTER_ROLE) {
        require(index < vestingSchedules.length, "AtemToken#modifyVestingSchedule: invalid index");

        require(userGroupID < userGroups.length, "AtemToken#modifyVestingSchedule: invalid user group ID");
        require(slicePeriodSeconds > 0, "AtemToken#modifyVestingSchedule: slice period must be greater than 0");
        require(tgeRatio <= 10000, "AtemToken#modifyVestingSchedule: TGE ratio must be less than or equal to 10000");
        require(duration > 0, "AtemToken#modifyVestingSchedule: duration must be greater than 0");

        uint256 old_tgeRatio = vestingSchedules[index].tgeRatio;
        uint256 old_cliff = vestingSchedules[index].cliff;
        uint256 old_start = vestingSchedules[index].start;
        uint256 old_duration = vestingSchedules[index].duration;
        uint256 old_slicePeriodSeconds = vestingSchedules[index].slicePeriodSeconds;
        uint256 old_userGroupID = vestingSchedules[index].userGroupID;
        bool old_revocable = vestingSchedules[index].revocable;

        vestingSchedules[index].tgeRatio = tgeRatio;
        vestingSchedules[index].cliff = cliff;
        vestingSchedules[index].start = start;
        vestingSchedules[index].duration = duration;
        vestingSchedules[index].slicePeriodSeconds = slicePeriodSeconds;
        vestingSchedules[index].userGroupID = userGroupID;
        vestingSchedules[index].revocable = revocable;

        emit VestingScheduleModified(
            index,
            old_tgeRatio,
            old_cliff,
            old_start,
            old_duration,
            old_slicePeriodSeconds,
            old_userGroupID,
            old_revocable,
            tgeRatio,
            cliff,
            start,
            duration,
            slicePeriodSeconds,
            userGroupID,
            revocable
        );
    }


    /** --------------------------------------------
     * User groups
     * --------------------------------------------- */

    function addUserGroup(
        string memory name,
        uint256 maxAmount
    ) external onlyRole(MINTER_ROLE) {
        uint256 userGroupNum = userGroups.length;
        userGroups.push(UserGroupParams({
            userGroupID: userGroupNum,
            name: name,
            maxAmount: maxAmount
        }));

        emit UserGroupAdded(userGroupNum, name, maxAmount);
    }

    function modifyUserGroup(
        uint256 index,
        string memory name,
        uint256 maxAmount
    ) external onlyRole(MINTER_ROLE) {
        require(index < userGroups.length, "AtemToken#modifyUserGroup: invalid index");

        string memory old_name = userGroups[index].name;
        uint256 old_maxAmount = userGroups[index].maxAmount;

        userGroups[index].name = name;
        userGroups[index].maxAmount = maxAmount;

        emit UserGroupModified(index, old_name, old_maxAmount, name, maxAmount);

    }

    function getUserGroupNum() external view returns (uint256) {
        return userGroups.length;
    }

    /** --------------------------------------------
     * Vesting contract helpers
     * --------------------------------------------- */

    function setVestingContract(address payable addr_) external onlyRole(MINTER_ROLE) {
        address old = vestingContractAddress;
        _vestingContract = TokenVesting(addr_);
        vestingContractAddress = addr_;
        emit VestingContractChanged(old, addr_);
    }

    function createVesting(
        address _beneficiary,
        uint256 _start,
        uint256 _cliff,
        uint256 _duration,
        uint256 _slicePeriodSeconds,
        uint256 _userGroupID,
        bool _revocable,
        uint256 _amount
    ) external onlyRole(MINTER_ROLE) {
        require(_userGroupID < userGroups.length, "AtemToken#createVesting: invalid user group ID");
        require(_slicePeriodSeconds > 0, "AtemToken#createVesting: slice period must be greater than 0");
        require(_duration > 0, "AtemToken#createVesting: duration must be greater than 0");
        require(_amount > 0, "AtemToken#createVesting: amount must be greater than 0");
        require(_beneficiary != address(0), "AtemToken#createVesting: cannot create vesting for the zero address");

        _vestingContract.createVestingSchedule(
            _beneficiary,
            _start,
            _cliff,
            _duration,
            _slicePeriodSeconds,
            _userGroupID,
            _revocable,
            _amount
        );
    }

    function revokeVesting(
        bytes32 vestingScheduleId
    ) external onlyRole(MINTER_ROLE) {
        _vestingContract.revoke(vestingScheduleId);
    }

    function withdrawTokenFromVestingContract(uint256 amount, address to) external onlyRole(WITHDRAWER_ROLE) {
        _vestingContract.withdraw(amount, to);
    }

    function withdrawToken(uint256 amount, address to) external onlyRole(WITHDRAWER_ROLE) {
        _transfer(address(this), to, amount);
    }

    function releaseVestedTokens(bytes32 vestingScheduleId, uint256 amount) external onlyRole(MINTER_ROLE) {
        _vestingContract.release(vestingScheduleId, amount);
    }

}
