// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

import "./IPBP.sol";

import "./Mintable.sol";
import "./Operatable.sol";

contract PBP is IPBP, ERC20, ERC20Burnable, Mintable, Operatable {
    uint8 constant _decimals = 9;   // same as gwei&BTC, easy to op
    uint256 constant _mintCaps = 1e9*10**_decimals; // 1B MAX-Supply

//    uint constant _HALVING_START = 45 minutes;
//    uint constant _HALVING_END = 720 minutes;
//    uint constant _HALVING_UNIT = 30 seconds;

    uint constant _HALVING_START = 90 days;
    uint constant _HALVING_END = 1440 days;
    uint constant _HALVING_UNIT = 1 days;

    uint256 _stakeStartTime;
    uint256 _stakeMinted;
    uint256 _otherMinted;

    constructor () ERC20('PlotBridge Point', 'PBP') {
        _otherMinted = 0;
    }

    function stakeMintables() public view returns (uint256) {
        return stakeRewardIn(_stakeStartTime, block.timestamp);
    }

    function stakeRewardIn(uint256 startTime, uint endTime) public view returns (uint256){
        if(_stakeStartTime==0) return 0;
        if(startTime<_stakeStartTime){
            startTime = _stakeStartTime;
        }
        if(startTime>endTime) return 0;
        uint256 tfrom = startTime - _stakeStartTime;
        uint256 tto = endTime - _stakeStartTime;
        uint256 amount = 0;
        for(uint t = _HALVING_START;t<= _HALVING_END;t*=2){
            if(tfrom<t){
                uint256 stageAmount = _mintCaps*72/1000;
                uint256 blkCaps = stageAmount/t;
                if(tto<t){
                    amount += (tto-tfrom)*blkCaps;
                    break;
                }else{
                    amount += (t-tfrom)*blkCaps;
                    tto -= t;
                }
                tfrom = 0;
            }else{
                tfrom -= t;
                tto -= t;
            }
        }
        return amount;
    }

    function mintables() public view returns (uint256) {
        uint256 slvl = _mintCaps*145/1000;
        if(_stakeStartTime>0 && block.timestamp>_stakeStartTime){
            uint mdays = (block.timestamp - _stakeStartTime) / _HALVING_UNIT + 1;
            for(uint d=90;d<=1440;d*=2){
                uint256 stageAmount = _mintCaps*99/1000;
                uint256 dailyCaps = stageAmount/d;
                if(mdays>d){
                    slvl += stageAmount;
                    mdays -= d;
                }else{
                    slvl += mdays*dailyCaps;
                    break;
                }
            }
        }
        return slvl - _otherMinted;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function totalMinted() public view returns (uint256) {
        return _stakeMinted + _otherMinted;
    }

    function setStakeStart(uint256 startTime) public onlyOperator {
        require(_stakeStartTime==0,"this can only set once");
        require(startTime>=block.timestamp,"should start in the future");
        _stakeStartTime = startTime;
    }

    function stakeStart() public view returns (uint256) {
        return _stakeStartTime;
    }

    function mint(address account, uint256 amount) public {
        if(msg.sender==minter()){
            require(amount<=mintables(), "mint caps reached");
            _otherMinted += amount;
            _mint(account, amount);
        }else if(msg.sender==operator()){
            require(amount<=stakeMintables(), "stake mint caps reached");
            _stakeMinted += amount;
            _mint(account, amount);
        }else{
            revert();
        }
    }
}
