import "@openzeppelin/contracts/access/Ownable.sol";

abstract contract Operatable is Ownable{
  address private _operator;

  event OperatorTransferred(address indexed oldOp, address indexed newOp);

  /**
   * @dev Initializes the contract setting the deployer as the initial operator.
   */
  constructor () {
    address msgSender = _msgSender();
    _operator = msgSender;
    emit OperatorTransferred(address(0), msgSender);
  }

  /**
   * @dev Returns the address of the current operator.
   */
  function operator() public view returns (address) {
    return _operator;
  }

  /**
   * @dev Throws if called by any account other than operator.
   */
  modifier onlyOperator() {
    require(_operator == _msgSender() ||(_operator == address(0) && _msgSender() == owner()), "Operatable: caller should be operator");
    _;
  }

  /**
   * @dev Throws if called by any account other than the operator/owner.
   */
  modifier onlyOperatorOrOwner() {
    require(_operator == _msgSender() || owner() == _msgSender(), "Mintable: caller should be operator or owner");
    _;
  }

  /**
   * @dev Leaves the contract without operator. It will not be possible to call
   * `onlyOperator` functions anymore. Can only be called by the current owner.
   */
  function renounceOperator() public onlyOwner {
    emit OperatorTransferred(_operator, address(0));
    _operator = address(0);
  }

  /**
   * @dev Transfers operator of the contract to a new account (`newOp`).
   * Can only be called by the current owner.
   */
  function transferOperator(address newOp) public onlyOwner {
    _transferOperator(newOp);
  }

  /**
   * @dev Transfers operator of the contract to a new account (`newOp`).
   */
  function _transferOperator(address newOp) internal {
    require(newOp != address(0), "Operator: new operator is address 0");
    emit OperatorTransferred(_operator, newOp);
    _operator = newOp;
  }
}
