// File: contracts/send.sol



pragma solidity ^0.8.0;


interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

contract H2_MultiSender is Ownable {

    function sendBNBSameValue(address payable[] memory _to, uint256 _value) public payable {
        require(_value > 0, "H2_MultiSender: _value is zero");
        require(_to.length > 0, "H2_MultiSender: _to array is empty");
        for (uint256  i = 0; i < _to.length; i++) {
            payable(_to[i]).transfer(_value);
        }
    }

    function sendBNBDifferentValue(address payable[] memory _to,  uint256 [] memory _value) public payable {
        require(_to.length > 0, "H2_MultiSender: _to array is empty");
        require(_value.length == _to.length, "H2_MultiSender: _to and _value have different length");
        for (uint256  i = 0; i < _to.length; i++) {
            payable(_to[i]).transfer(_value[i]);
        }
    }

    function sendTokenSameValue(address _tokenAddress, address [] memory _to, uint256 _value) public {
        require(_value > 0, "H2_MultiSender: _to array is empty");
        require(_to.length > 0, "H2_MultiSender: _to array is empty");
        for (uint256  i = 0; i < _to.length; i++) {
            IERC20(_tokenAddress).transferFrom(msg.sender, _to[i], _value);
        }
    }

    function sendTokenDifferentValue(address _tokenAddress, address [] memory _to, uint256 [] memory _value) public {
        require(_to.length > 0, "H2_MultiSender: _to array is empty");
        require(_value.length == _to.length, "H2_MultiSender: _to and _value have different length");
        for (uint256  i = 0; i < _to.length; i++) {
            IERC20(_tokenAddress).transferFrom(msg.sender, _to[i], _value[i]);
        }
    }

    function claimToken(address _tokenAddress, address _account) public onlyOwner {
        require(_account != address(0));
        uint256 balance  = IERC20(_tokenAddress).balanceOf(address(this));
        require(balance > 0);
        IERC20(_tokenAddress).transfer(_account, balance);
    }

    function claimBNB(address _account) public onlyOwner {
        require(_account != address(0));
        uint256 balance = address(this).balance;
        require(balance > 0);
        payable(_account).transfer(balance);
    }
}
