contract BASBetaRewards is Ownable, ReentrancyGuard {
    
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    //Bas token and NFT collection
    address basTokenAddress;
    IERC20 basToken;
    address basc;
    IERC721Metadata bascNFT;

    //Dead address
    address dead = 0x0000000000000000000000000000000000000000; 
    //Server priveleged address
    address server;

    /*
    Struct for a BAS NFT

    Owner = Wallet that deposited
    Champion = Wallet assigned to play with the champion in the beta
    OwnerRewards = Amount of pending rewards for the NFT owner
    ChampionRewards = Amount of pending rewards for the Champion
    OwnerClaimed = Amount of rewards claimed from the owner
    ChampionClaimed = Amount of rewards claimed by the champion
    */
    struct BasNFT {
        address owner;
        address champion;
        uint256 ownerRewards;
        uint256 championRewards;
        uint256 ownerClaimed;
        uint256 championClaimed;
    }

    //One to one mapping of TokenID to BasNFT struct
    mapping (uint256 => BasNFT) tokenToInfo;
    //One to many mapping of Owner wallet to deposited NFT token IDs
    mapping(address => EnumerableSet.UintSet) ownerNFTs;
    //One to one mapping of champion to NFT token ID
    mapping(address => uint256) championToTokenID;
    //Total Rewards to send out in Beta
    uint256 public totalRewards;
    //Total Rewards sent out so far in Beta
    uint256 public totalRewardsAssigned;
    //Struct to reset tokenToInfo
    BasNFT emptyStruct = BasNFT(dead,dead,0,0,0,0);
    
    constructor(address _basTokenAddress, address _basc, uint256 _totalRewards, address _server) {

        basTokenAddress = _basTokenAddress;
        basc = _basc;
        totalRewards = _totalRewards;
        server = _server;
        bascNFT = IERC721Metadata(basc);
        basToken = IERC20(basTokenAddress);
    }
    
    //Allow NFT owner to deposit an NFT for beta p2e and add in an associated champion
    function deposit(uint256 _tokenID, address _champion) external {

        //Check token is not equal to 0
        require(_tokenID != 0, "Token 0 not allowed");
        //Check token doesnt already have a struct
        require(tokenToInfo[_tokenID].owner == dead, "Token already assigned");
        //Check player hasnt been championed by someone else
        require(championToTokenID[_champion] == 0, "Champion already assigned");
        //Transfer NFT to farm
        bascNFT.transferFrom(msg.sender, address(this), _tokenID);
        //Set variables
        tokenToInfo[_tokenID] = BasNFT(msg.sender, _champion, 0, 0, 0, 0);
        championToTokenID[_champion] = _tokenID;
        EnumerableSet.add(ownerNFTs[msg.sender], _tokenID);
        
    }

    //Allow NFT owner to withdraw and NFT from the farm, sending out all pending rewards and deleting the struct data
    function withdrawChampion(uint256 _tokenID, bool withdrawTokens) external {

        //Check NFT belongs to msg sender
        require(msg.sender == tokenToInfo[_tokenID].owner, "Not owner");
        //Transfer tokens
        if (tokenToInfo[_tokenID].ownerRewards>0 && withdrawTokens==true) {
            basToken.safeTransfer(tokenToInfo[_tokenID].owner, tokenToInfo[_tokenID].ownerRewards);
        }
        if (tokenToInfo[_tokenID].championRewards>0 && withdrawTokens==true) {
            basToken.safeTransfer( tokenToInfo[_tokenID].champion, tokenToInfo[_tokenID].championRewards);
        }
        
        //Transfer NFT
        bascNFT.transferFrom(address(this), msg.sender, _tokenID);
        //Reset internal variables
        championToTokenID[tokenToInfo[_tokenID].champion]=0;
        tokenToInfo[_tokenID] = emptyStruct;
        EnumerableSet.remove(ownerNFTs[msg.sender], _tokenID);

    }

    //Allow champion to claim his rewards
    function claimChampionRewards() external {

        //Initiate token number
        uint256 token = championToTokenID[msg.sender];
        //Check Champion has NFT backed and rewards pending
        require(token!=0, "Not Champion");
        require(tokenToInfo[token].champion == msg.sender, "Not Champion");
        require(tokenToInfo[token].championRewards > 0, "No Rewards");
        //Update Claimed Champion rewards 
        tokenToInfo[token].championClaimed = tokenToInfo[token].championClaimed.add(tokenToInfo[token].championRewards);
        //Transfer tokens to Champion
        basToken.safeTransfer(tokenToInfo[token].champion, tokenToInfo[token].championRewards);
        //Set Champion pending rewards to 0
        tokenToInfo[token].championRewards = 0;

    }

    //Allow NFT owner to claim his rewards
    function claimOwnerRewards(uint256 _tokenID) external {

        //Check conditions
        require(tokenToInfo[_tokenID].owner == msg.sender, "Not owner");
        require(EnumerableSet.contains(ownerNFTs[msg.sender], _tokenID) == true, "Not owner");
        require(tokenToInfo[_tokenID].ownerRewards > 0, "No Rewards");
        //Update Claimed Owner rewards 
        tokenToInfo[_tokenID].ownerClaimed = tokenToInfo[_tokenID].ownerClaimed.add(tokenToInfo[_tokenID].ownerRewards);
        //Transfer tokens to Owner
        basToken.safeTransfer(tokenToInfo[_tokenID].owner, tokenToInfo[_tokenID].ownerRewards);
        //Set Owner pending rewards to 0
        tokenToInfo[_tokenID].ownerRewards = 0;

    }

    function championRemoveOwner() external {

        //Initiate token number
        uint256 token = championToTokenID[msg.sender];
        //Check Champion has NFT backed and rewards pending
        require(token!=0, "Not Champion");
        require(tokenToInfo[token].champion == msg.sender, "Not Champion");
        //Transfer tokens to Champion
        if(tokenToInfo[token].championRewards>0) {
            basToken.safeTransfer(tokenToInfo[token].champion, tokenToInfo[token].championRewards);
        }
        //Reset variables
        tokenToInfo[token].champion = dead;
        tokenToInfo[token].championRewards = 0;
        tokenToInfo[token].championClaimed = 0;
        championToTokenID[msg.sender] = 0;

    }

    function ownerChangeChampion(uint256 _tokenID, address _champion) external {

        //Check conditions
        require(tokenToInfo[_tokenID].owner == msg.sender, "Not owner");
        require(EnumerableSet.contains(ownerNFTs[msg.sender], _tokenID) == true, "Not owner");
        require(championToTokenID[_champion] == 0, "Champion taken");
        //Transfer tokens to current Champion
        if(tokenToInfo[_tokenID].championRewards>0) {
            basToken.safeTransfer(tokenToInfo[_tokenID].champion, tokenToInfo[_tokenID].championRewards);
        }
        //Set variables
        championToTokenID[tokenToInfo[_tokenID].champion] = 0;
        championToTokenID[_champion] = _tokenID;
        tokenToInfo[_tokenID].champion = _champion;
        tokenToInfo[_tokenID].championClaimed = 0;
        tokenToInfo[_tokenID].championRewards = 0;

    }

    //VIEW FUNCTIONS

    function viewNFTsHeld(address _owner) external view returns(uint256[] memory) {

        uint256[] memory arr = new uint256[](EnumerableSet.length(ownerNFTs[_owner]));
        for (uint256 i=0; i<EnumerableSet.length(ownerNFTs[_owner]); i++ ) {
            arr[i]=(EnumerableSet.at(ownerNFTs[_owner], i));
        }
        return arr;
    }

    function viewChampionInfo(address _champion) external view returns(uint256, string memory, uint256, uint256, address) {
        
        uint256 token = championToTokenID[_champion];
        if (token == 0) {
            return (0, "", 0, 0, dead);
        } else {
            return (token, bascNFT.tokenURI(token), tokenToInfo[token].championRewards, tokenToInfo[token].championClaimed, tokenToInfo[token].owner);
        }
    }

    function viewNFTinfo(uint256 _tokenID) external view returns(address, address, uint256, uint256, uint256, uint256, string memory) {

        if (tokenToInfo[_tokenID].owner == dead) {
            return(dead, dead, 0, 0, 0, 0, "");
        } else {
            return(tokenToInfo[_tokenID].owner, tokenToInfo[_tokenID].champion, tokenToInfo[_tokenID].ownerRewards, tokenToInfo[_tokenID].championRewards, tokenToInfo[_tokenID].ownerClaimed, tokenToInfo[_tokenID].championClaimed, bascNFT.tokenURI(_tokenID));
        }
    }

    function viewRewardsInfo() external view returns(uint256, uint256) {
        return(totalRewards, totalRewardsAssigned);
    }

    //SERVER SIDE FUNCTIONS

    function distributeRewards(address[] memory champions, uint256[] memory rewards) external {

        require(msg.sender == server, "NOT SERVER");
        require(champions.length ==rewards.length, "MISMATCH");

        uint256 token;
        
        for (uint256 i=0; i<champions.length; i++) {

            token = championToTokenID[champions[i]];

            if (token != 0 && (totalRewardsAssigned.add((rewards[i].mul(2))) <= totalRewards)) {

                //Update rewards
                tokenToInfo[token].ownerRewards = tokenToInfo[token].ownerRewards.add(rewards[i]);
                tokenToInfo[token].championRewards = tokenToInfo[token].championRewards.add(rewards[i]);
                
                totalRewardsAssigned = totalRewardsAssigned.add((rewards[i].mul(2)));
                
            }
        }
    }

    function checkDeposit(address _champion) external view returns(string memory) {
        
        if(championToTokenID[_champion] != 0) {
           return bascNFT.tokenURI(championToTokenID[_champion]); 
        } else {
            return "";
        }
    }

    function emergencyWithdrawTokens() external onlyOwner {

        basToken.safeTransfer(msg.sender, basToken.balanceOf(address(this)));
    }

    function changeServer(address _newServer) external onlyOwner {
        server = _newServer;
    }
    
}
