            "Caller is not the manager"
        );
        _;
    }

    modifier ownerOrManager() {
        bool isOwner = hasRole(OWNER_ROLE, _msgSender());
        bool isManager = hasRole(MANAGER_ROLE, _msgSender());
        require(isOwner || isManager, "Caller is not the manager nor owner");
        _;
    }

    //@brief 构造函数
    //@param _managerAddress  管理员账户地址
    constructor(
        address _managerAddress
    ) internal {
        //添加用户的默认角色
        _setupRole(MANAGER_ROLE, _managerAddress);
        //设置各角色的管理员
        _initRoleAdmin();
        //给owner添加相关角色
        _initOwnerRoles(_msgSender());
    }

    //@brief 批量添加免交易费的用户
    //@param _users 新增的用户列表
    function addFeeFreeUserBatch(address[] memory _users) public onlyManager {
        require(_users.length > 0, "_users can not be empty");
        for (uint256 i = 0; i < _users.length; i++) {
            address newUser = _users[i];
            _checkNewAddress(newUser);
            if (!hasRole(FEE_FREE_ROLE, newUser))
                grantRole(FEE_FREE_ROLE, newUser);
        }
    }

    //@brief 将某个用户移出免交易费用户列表
    //@param _user 被移除的用户
    function removeFeeFreeUser(address _user) public onlyManager {
        if (!hasRole(FEE_FREE_ROLE, _user)) return;
        revokeRole(FEE_FREE_ROLE, _user);
    }

    //@brief 添加管理员
    //@param _users 新增用户
    function addManager(address _users) public onlyOwner {
        require(
            !hasRole(MANAGER_ROLE, _users),
            "The same address cannot be added"
        );
        _checkNewAddress(_users);

        grantRole(MANAGER_ROLE, _users);
    }

    //@brief 将某个用户移出管理员列表
    //@param _user 被移除的用户
    function removeManager(address _user) public onlyOwner {
        require(hasRole(MANAGER_ROLE, _user), "The address was not found");
        // require(
        //     getRoleMemberCount(MANAGER_ROLE) > 1,
        //     "cannot delete the last administrator"
        // );
        revokeRole(MANAGER_ROLE, _user);
    }

    //@brief 更改合约拥有者
    //@param _newOwner 新的合约拥有者
    function changeOwner(address _newOwner) public onlyOwner {
        address oldOwner = _msgSender();
        require(_newOwner != oldOwner, "This is an owner");
        //检查新地址
        _checkNewAddress(_newOwner);
        //给新owner添加相关角色
        _initOwnerRoles(_newOwner);
        //移除旧管理员的角色
        _removeAllRolesOfOldOwner(oldOwner);
    }

    //@brief
    //@param _newOwner 新的合约拥有者
    function _checkNewAddress(address addr) internal pure {
        require(address(0) != addr, "Zero address is not supported");
    }

    //@brief 初始化各个角色的管理员
    function _initRoleAdmin() private {
        //设置"管理员角色"的管理员
        _setRoleAdmin(MANAGER_ROLE, OWNER_ROLE);
        //设置"免手续费用角色"的的管理员
        _setRoleAdmin(FEE_FREE_ROLE, MANAGER_ROLE);
    }

    //@brief 给owner添加相关角色
    function _initOwnerRoles(address _owner) private {
        for (uint256 i = 0; i < rolesForOwner.length; i++)
            _setupRole(rolesForOwner[i], _owner);
    }

    //@brief 移除旧owner用户的所有角色(只能移除调用者自己的角色)
    function _removeAllRolesOfOldOwner(address _owner) private {
        for (uint256 i = 0; i < rolesForOwner.length; i++)
            renounceRole(rolesForOwner[i], _owner);
    }
}

// File: contracts/FtnToken/FtnToken.sol



pragma solidity ^0.6.12;





contract FtnToken is IERC20, AccessControlAble {
    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _balances; //账户余额
    mapping(address => mapping(address => uint256)) private _allowances; //授权额度

    //基本信息
    string private _name = "Fraternity Coin"; //名称
    string private _symbol = "FTN"; //标志符
    uint8 private _decimals = 8; //小数点位数

    uint256 private _totalSupply; //已发行总额

    //手续费相关: 每笔转账总手续费=慈善基金扣额+分红扣额（用于给不同等级的账户分红）
    uint256 private bonusFeePercentage = 3; //转账手续费百分比（用于分红）。例如：2--代表2%  50--代表50%
    uint256 private benevolentFundFeePercentage = 2; //转账手续费百分比（转到慈善基金账户）。例如：2--代表2%  50--代表50%
    address private benevolentFundAddress; //慈善基金账户
    address private bonusFeePoolAddress; //分红池账户（该账户存储待分红的手续费，日结时再分发到其它地址）
    uint256 private bonusFeeCumulate; //转账分红总累加值

    //event
    //变更慈善基金账户地址
    event OnBenevolentFundAddressChange(
        address indexed benevolentFundAddress,
        address indexed sender
    );
    //变更手慈善基金费率
    event OnBenevolentFundFeePercentageChange(
        uint256 indexed benevolentFundFeePercentage,
        address indexed sender
    );
    //变更分红池账户地址
    event OnBonusFeePoolAddressChange(
        address indexed bonusFeePoolAddress,
        address indexed sender
    );
    //变更分红池费率
    event OnBonusFeePercentageChange(
        uint256 indexed bonusFeePercentage,
        address indexed sender
    );
    //交易成功
    event OnTransfer(
        address indexed from,
        address indexed to,
        uint256 value,
        uint256 benevolentFundFee,
        uint256 bonusFee
    );

    //@brief 构造函数
    //@param _benevolentFundAddress 慈善基金账户地址
    //@param _benevolentFundFeePercentage  每笔转账，慈善扣除费用占交易额的百分比
    //@param _bonusFeePoolAddress  分红池账户地址
    //@param _bonusFeePercentage  每笔转账，分红池扣除费用占交易额的百分比
    //@param _managerAddress  管理员账户地址
    constructor(
        address _benevolentFundAddress,
        uint256 _benevolentFundFeePercentage,
        address _bonusFeePoolAddress,
        uint256 _bonusFeePercentage,
        address _managerAddress
    ) public AccessControlAble(_managerAddress) {
        benevolentFundAddress = _benevolentFundAddress;
        benevolentFundFeePercentage = _benevolentFundFeePercentage;
        bonusFeePoolAddress = _bonusFeePoolAddress;
        bonusFeePercentage = _bonusFeePercentage;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    //@brief 查询转账手续费百分比（用于分红）
    function getBonusFeePercentage() public view returns (uint256) {
        return bonusFeePercentage;
    }

    //@brief 查询转账手续费百分比（转到慈善基金账户）
    function getBenevolentFundFeePercentage() public view returns (uint256) {
        return benevolentFundFeePercentage;
    }

    //@brief 查询慈善基金账户地址
    function getBenevolentFundAddress() public view returns (address) {
        return benevolentFundAddress;
    }

    //@brief 查询分红池账户地址
    function getBonusFeePoolAddress() public view returns (address) {
        return bonusFeePoolAddress;
    }

    //@brief 更改慈善基金账户地址
    //@param _benevolentFundAddress 新的慈善基金账户地址
    function changeBenevolentFundAddress(address _benevolentFundAddress)
        public
        onlyManager
    {
        require(
            benevolentFundAddress != _benevolentFundAddress,
            "Cannot be the same as the old value"
        );
        removeFeeFreeUser(benevolentFundAddress);
        benevolentFundAddress = _benevolentFundAddress;
        grantRole(FEE_FREE_ROLE, benevolentFundAddress);
        emit OnBenevolentFundAddressChange(
            _benevolentFundAddress,
            _msgSender()
        );
    }

    //@brief 更改慈善基金费率
    //@param _benevolentFundFeePercentage 慈善扣除费用占交易额的百分比
    function changeBenevolentFundFeePercentage(
        uint256 _benevolentFundFeePercentage
    ) public onlyManager {
        benevolentFundFeePercentage = _benevolentFundFeePercentage;
        emit OnBenevolentFundFeePercentageChange(
            _benevolentFundFeePercentage,
            _msgSender()
        );
    }

    //@brief 更改分红池地址
    //@param _bonusFeePoolAddress 新的分红池地址
    function changeBonusFeePoolAddress(address _bonusFeePoolAddress)
        public
        onlyManager
    {
        require(
            bonusFeePoolAddress != _bonusFeePoolAddress,
            "Cannot be the same as the old value"
        );
        removeFeeFreeUser(benevolentFundAddress);
        bonusFeePoolAddress = _bonusFeePoolAddress;
        grantRole(FEE_FREE_ROLE, bonusFeePoolAddress);
        emit OnBonusFeePoolAddressChange(bonusFeePoolAddress, _msgSender());
    }

    //@brief 更改分红池扣除费用占交易额的百分比
    //@param _bonusFeePercentage 慈善扣除费用占交易额的百分比
    function changeBonusFeePercentage(uint256 _bonusFeePercentage)
        public
        onlyManager
    {
        bonusFeePercentage = _bonusFeePercentage;
        emit OnBonusFeePercentageChange(_bonusFeePercentage, _msgSender());
    }

    //@brief 获取转账分红手续费历史累加总值
    function getBonusFeeCumulate() public view returns (uint256) {
        return bonusFeeCumulate;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        returns (bool)
    {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        _approve(_msgSender(), spender, currentAllowance.add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        returns (bool)
    {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        _approve(_msgSender(), spender, currentAllowance.sub(subtractedValue));

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];

        //更新转出方余额
        _balances[sender] = senderBalance.sub(amount);

        //如果不在免手续费列表，则需要扣除手续费
        uint256 recipientAmount = amount;
        uint256 bonusFee;
        uint256 benevolentFundFee;
        if (!hasRole(FEE_FREE_ROLE, _msgSender())) {
            //转账分红手续费
            bonusFee = amount.mul(bonusFeePercentage).div(100);
            uint256 bonusFeePoolBalance = _balances[bonusFeePoolAddress];
            _balances[bonusFeePoolAddress] = bonusFeePoolBalance.add(bonusFee);
            bonusFeeCumulate = bonusFeeCumulate.add(bonusFee);

            //慈善费
            benevolentFundFee = amount.mul(benevolentFundFeePercentage).div(
                100
            );
            uint256 benevolentFundBalance = _balances[benevolentFundAddress];
            _balances[benevolentFundAddress] = benevolentFundBalance.add(
                benevolentFundFee
            );

            //实际接收到的额度=交易总额-(分红费+慈善费)
            recipientAmount = amount.sub(bonusFee.add(benevolentFundFee));

            emit Transfer(sender, bonusFeePoolAddress, bonusFee);
            emit Transfer(sender, benevolentFundAddress, benevolentFundFee);
        }

        //更新接收方余额
        uint256 recipientBalance = _balances[recipient];
        _balances[recipient] = recipientBalance.add(recipientAmount);

        emit Transfer(sender, recipient, amount);
        emit OnTransfer(sender, recipient, amount, benevolentFundFee, bonusFee);
    }

    function mint(address account, uint256 amount) public onlyManager {
        _mint(account, amount);
    }

    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");
        // require(amount + _minted <= _totalSupply, "total supply overflow");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply.add(amount);

        uint256 accountBalance = _balances[account];
        _balances[account] = accountBalance.add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        _balances[account] = accountBalance.sub(amount);
        _totalSupply = _totalSupply.sub(amount);

        emit Transfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {}
}
