// File contracts/DBOEPrivateTokenSale.sol


pragma solidity >= 0.8.11;






contract DBOEPrivateTokenSale is Ownable, ReentrancyGuard {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    
    struct VestingSchedule {
        address investor;        
        uint256 totalAmount;

        uint256 startTimeUtc;                
        uint256 releasePeriodicalAmount;        
        bool revoked;          
    }
    mapping(address => bool) investors;
    mapping(address => VestingSchedule) private vestingSchedulePerInvestor;

    address immutable private _treasury;

    IERC20 immutable private _token;

    IERC20 immutable private _acceptedStableCoin;
        
    uint256 private noOfInvestors;
    uint256 private soldAmount;

    uint256 private cliffSecs;
    uint256 private releaseIntervalSecs;
    uint256 private constant numOfClaims = 12;

    uint256 private constant maxNoOfInvestors = 40;    
    uint256 private constant fixedRate = 6;
    uint256 private constant rateScale = 10**2;    
    uint256 immutable private totalPool;
    uint256 private constant minOrder = 50 * (10**3) * (10**18);        

    // investor -> claimScheduleId -> yes/no
    mapping(address => mapping(uint256 => bool)) claimedByIdx;

    event Released(address investor, uint256 amount);

    constructor(uint256 totalPool_, address treasury_, address token_, address acceptedStableCoin_, uint256 cliffSecs_, uint256 releaseIntervalSecs_) {
        require(treasury_ != address(0) && token_ != address(0) && acceptedStableCoin_ != address(0));
        totalPool = totalPool_;
        _token = IERC20(token_);
        _acceptedStableCoin = IERC20(acceptedStableCoin_);
        _treasury = treasury_;

        cliffSecs = cliffSecs_;
        releaseIntervalSecs = releaseIntervalSecs_;
    }
    
    function getTokenAddress() external view returns(address) {
        return address(_token);
    }
    
    function getStableCoinAddress() external view returns(address) {
        return address(_acceptedStableCoin);
    }

    // total token, token left, max slot, slot left, min order size, price per token, price scale
    function getProgress() external view returns (uint256, uint256, uint256, uint256,  uint256, uint256, uint256) {
        return (totalPool, totalPool - soldAmount, maxNoOfInvestors, maxNoOfInvestors - noOfInvestors, minOrder, fixedRate, rateScale);
    }

    //cliff secs, release interval in secs, num of claims
    function getTerms() external view returns (uint256, uint256, uint256) {
        return (cliffSecs, releaseIntervalSecs, numOfClaims);
    }

    function getVestingSchedule(address _investor) external view returns (VestingSchedule memory) {
        return vestingSchedulePerInvestor[_investor];
    }

    function bidding(uint256 noOfToken) public {
        require(noOfToken >= minOrder, "Too small");        
        require(noOfInvestors < maxNoOfInvestors, "No slot left");
        require(noOfToken <= totalPool - soldAmount, "Not enough token left");
        uint256 amount = noOfToken * fixedRate/rateScale;        
        _acceptedStableCoin.safeTransferFrom(msg.sender, _treasury, amount);
        vestingSchedulePerInvestor[msg.sender] = VestingSchedule(msg.sender, noOfToken, block.timestamp, noOfToken/numOfClaims, false);
        
        noOfInvestors += 1;
        soldAmount += noOfToken;
        investors[msg.sender] = true; 
    }
     
    function revokeInvestor(address _investor) public onlyOwner {       
        require(investors[_investor], "Nonexisted"); 
        vestingSchedulePerInvestor[_investor].revoked = true;        
    }

    function withdraw(uint256 amount) public nonReentrant onlyOwner {        
        _token.safeTransfer(owner(), amount);
    }

    function claim(uint256 scheduleId) public nonReentrant {
        require(scheduleId < 12, "Invalid schedule id");
        require(investors[msg.sender], "Nonexisted");

        VestingSchedule storage vestingSchedule = vestingSchedulePerInvestor[msg.sender];        
        require(msg.sender == vestingSchedule.investor, "Not correct beneficiary");        
        require(!claimedByIdx[msg.sender][scheduleId], "Already claimed");        
        require(!vestingSchedule.revoked, "Vesting Schedule revoked");

        require(block.timestamp >= vestingSchedule.startTimeUtc + cliffSecs + releaseIntervalSecs*scheduleId, "Premature claim");
        
        _token.safeTransfer(msg.sender, vestingSchedule.releasePeriodicalAmount);
        claimedByIdx[msg.sender][scheduleId] = true;
        emit Released(msg.sender, vestingSchedule.releasePeriodicalAmount);                        
    }       
}
