//  / ____/ /  / /_/ / /_/ /_/ / /__/ /_/ / /
// /_/   /_/   \____/\__/\____/\___/\____/_/

/**
 * @title Swap oracle.
 * @notice Allows to get token swap information
 * @author Leech Protocol (https://app.leechprotocol.com/).
 */
contract SwapOracleBsc is Ownable {
    IUniswapV2Router02 public dexRouter;

    /// @notice Paths for the token swaps.
    /// @dev tokenFrom => tokenTo => Array with routes
    mapping(IERC20 => mapping(IERC20 => address[])) public routes;

    /**
     * @notice Contract deployment params
     * @param _dexRouter Swap router.
     */
    constructor(IUniswapV2Router02 _dexRouter) {
        dexRouter = _dexRouter;
        // USDT > USDC
        routes[IERC20(0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d)][
            IERC20(0x55d398326f99059fF775485246999027B3197955)
        ] = [
            0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d,
            0x55d398326f99059fF775485246999027B3197955
        ];
    }

    /**
     * @dev
     * @param amount Amount of the deposit token
     * @param depositToken Deposit token.
     */
    function getAmountOut(
        uint256 amount,
        IERC20 depositToken,
        IERC20 tokenOut
    ) external view returns (uint256 baseAmount) {
        baseAmount = dexRouter.getAmountsOut(
            amount,
            routes[depositToken][tokenOut]
        )[routes[depositToken][tokenOut].length - 1];
    }

    /**
     * @notice Sets pathes for tokens swap.
     * @dev Only owner can set a pathes.
     * @param tokenIn From token.
     * @param tokenOut To token.
     * @param path BSW routes array.
     */
    function setRoutes(
        IERC20 tokenIn,
        IERC20 tokenOut,
        address[] calldata path
    ) external onlyOwner {
        routes[tokenIn][tokenOut] = path;
    }
}
